INSERT INTO `equipment_data` VALUES (/*equipment_id*/101011, /*equipment_name*/"鐵劍", /*description*/"四處都可買到，價格低廉的劍。\n對大多數習劍之人來說，這是他們\n第一次獲得的，很重要的一把劍。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/15.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101071, /*equipment_name*/"殺手筆劍", /*description*/"外觀宛如一隻筆的短劍。\n攻擊力雖低，\n但十分方便攜帶。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101101, /*equipment_name*/"木製拳套", /*description*/"能夠強化拳頭威力的木製武器。\n雖然做工簡樸，但比起徒手攻擊，\n可減輕對拳頭帶來的負擔", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101131, /*equipment_name*/"獵人弓", /*description*/"標準的木製弓。\n主要受到獵人們的喜愛，\n無論是射程或威力，\n都十分適合用來狩獵動物。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101161, /*equipment_name*/"鋼之戟", /*description*/"士兵經常使用的戟。\n攻擊距離較長，且易於使用，\n所以經常被使用於戰鬥之中。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101191, /*equipment_name*/"單手斧", /*description*/"小型單手斧。\n本來是要做為擲斧用的，\n但由於使用上非常方便，\n因此也被拿來當成近戰攻擊使用。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/8.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101251, /*equipment_name*/"檀香之杖", /*description*/"以生長在神秘森林裡，\n名叫阿斯特賴亞檜木的檀木\n所削製而成的法杖。\n其散發的香氣能緩和傷者的心情。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/1, /*sale_price*/60, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/8.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101281, /*equipment_name*/"皮革工作服", /*description*/"原本是工匠在穿的圍裙。\n上面的大口袋保留了原本的特徵。\n新人冒險者身上得攜帶的物品較多，\n所以這些口袋對他們來說非常重要。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/50.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101371, /*equipment_name*/"旅行長袍", /*description*/"冒險者在最初時選擇的一套裝備。\n道具名稱蘊含了祝福冒險者\n踏出第一步的意思，\n因此名稱也成為賣點之一。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/55.0, /*atk*/0.0, /*magic_str*/1.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101401, /*equipment_name*/"木盾", /*description*/"以名為阿斯特賴亞雪松的針葉樹，\n所製作而成的盾。\n因為方便加工而且便宜，\n所以經常用在學生的模擬戰。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/15.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101431, /*equipment_name*/"皮靴", /*description*/"即使不是冒險者也很喜歡的基本款。\n很輕，也很適合走路，\n穿得越久就會越合腳。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101521, /*equipment_name*/"時尚貝雷帽", /*description*/"原本是畫家愛用的帽子，\n但因為很適合用來遮陽跟擋風，\n所以很受新手冒險者的歡迎。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/2, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101551, /*equipment_name*/"幹勁手鍊", /*description*/"只要裝備之後，就會湧出幹勁的\n不可思議的手鍊。\n具有提升攻擊力的效果。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101581, /*equipment_name*/"守護墜飾", /*description*/"有著四葉幸運草圖案的\n護身符。\n據說能夠帶來幸運，\n能保護持有者的安全。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/50.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/101611, /*equipment_name*/"氣魄手環", /*description*/"帶有魔力的手鍊。\n戴上之後氣魄就會高漲，\n有能使裝備者的魔法攻擊力\n提升的效果。", /*promotion_level*/1, /*craft_flg*/0, /*equipment_enhance_point*/2, /*sale_price*/60, /*require_level*/3, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102011, /*equipment_name*/"齒輪劍", /*description*/"刀刃與刀柄是用齒輪接合的一把很稀有的劍。\n這是由鐘錶商人與鐵匠所合作\n開發出來的武器。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/30.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102013, /*equipment_name*/"湛藍之血", /*description*/"模仿某把獻給貴族的名劍\n所製造出來的一把劍。\n不僅兼具了外表的美觀，\n銳利度也很不錯，實用性相當高。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/60.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102014, /*equipment_name*/"極光之劍", /*description*/"會發出七色光芒的美麗之劍。\n只要揮動刀身就會閃耀光芒，\n劃過的軌跡宛如光於空中飛舞一般。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102041, /*equipment_name*/"彎月刀", /*description*/"有著美麗曲線的一把刀。\n過去曾有位以勇猛著稱的將軍，\n說這把刀沾上血液實屬可惜，\n將之視為美術品珍藏的頂級武器。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/35.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102071, /*equipment_name*/"野戰小刀", /*description*/"比起戰場，在森林或山上遇難時，\n更能發揮它真正的價值。\n為了幫助遇難者能生存下去，\n藏有許多機關。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102101, /*equipment_name*/"皮製指虎", /*description*/"在皮革裡嵌入鐵塊，\n強化拳頭威力的武器。\n是為了希望能將武器融入自己肉體\n的武道家所設計的裝備。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102131, /*equipment_name*/"水晶之弓", /*description*/"使用來自冰晶山脈的素材，\n所製作而成的一把弓。\n由於水晶可以當作魔法的觸媒，\n因此可以射出夾帶魔力的箭。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/27.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102162, /*equipment_name*/"三叉戟", /*description*/"擁有相當於能讓整座大陸沉沒於\n海裡的力量，是力量與海神\n波賽頓三叉戟相近的一把長戟。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/7, /*hp*/0.0, /*atk*/12.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102163, /*equipment_name*/"火焰之矛", /*description*/"灌注了火精靈之力的長戟。\n宛如搖曳火焰一般的矛頭，\n打倒越多魔物\n就會燃燒得越旺盛。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102191, /*equipment_name*/"金屬戰斧", /*description*/"鋼製的戰斧。\n與伐木用的斧頭不同，\n以作為武器為目的而被鍛造出來，\n因此具有明確的殺傷力。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/16.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102192, /*equipment_name*/"精鋼斧", /*description*/"使用先進的鑄造技術，\n所製作而成的戰斧。\n善用它的重量來進行攻擊，\n可殲滅敵人。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/34.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102221, /*equipment_name*/"綠意之杖", /*description*/"與自然有著緊密連結的精靈，\n所製作的法杖。\n除了將其當作武器使用以外，\n也會用來祈禱豐收。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/7, /*hp*/0.0, /*atk*/0.0, /*magic_str*/16.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102222, /*equipment_name*/"雛龍之杖", /*description*/"看似抱著寶玉的\n龍形法杖。\n雖然稀微，\n但似乎蘊藏著龍之力。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/34.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102251, /*equipment_name*/"醫生掰掰法杖", /*description*/"與恢復魔法配合度很高的法杖。\n是恢復魔法師的\n愛用道具。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/6, /*hp*/0.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102252, /*equipment_name*/"獨角獸的慈愛", /*description*/"使用貴重的獨角獸之角製成的法杖。\n獨角獸之角有著治癒的力量，\n能夠提升恢復魔法的效果", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/14, /*sale_price*/320, /*require_level*/9, /*hp*/0.0, /*atk*/0.0, /*magic_str*/27.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102281, /*equipment_name*/"智慧之服", /*description*/"過去僧侶為傳教，\n而到各地旅行時所穿著的服裝。\n由於很方便行動又堅固耐用，\n所以很多魔法師也很愛穿。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/8, /*sale_price*/160, /*require_level*/8, /*hp*/90.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102282, /*equipment_name*/"輕板甲", /*description*/"用薄板所製成的鎧甲。\n擁有穩固的防禦力，以及因重量較輕而方便行動是它的特徵。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/320, /*require_level*/14, /*hp*/150.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102311, /*equipment_name*/"鱗甲", /*description*/"將金屬或皮革以鱗片的方式貼合而成的鎧甲。\n比一般的鎧甲還易於活動，\n對斬擊與突刺的抵抗力很強，\n相反地，較難抵禦鈍器的打擊。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/9, /*hp*/95.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102312, /*equipment_name*/"精鋼鎧甲", /*description*/"使用先進的鑄造技術，\n所製作而成的堅固鎧甲。\n一般的攻擊完全無法傷害到\n穿著這套鎧甲的人。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/180.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102341, /*equipment_name*/"民俗服飾", /*description*/"融入了阿斯特賴亞傳統設計的衣服。\n購買時需要有相符的身份與家世。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/9, /*hp*/90.0, /*atk*/6.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102342, /*equipment_name*/"忍者裝扮", /*description*/"想要在暗地裡行動，\n沒有比這套更加適合的服裝了。\n據說在某個國家，職業級間諜\n都很愛穿這套衣服。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/120.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/110.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102371, /*equipment_name*/"春日長袍", /*description*/"讓人聯想到春天的可愛長袍。\n作工部份相當堅實，很適合在旅行時使用，\n從外觀設計到\n實用性都受到不少人的喜愛。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/65.0, /*atk*/0.0, /*magic_str*/4.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102372, /*equipment_name*/"北國長袍", /*description*/"為寒冷的北方國家所喜愛。\n羊毛製成的線十分溫暖，\n因此用這些線所織成的斗篷能夠好好地包住肩膀與手臂，\n不受到寒風的侵擾。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/14, /*hp*/120.0, /*atk*/0.0, /*magic_str*/7.0, /*def*/1.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102401, /*equipment_name*/"塔盾", /*description*/"相當於與人等高的金屬大盾。\n雖然有著壓倒性的防禦力，\n但另一方面，因為重量的關係而難以移動。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/15, /*hp*/135.0, /*atk*/7.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102431, /*equipment_name*/"小丑靴", /*description*/"以小丑的形象製作而成的\n時尚靴子。\n穿上之後，心情就會變得開朗。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/30.0, /*atk*/2.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102432, /*equipment_name*/"狩獵靴", /*description*/"使用棲息於高山之魔物的骨頭\n所製作而成的靴子。\n那野性奔放的設計，可以喚醒野性！", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/15, /*hp*/60.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102461, /*equipment_name*/"女巫靴", /*description*/"為了採集製作魔法藥的素材，\n而旅行各地的魔法師所穿著的鞋子。\n為了能承受各種嚴峻的環境，\n所以作工非常堅固。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/8, /*sale_price*/160, /*require_level*/9, /*hp*/30.0, /*atk*/0.0, /*magic_str*/8.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102491, /*equipment_name*/"可愛報童帽", /*description*/"附有防風鏡的報童帽。\n看起來像氣球的剪影一樣非常可愛，\n是受到蘭德索爾少男少女們\n矚目的一項道具。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/160, /*require_level*/8, /*hp*/90.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102493, /*equipment_name*/"羅馬軍團盔", /*description*/"古代戰士們所使用的頭盔。\n作工非常堅固，\n即使在現代也十分合用。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/160, /*require_level*/9, /*hp*/90.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102521, /*equipment_name*/"修女兜帽", /*description*/"修女所使用的頭巾。\n她們能給予受了傷的人們，\n身與心之上的療癒。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/10, /*sale_price*/320, /*require_level*/16, /*hp*/0.0, /*atk*/0.0, /*magic_str*/7.0, /*def*/1.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102551, /*equipment_name*/"勇氣寶珠", /*description*/"能夠賦予戰鬥所需之『仁．智．勇』\n的『勇』的寶珠。\n能夠毫不畏懼地面對各種敵人，\n勇氣的護身符。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/9, /*hp*/0.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/30.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102552, /*equipment_name*/"尖刺手環", /*description*/"帶有魔物身上刺狀鱗片的手鐲。\n有提升裝備者力量的效果。\n由於是用兇暴的魔物身上的鱗片\n製作而成，因此是件在市面上\n相當少見的裝備。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/20, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102581, /*equipment_name*/"仁愛寶珠", /*description*/"能夠提供戰鬥所需之『仁．智．勇』\n的『仁』之寶珠。\n祈求能夠保護自己跟夥伴，\n是充滿愛的心之護身符。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/55.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102582, /*equipment_name*/"勝利的編織手環", /*description*/"祈禱騎士能夠平安返回，\n蘊含如此心意所製作而成的手環。\n這股心意會保護裝備者，\n讓防禦力上升。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/20, /*sale_price*/320, /*require_level*/17, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102611, /*equipment_name*/"魔法眼鏡", /*description*/"能夠提升裝備者魔法攻擊力的\n特殊眼鏡。\n將魔法石當作素材\n製作而成。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/9, /*hp*/0.0, /*atk*/0.0, /*magic_str*/11.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102612, /*equipment_name*/"智慧寶珠", /*description*/"能夠提供戰鬥所需之『仁．智．勇』\n的『智』之寶珠。\n是能夠讓腦袋變得清晰，並能提升魔法效果的智力護身符。", /*promotion_level*/2, /*craft_flg*/0, /*equipment_enhance_point*/12, /*sale_price*/160, /*require_level*/10, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/25.0, /*wave_energy_recovery*/5.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/1, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102613, /*equipment_name*/"魔法單眼鏡", /*description*/"高階魔法師會使用的單眼鏡。\n金屬部份刻有符文，\n有著能讓裝備者魔力上升的力量。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/6, /*sale_price*/320, /*require_level*/17, /*hp*/0.0, /*atk*/0.0, /*magic_str*/24.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/102614, /*equipment_name*/"圖紋手鐲", /*description*/"銀製手鐲上鑲嵌著魔法石，\n並且刻著特殊印記的配件。\n能夠活化魔法的流動，\n有著能提升使用者魔力的效果。", /*promotion_level*/2, /*craft_flg*/1, /*equipment_enhance_point*/16, /*sale_price*/320, /*require_level*/18, /*hp*/0.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103011, /*equipment_name*/"閃耀之劍", /*description*/"使用隕石碎片製成的單刃彎刀。\n當刀身照耀於光線之下時，\n將會像星空一般閃爍。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/90.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103012, /*equipment_name*/"龍紋劍", /*description*/"描繪那龍之吐息所製成的一把劍。\n有著較為寬闊的刀刃。比起突刺，\n更適合用來斬切。揮動它的模樣，\n看起來宛若是龍在空中飛舞一般。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/115.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103013, /*equipment_name*/"羽刃之劍", /*description*/"某位魔法師為了接近戰\n所使用過的劍。腕力較弱的他，\n在大鳥的羽毛上施加了魔法，\n進而造出了這把極為輕型的劍。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/37, /*hp*/135.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103041, /*equipment_name*/"月光刀", /*description*/"存在於東國的某個劍術流派中，\n授予獲得宗師認可的人的刀劍。\n因寄宿著月光之力，而更能夠\n琢磨精鍊裝備者的五感與靈力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/3000, /*require_level*/30, /*hp*/65.0, /*atk*/58.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103042, /*equipment_name*/"雷神刀", /*description*/"是某位技術甚高的武士，\n在退治雷獸時所使用的刀。\n斬除了雷獸的刀身，\n寄宿著閃電之力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/40, /*hp*/75.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103071, /*equipment_name*/"鉑金小刀", /*description*/"是以高價珍貴的白金，\n打造而成的短劍。\n不少貴族把它藏在懷中以用來護身。。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/40.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103072, /*equipment_name*/"吸血鬼匕首", /*description*/"擁有意志並且不斷渴望著鮮血，\n是把令人畏懼的短劍。　\n沾染上血跡而生鏽的部分\n將會比平時更閃爍著惡寒之氣。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/3.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103101, /*equipment_name*/"龍牙之爪", /*description*/"仿照兇惡之黑龍的下顎形狀而製的鉤爪。\n暗器的一種，據說以前使用這種鉤爪暗殺人時，會在爪上塗上毒液，提高殺傷能力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103131, /*equipment_name*/"邱比特之弓", /*description*/"過去曾有一位男人以此弓狩獵，並將狩得的獵物贈於心上人成就了愛戀。\n他因此事而做為契機閃念，\n以愛神命名這把弓。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/53.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103132, /*equipment_name*/"惡魔之弓", /*description*/"是被人類迫害，而被追討到地底深處的魔族所製造的弓。\n只要是踏入他們的領域的人類，\n就會葬送於這把弓下。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/61.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103161, /*equipment_name*/"騎士的驕傲", /*description*/"贈送給名譽騎士的斧戟。\n其刀刃堅實銳利不容折損，\n如同體現了為國為民而戰的騎士，\n心中的那股驕傲。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/18, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103162, /*equipment_name*/"絕對之矛", /*description*/"用來刺殺的長戟。\n它的殺傷能力，\n能輕易刺穿堅固的鎧甲，\n僅一擊便能奪走敵人性命。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/46.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103191, /*equipment_name*/"天球斧", /*description*/"以湛藍天體點綴的戰斧。\n象徵著世界本質的天體，\n能吸收天與地的能量，\n進而賦予裝備者力量。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103192, /*equipment_name*/"麗花戰斧", /*description*/"被賦予著華麗之花的這把斧頭，\n每次揮舞，就會綻放出大朵鮮血。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/30, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103193, /*equipment_name*/"閃電之斧", /*description*/"是研究先進武器製造的某個工坊的自豪之作。\n能在斧頭的內部積蓄雷電，\n並能夠在攻擊之際一齊放出。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/66, /*sale_price*/4000, /*require_level*/41, /*hp*/0.0, /*atk*/77.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103221, /*equipment_name*/"芒刺血杖", /*description*/"某個殘酷的魔法師團體，\n使過的法杖。\n被害者人們的鮮血，\n將此法杖染上了鮮明的血紅色。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/19, /*hp*/0.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103222, /*equipment_name*/"惡魔之角", /*description*/"據說是某位魔法師與惡魔交易之後，\n而獲得的問題法杖。\n傳說被擊倒的敵人之魂，\n將會透過此法杖獻上給惡魔。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/4000, /*require_level*/40, /*hp*/0.0, /*atk*/0.0, /*magic_str*/77.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103251, /*equipment_name*/"天球的守護", /*description*/"是將占星術為業的某個魔法師一族，\n所使用的法杖。\n據說他們能藉由觀測星象，\n預知未來。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/12, /*sale_price*/1400, /*require_level*/20, /*hp*/0.0, /*atk*/0.0, /*magic_str*/40.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103252, /*equipment_name*/"花蕾之杖", /*description*/"試圖拯救人們的意念，\n使花蕾綻放，\n而那純淨的能量，\n成為了治癒的力量", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/3000, /*require_level*/29, /*hp*/0.0, /*atk*/0.0, /*magic_str*/53.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103281, /*equipment_name*/"時尚盔甲", /*description*/"以強大又時尚的騎士為主題，\n所製作出的鎧甲。但不管再怎麼盛裝打扮，若不具有相應的實力，魅力定會減半。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/32, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/11.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103282, /*equipment_name*/"戰陣鎧衣", /*description*/"將威猛的男人全部擊敗，\n成為鬥技場女王的女戰士的鎧甲。\n傳聞對戰對手只見到她那\n如野獸般的銳利眼光就戰意全失了。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/42, /*sale_price*/3000, /*require_level*/32, /*hp*/325.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103283, /*equipment_name*/"重金屬盔甲", /*description*/"使用厚實的鐵板組合而成的鎧甲。\n捨去輕便性，而著重於防禦力。\n是使用斧頭或大劍的戰士們\n愛用的裝備之一。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/380.0, /*atk*/17.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103311, /*equipment_name*/"華麗盔甲", /*description*/"愛好華麗的騎士，向設計師委託而製成的鎧甲。\n過於華麗的緣故，在戰場上格外顯\n眼，在敵人眼中也正好成為了靶子。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/245.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103341, /*equipment_name*/"多層次服飾", /*description*/"在布料上覆蓋一層皮革長袍的服飾。\n具有優異的防寒效果，\n適合在寒冷的地方採集素材時穿著。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/1400, /*require_level*/21, /*hp*/180.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/1.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/160.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103342, /*equipment_name*/"宮廷服飾", /*description*/"任職於王城內的女騎士所著裝之服。\n使用了高級的皮革與黑色金屬，\n在胸前還有顯示騎士的身分地位的裝飾。\n構思極具品味。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/13.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/210.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103343, /*equipment_name*/"玻斯菊服飾", /*description*/"是精靈的女王，在和其他種族的\n和平協定會議上所穿著的服飾。\n精靈族為以防萬一，\n在女王的服飾上施加了強力魔法的薄紗。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/75, /*sale_price*/4000, /*require_level*/44, /*hp*/205.0, /*atk*/15.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/370.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103371, /*equipment_name*/"邱比特長袍", /*description*/"以傳說中愛神的服裝為形象製作的長袍。\n背上之羽，因魔法漂浮。\n讓人分不出來它與真品之差。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/30, /*sale_price*/3000, /*require_level*/32, /*hp*/235.0, /*atk*/0.0, /*magic_str*/13.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103372, /*equipment_name*/"哲學家長袍", /*description*/"一生輔佐自幼即成為皇帝的少年的哲學家，所著的服裝。\n指引皇帝踏上正途，\n他引領了國家朝著更好的未來邁進。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/43, /*hp*/245.0, /*atk*/0.0, /*magic_str*/14.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103401, /*equipment_name*/"石榴石之盾", /*description*/"驍勇果敢的將軍之盾。\n他只要一站上戰場，\n親自率兵破軍殺敵，\n引領軍隊迎向勝利。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/34, /*hp*/290.0, /*atk*/8.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103431, /*equipment_name*/"忍者鞋", /*description*/"穿上之後可以消除腳步聲的特殊靴。\n傳說是東國間諜所穿的靴子，\n但有關於他們的任何情報，\n一直都是個謎。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/23, /*hp*/90.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/1.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/3.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103432, /*equipment_name*/"擊劍之靴", /*description*/"為了使用西洋劍的劍士們，\n所製成的靴子。\n相當輕盈，是對於比起力量更重視機動性的細劍士，最合適的一件裝備。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/3000, /*require_level*/35, /*hp*/115.0, /*atk*/26.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/4.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103461, /*equipment_name*/"女巫宮廷靴", /*description*/"魔法師的正式服裝之一的靴子。\n附有無論走在任何地方\n都不會髒掉的魔法。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/46, /*sale_price*/3000, /*require_level*/34, /*hp*/115.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103491, /*equipment_name*/"黑龍頭盔", /*description*/"信仰曾存在的黑龍的騎士團所用的頭盔。\n他們為了供奉活祭品給龍，不斷奪走無辜的人的性命。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/3000, /*require_level*/35, /*hp*/235.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103521, /*equipment_name*/"女巫髮箍", /*description*/"魔法師的一家人，贈送給為修行\n而離家的女兒的髮箍。\n是母親以自己的魔力，以及愛所製成。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/20, /*sale_price*/1400, /*require_level*/23, /*hp*/90.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103522, /*equipment_name*/"女巫帽", /*description*/"在傳統的魔法師的帽子上\n加了能輔助魔力的配件。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/34, /*hp*/115.0, /*atk*/0.0, /*magic_str*/26.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103551, /*equipment_name*/"蛋白石耳環", /*description*/"蘊藏魔力的\n蛋白石耳環。\n能讓裝備者從身體內部湧出活力，\n使功擊力提升。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/33.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/90.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103552, /*equipment_name*/"獅鷲之羽飾", /*description*/"以上半身為鷹鷲，下半身為猛獅的\n格里芬之羽所製成的羽飾。\n因而寄宿著猙獰猛獸之力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/3000, /*require_level*/36, /*hp*/0.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/235.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103553, /*equipment_name*/"緋紅鑽石", /*description*/"世界上極為罕見的緋紅色鑽石。\n據說是封進了龍的火焰，\n裝備後即猶如\n獲得烈火一般的力量。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/46, /*hp*/70.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103581, /*equipment_name*/"綠寶石耳環", /*description*/"蘊藏魔力的\n綠寶石耳環。\n能給予裝備者不屈不撓的精神力，\n使得防禦力進而上升。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/180.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103582, /*equipment_name*/"八芒星墜飾", /*description*/"八芒星象徵無限循環，\n能夠於大自然中吸收\n八種不同的元素。不斷的守護著裝備者。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/40, /*sale_price*/3000, /*require_level*/37, /*hp*/0.0, /*atk*/26.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103583, /*equipment_name*/"獅子王的守護", /*description*/"曾經被稱為獅子王的大王之物。\n生涯大半皆活躍於征戰之中的他，\n那份驍勇善戰，使大多數的人民\n對他抱持著敬畏之心，", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/45, /*sale_price*/4000, /*require_level*/46, /*hp*/270.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103611, /*equipment_name*/"綠松石耳環", /*description*/"蘊藏魔力，\n以綠松石作成的耳環。\n能讓裝備者得到智慧，\n使魔法攻擊力上升。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/0.0, /*magic_str*/30.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103612, /*equipment_name*/"珍珠耳環", /*description*/"蘊藏魔力的\n珍珠耳環。\n能增加裝備者的集中力，\n進而提升魔力。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/36, /*sale_price*/1400, /*require_level*/25, /*hp*/0.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103613, /*equipment_name*/"魔法使眼鏡", /*description*/"賢者在探索龍脈時會裝備使用。\n會於研究大地中流動的氣脈，\n或是調查大陸的環境與狀態時使用。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/41, /*sale_price*/3000, /*require_level*/36, /*hp*/0.0, /*atk*/0.0, /*magic_str*/39.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/35.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/103614, /*equipment_name*/"月桂樹的悲悽", /*description*/"精靈達芙妮將自身姿貌，\n轉化為月桂樹。傳聞深愛著她的太陽神．阿波羅，\n將月桂樹葉做為永恆的愛之印記，\n一直配戴在身上。", /*promotion_level*/3, /*craft_flg*/1, /*equipment_enhance_point*/25, /*sale_price*/4000, /*require_level*/46, /*hp*/0.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104011, /*equipment_name*/"天使之劍", /*description*/"據說是大天使所使用過的\n白銀之劍。\n具備將邪惡的存在消除的\n退魔之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/165.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104012, /*equipment_name*/"太陽劍", /*description*/"擁有太陽神．阿波羅的保護之力的劍。得到了此劍的人物，\n被譽為太陽王，並且得到的繁榮像名留青史一般，永遠留存。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/70.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/15.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104013, /*equipment_name*/"冰之大劍", /*description*/"能於斬擊的同時，\n一併發動冰之魔法來討伐敵人。\n因蘊含著冰之妖精的魔力，\n總是帶著陣陣寒氣。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/85.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104014, /*equipment_name*/"渾沌之劍", /*description*/"吸取了上萬活人鮮血的妖劍。\n只要被詛咒的話就會化為渴求鮮血\n的傀儡，若能駕馭它的話，就可獲得\n如鋼鐵般的力量。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/140.0, /*atk*/95.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104041, /*equipment_name*/"妖蝶之刀", /*description*/"一把沉睡在森林遺跡最深處的刀劍。\n傳聞中被此劍所選中之人，\n將會受到蝴蝶的指引，\n而得以抵達它所在之處。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/80.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104042, /*equipment_name*/"光輝之劍", /*description*/"接到光之女神．阿格萊亞神諭的\n刀匠，耗費了一生而鍛造的光之劍。以人類得以對抗魔神之武器為名，\n由威震八方的勇者持有。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/192, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/10.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104043, /*equipment_name*/"鷹神之煌劍", /*description*/"可支配廣闊天際的崇高的鷹神之劍。\n振臂一揮，就宛若鎖定獵物的老鷹，\n以魔力加速的那鋒刃，\n將會分毫不差地朝敵人的要害突擊。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104044, /*equipment_name*/"鳳凰刀", /*description*/"出現於日出之處並寄宿有鳳凰神\n之力的刀。纏繞之火會隨意志\n強度來變化，若是擁有強韌意志之人，\n則會化為可融化鐵的烈火。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/180, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104071, /*equipment_name*/"祕寶匕首", /*description*/"傳聞為某個矮人的名匠所造，\n且獻給了人類之王的短劍。\n採用非現存的礦物的未知素材。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104072, /*equipment_name*/"公主懷劍", /*description*/"那歷史上存續最悠久的小國王族，\n所相傳的短刀。代代皆以王女繼承，\n她們退去降臨的災禍魔惡，\n持續守護著國土的安寧。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/50.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/300.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104073, /*equipment_name*/"暗黑物質短劍", /*description*/"為肅清雙手沾染上禁忌，無法無天的邪惡魔法師，而製作的短劍。施加了能夠封印魔法發動的術式。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104074, /*equipment_name*/"獨角獸匕首", /*description*/"只有天真無邪的女性可使用的短劍。\n由於可拒各種汙穢於門外，\n因此抱有邪惡意志之人\n甚至無法觸碰。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/160, /*sale_price*/18000, /*require_level*/59, /*hp*/100.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104101, /*equipment_name*/"火焰拳套", /*description*/"以煉獄之焰鍛造而成的\n深紅拳套。\n從拳尖散射肆虐的業火，\n能將眼前的敵人徹底燒之殆盡。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/50, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104102, /*equipment_name*/"獅子王手套", /*description*/"被譽為獅子王的名君，\n曾使用過的黃金拳套。傳說此拳套能賦予使用者\n無限的力量，以及一騎當千的戰力。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/81.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/12.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104103, /*equipment_name*/"暴雪之爪", /*description*/"孤軍一人便壞滅萬人大軍的冰之女\n神的魔力，所封印的鉤爪。\n那刺骨的寒風，將如同遭遇暴雪般受冰寒的敵人的戰意給徹底抹滅。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/230, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/90.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/5.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104104, /*equipment_name*/"蒼雷拳", /*description*/"受勇猛的雷神所加護的護手。\n放出的蒼之雷擊不只可一瞬間\n電焦敵人，甚至連雷雲\n都能夠呼喚過來。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104131, /*equipment_name*/"雷光弓", /*description*/"英雄為擊倒那食人海龍，\n將雷之力封存於此弓之中。\n傳說那被電光纏繞著的箭矢，\n僅僅靠一擊便將那海龍給埋葬。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/145, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/73.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104132, /*equipment_name*/"天使之弓", /*description*/"古代的末日之戰時，\n大天使與惡魔一戰時所持的大弓。傳聞滿弦放出的一矢，\n便能夠貫穿地獄直抵冥河。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/65.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104133, /*equipment_name*/"月亮女神之弓", /*description*/"寄宿著狩獵女神的庇佑的弓。\n擁有對獵物百發百中\n和破魔效果的箭矢，\n僅一射便能徹底扭轉戰局。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/75.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/6.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104134, /*equipment_name*/"精靈樹之弓", /*description*/"以精靈樹為素材所製成的弓。\n弓內蘊涵精靈界的神聖魔力，\n擊出的箭擁有可貫穿\n各種敵人的威力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/60.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/5.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104161, /*equipment_name*/"緋龍戟", /*description*/"以棲息於火山周遭的\n龍背上長的針刺所製成的戰戟。\n那鋒利的刃尖，因長年寄宿於龍之體內，而蘊含著火炎之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104162, /*equipment_name*/"皇后之矛", /*description*/"傳說中支配整個大陸的女王所使用的大戰戟。她擁著睿智才華，在長年征戰之下，\n以此戰戟之力將整座大陸囊括懷中。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/68.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104163, /*equipment_name*/"暴風權杖", /*description*/"傳說中的槍騎士所持有的名戟。\n只要一揮舞，那真空之刃便會\n捲起旋風，無須以刃直接碰觸\n也能將敵人一網打盡。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/205, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/85.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/5.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104164, /*equipment_name*/"蓋亞戰斧", /*description*/"受大地母神所加護的槍。\n槍身絕不會斷裂缺損或粉碎。\n那份強韌會寄宿在持有者體內，使\n臂力得到驚人的成長。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/59, /*hp*/0.0, /*atk*/80.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104191, /*equipment_name*/"紫龍斧", /*description*/"各地集結的英傑們齊心，將\n威脅世間的邪龍封印於此斧之中。\n寄宿著龍之魂的這把斧頭，\n能夠釋放出造成災害禍殃的能量。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/91.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104192, /*equipment_name*/"地獄火之斧", /*description*/"封印了地獄之火的雙刃斧。那外觀樣貌如同鮮血般赤紅，\n高舉一斬那紅蓮之火將會襲向敵人，\n將對方連骨帶肉給燒之殆盡。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/97.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104193, /*equipment_name*/"戰女王之烈斧", /*description*/"總是君臨於最前線的女王的戰斧。\n雖擁有著超乎想像的重量，但被此\n斧所認定之人將能輕易操使。此斧能一擊打破牢固的城門。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/114.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104194, /*equipment_name*/"水靈破壞者", /*description*/"水之精靈注入精靈之力的斧頭。\n雖然很輕，但卻相當鋒利，\n即使只是接觸到就有可能使得礦物\n一分為二。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/59, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104221, /*equipment_name*/"狂怒之杖", /*description*/"因妻子被殺害，\n而化為復仇之鬼的魔法師所持的法杖。\n他將自身的憤恨注於此杖之上，\n把所有與妻之死有關之人抹殺殆盡。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/48, /*hp*/0.0, /*atk*/0.0, /*magic_str*/91.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104222, /*equipment_name*/"太陽之杖", /*description*/"遠古的賢人們，集結太陽的碎片所製成的神祕之杖。\n傳聞被此法杖認定的人，\n將會被賦予全知全能之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/52, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104223, /*equipment_name*/"司法之神的錫杖", /*description*/"據說是司掌正義、和平與真實之神，\n所持有的錫杖。\n傳聞得到此錫杖之人，\n將會被被託付神之示諭。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/52, /*hp*/0.0, /*atk*/0.0, /*magic_str*/104.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104224, /*equipment_name*/"大惡魔法杖", /*description*/"寄宿有高等惡魔之魂的杖。\n對敵人毫不留情，\n只要施展擊倒敵人的魔法\n便能發揮其真正價值。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/0.0, /*magic_str*/108.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104251, /*equipment_name*/"月光之杖", /*description*/"據說是月之女神在治療虔誠信徒的疾病時，所使用過的長杖。\n傳說畏懼著女神之力的死神，\n將此長杖給埋藏於下界之中。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/100, /*sale_price*/7200, /*require_level*/49, /*hp*/0.0, /*atk*/0.0, /*magic_str*/73.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104252, /*equipment_name*/"生命之杖", /*description*/"以被譽為生命之樹的巨大樹枝，\n所製造而成的\n奇蹟之杖。傳聞中只要一揮，連腐朽之木也能\n長出新芽，輕觸於地則能湧泉。\n", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/10000, /*require_level*/53, /*hp*/0.0, /*atk*/0.0, /*magic_str*/67.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104253, /*equipment_name*/"不死鳥之杖", /*description*/"擁有不死鳥庇護的神聖之杖。\n能獲得無窮的魔力，\n因魔力不會用盡，\n可無止境地盡情施展魔法。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/230, /*sale_price*/15000, /*require_level*/53, /*hp*/0.0, /*atk*/0.0, /*magic_str*/77.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/5.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104254, /*equipment_name*/"大天使之杖", /*description*/"受滿溢慈愛的天使所加護的杖。\n由於可回應憐愛他人的情感，\n因此幫助同伴的魔法的效果\n將明顯地提升。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/0.0, /*magic_str*/86.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104281, /*equipment_name*/"隱身鎧衣", /*description*/"施加了隱身魔法的特殊鎧甲。\n據說是某國的公主因陷入了一場不被允許的戀愛，\n在與戀人私奔時使用的鎧甲。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/51, /*hp*/450.0, /*atk*/20.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104282, /*equipment_name*/"祕銀脛甲", /*description*/"當富有銀之光輝與鋼之強悍，\n的稀有金屬，\n碰上當代首屈一指的名匠，\n則誕生出兼具外貌與強韌的至高無上的鎧甲。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/172, /*sale_price*/10000, /*require_level*/52, /*hp*/250.0, /*atk*/25.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104283, /*equipment_name*/"十字軍板甲", /*description*/"以神之名義驅逐了異教徒，\n高明遠播的勇者所穿著的鎧甲。\n被賦予著神的恩惠與庇祐之鎧，\n唯有擁有純淨之魂的人才能獲得。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/200, /*sale_price*/15000, /*require_level*/54, /*hp*/150.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104284, /*equipment_name*/"緋紅鎧甲", /*description*/"由黑暗女帝贈送，為護國而戰的\n騎士公主所持有的紅色鎧甲。\n其神聖光輝使敵人膽寒，\n能給予民眾勇氣。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/160.0, /*atk*/44.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104311, /*equipment_name*/"天使盔甲", /*description*/"天使們在征戰時所使用的鎧甲。\n低等級惡魔連接近此鎧甲都辦不到，\n只是被那白銀所反射的光芒給照耀，\n就會被消滅。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/155, /*sale_price*/7200, /*require_level*/51, /*hp*/450.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104312, /*equipment_name*/"朱紅鎧甲", /*description*/"以單騎赴敵而獻上自身之命，\n那瘋狂戰士之魂所寄宿的鎧甲。\n著此鎧甲於身者，\n將能得到那戰鬼之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/125, /*sale_price*/10000, /*require_level*/52, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/400.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104313, /*equipment_name*/"紫羅蘭盔甲", /*description*/"被視為戰場的死神而遭人畏懼的大將軍，\n所配備的美麗鎧甲。\n敵軍兵將光是看見那菫色的甲冑，便將無心再戰且落荒而逃。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/252, /*sale_price*/15000, /*require_level*/54, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104314, /*equipment_name*/"龍威戰甲", /*description*/"屠龍英雄所愛用的重鎧甲。\n以龍為概念所設計的特厚裝甲，\n據說能夠防護斬釘截鐵的爪擊，\n以及灼熱的龍之吐息。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/330, /*sale_price*/18000, /*require_level*/58, /*hp*/100.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/1.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104341, /*equipment_name*/"月下之法衣", /*description*/"神官受王之敕令而賭上性命，\n所製作的絢麗法衣。\n傳聞因此衣是於月光中縫製而成，\n只要被月光照耀，便會發出七彩光芒。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/190, /*sale_price*/7200, /*require_level*/51, /*hp*/360.0, /*atk*/10.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/325.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104342, /*equipment_name*/"隱者服飾", /*description*/"世俗譽之為隱士，窺視了魔道\n深淵之人所著的衣物。\n以滲入魔力的絲與線\n縫製而成。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/150, /*sale_price*/10000, /*require_level*/52, /*hp*/180.0, /*atk*/35.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104343, /*equipment_name*/"蒼天長袍", /*description*/"若為了人民，即便是對神也會刀劍相向，\n抱有義俠之志的術士穿的長袍。\n蘊含著至高無上光輝的天之庇護，\n將會賦予裝備者鋼鐵般的意志。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/270, /*sale_price*/15000, /*require_level*/54, /*hp*/200.0, /*atk*/45.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104344, /*equipment_name*/"暗黑懼袍", /*description*/"黑暗女帝身穿的衣裳。\n潛伏如黑暗的意念，\n能夠魅惑任何魔物，\n施予眾人恐怖。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/330, /*sale_price*/18000, /*require_level*/58, /*hp*/200.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/7.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/250.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104371, /*equipment_name*/"大魔導師長袍", /*description*/"將人生奉獻給魔道的大魔導師，\n所穿過的長袍。\n因為曾經的持有者的強大力量，\n依舊持續散發著不祥的魔力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/51, /*hp*/165.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104372, /*equipment_name*/"聖者長袍", /*description*/"與天使們一同\n與惡魔對峙的聖者，\n所身穿的長袍。\n這份崇高已達神話的領域。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/141, /*sale_price*/10000, /*require_level*/52, /*hp*/240.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/2.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104373, /*equipment_name*/"翠綠靈衣", /*description*/"據說是住在神樹森林裡的\n偉大賢者身穿的長袍。\n蘊含有神靈之力，穿著時\n可以防備任何邪惡魔力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/321, /*sale_price*/18000, /*require_level*/58, /*hp*/200.0, /*atk*/0.0, /*magic_str*/25.0, /*def*/4.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/3.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104401, /*equipment_name*/"女帝之盾", /*description*/"只有女王完全信賴的進衛兵，\n才能得到的盾牌。\n因寄宿著絕對的守護之力，\n無論魔法還是詛咒皆會反彈。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/166, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/18.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/4.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104402, /*equipment_name*/"守護者之盾", /*description*/"於亂世中的小國，以生命守護王女之命的騎士的盾。\n爾後王女登基為女王，\n於她身邊的那名騎士，被以救國英雄知名稱讚。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/246, /*sale_price*/15000, /*require_level*/55, /*hp*/0.0, /*atk*/50.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/3.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104431, /*equipment_name*/"聖騎士脛甲", /*description*/"接受了神聖之力所庇祐，\n聖騎士們所穿著的足甲。\n為守護人民們寶貴的性命，\n他們總是命懸一線地與魔物戰鬥。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/155, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/9.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/6.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104432, /*equipment_name*/"天使之靴", /*description*/"在神話中曾出現過的神聖腳步防具。\n不論任何攻擊皆無法靠近，\n即使於熱炎之中，\n也能毫髮無傷步行。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/140, /*sale_price*/10000, /*require_level*/52, /*hp*/220.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/5.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/15.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104433, /*equipment_name*/"妖精護靴", /*description*/"受太古妖精所加持的腿甲。\n除了巨大的靈力，\n還有驅魔的秘銀點綴在周圍，\n使破魔之力大增。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/290, /*sale_price*/18000, /*require_level*/58, /*hp*/0.0, /*atk*/38.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/5.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104461, /*equipment_name*/"巫師靴", /*description*/"只有被王族認可的魔法師，\n才會獲贈的特別道具。\n只有於此行百年的精靈職人，\n才有辦法做得出來的靴子。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/0.0, /*magic_str*/27.0, /*def*/4.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104462, /*equipment_name*/"死靈術士之靴", /*description*/"被稱為司掌黃泉者而遭人畏懼，\n是稀有的死靈術士所鍾愛的靴子。\n只是聽聞到那靴音，精氣便會被\n奪去，且將隸屬於裝備者麾下。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/220, /*sale_price*/15000, /*require_level*/55, /*hp*/100.0, /*atk*/0.0, /*magic_str*/15.0, /*def*/1.0, /*magic_def*/12.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104463, /*equipment_name*/"寶瓶之靴", /*description*/"蘊含掌管水元素的\n四大精靈之力的美麗靴子。\n據說藉由偉大精靈之力，\n能夠激發裝備者的魔力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/18000, /*require_level*/58, /*hp*/40.0, /*atk*/0.0, /*magic_str*/50.0, /*def*/0.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/7.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/2.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104491, /*equipment_name*/"羽翼圓舞曲", /*description*/"被稱為是空中舞者的半神半人騎士\n所使用的頭盔。\n據說他會在空中來回飛舞，\n玩弄敵人於股掌之間。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/135, /*sale_price*/7200, /*require_level*/52, /*hp*/490.0, /*atk*/18.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104521, /*equipment_name*/"巫師兜帽", /*description*/"是受國家認可，\n身為最高階魔法師的證明。\n擁有此兜帽的人，\n將被允許立處於國王的身邊。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/105, /*sale_price*/7200, /*require_level*/52, /*hp*/165.0, /*atk*/0.0, /*magic_str*/37.0, /*def*/0.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104522, /*equipment_name*/"法王兜帽", /*description*/"歷代的法王出征時，\n會配戴的頭盔。\n因被賦予著破魔的效果，\n輕而易舉的識破幻術。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/180, /*sale_price*/10000, /*require_level*/52, /*hp*/90.0, /*atk*/0.0, /*magic_str*/45.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104523, /*equipment_name*/"福音之冠", /*description*/"某個魔法王國的女王所身著過，\n擁有顯赫正統背景的冠帽。以增強自身魔力的那份力量，\n女王為王國長久以來帶來了秩序。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/246, /*sale_price*/15000, /*require_level*/55, /*hp*/100.0, /*atk*/0.0, /*magic_str*/45.0, /*def*/3.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/5.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104551, /*equipment_name*/"元素之心", /*description*/"統整四大精靈的魔力，\n並且封印之的特殊礦石。\n稀世的鍊金術士\n完成的一大發明品。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/0.0, /*atk*/66.0, /*magic_str*/0.0, /*def*/2.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/5.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104552, /*equipment_name*/"太陽的護身符", /*description*/"使用了接受過太陽神庇護的寶玉，\n為了與魔抗衡的戰士們所製的首飾。\n就算與強大的敵人正面對峙，\n也能不陷入恐慌狀態與之戰鬥。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/0.0, /*atk*/70.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104553, /*equipment_name*/"焰帝的戒指", /*description*/"戒指上所鑲嵌的紅寶石中，寄宿著\n自創世之時便燃燒至今的火焰。\n配戴後將能夠讓可燒毀萬物\n的火焰纏繞於身上。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/200.0, /*atk*/74.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104554, /*equipment_name*/"貓神的項墜", /*description*/"被認為是由貓神所生出的寶石。\n配戴者可獲得驚人的視力\n與反射神經，因此絕不會\n看漏任何人，或讓其逃跑。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/0.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/8.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104581, /*equipment_name*/"替身手環", /*description*/"君臨了千秋萬世的皇帝，\n命人鍛造而出的傳奇的魔導具。\n傳聞會替裝備者\n承受降下的災厄。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/240.0, /*atk*/0.0, /*magic_str*/10.0, /*def*/5.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104582, /*equipment_name*/"月之手鍊", /*description*/"以封存了月之魔力的水晶，\n所製成的手鍊。\n持續散發的靈妙之光成就了庇護，\n無論何等詛咒都能輕易驅散。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/9.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104583, /*equipment_name*/"聖信徒的項墜", /*description*/"只有被聖靈所認可之人才被允許\n配戴的項鍊。\n藉由聖靈無限的慈愛\n可讓配戴者免於威脅。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/200.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/10.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104611, /*equipment_name*/"新月的嘆息", /*description*/"因受計謀而以弓傷了心愛之人，\n那月之女神所持有的護身符。\n下弦月的魔力滿負著哀戚，\n能夠將飛來之矢給彈落。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/80, /*sale_price*/7200, /*require_level*/53, /*hp*/100.0, /*atk*/0.0, /*magic_str*/60.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/6.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104612, /*equipment_name*/"龍之淚", /*description*/"是龍之淚超越悠久時空而化為的結晶，\n一種極為稀少的礦石。\n直接撼動裝備者的魂魄，\n傳達了那寄宿於內的龍之力。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/15000, /*require_level*/57, /*hp*/120.0, /*atk*/0.0, /*magic_str*/70.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/104613, /*equipment_name*/"大賢者的寶珠", /*description*/"將許多戰爭引向勝利的大賢者，\n把其所擁有的所有魔力全數注入\n的水晶球。擁有者將被授予引向光榮\n的聰明才智。", /*promotion_level*/4, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/18000, /*require_level*/60, /*hp*/0.0, /*atk*/0.0, /*magic_str*/80.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/16.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105011, /*equipment_name*/"雷霆神鋒", /*description*/"由雷神劈在地上的雷霆之怒\n所生的大劍。一揮動就能產生\n劈開長空的閃電，據說任何敵人\n都能掃除。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/120.0, /*atk*/105.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105012, /*equipment_name*/"天開之聖劍", /*description*/"據說是屬於前往聖域的關鍵物品，\n神聖的大劍。\n憑著劍身上的神氣護佑、\n能令佩戴者消除邪惡的存在。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/380, /*sale_price*/27000, /*require_level*/72, /*hp*/120.0, /*atk*/115.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105013, /*equipment_name*/"罪過之大劍", /*description*/"不斬人而斬除罪業的大劍。\n據說被此劍所斬的罪人將不會\n受傷，但其代價是將餘生奉獻給\n贖罪一途。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/475, /*sale_price*/30000, /*require_level*/80, /*hp*/140.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/8.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105041, /*equipment_name*/"百華白櫻刀", /*description*/"具備有映照潔白、盛開的櫻花般\n美麗的刀。當裝在柄上的鈴一響，\n就如繁花落盡的輕盈，\n能將來犯的敵人一一斬除。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/118.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105042, /*equipment_name*/"流禍之蒼刃", /*description*/"在天地異變之中沉沒的帝國魔法劍。\n劍刃上刻印著國家的歷史。\n受到水之精靈寵愛的刀身，\n能揮灑出流水一般變幻自如的斬擊。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105043, /*equipment_name*/"妖刀血鴉", /*description*/"受詛咒而時常嗜血的刀。\n據說此刀若判斷這一斬不值得\n將會咒死使用者，並使其化成\n籠罩於刀身的妖氣。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/592, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/125.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105071, /*equipment_name*/"翡翠匕首", /*description*/"受到豐饒之神所庇佑的匕首。\n那光彩鮮漾的刀刃\n據說能吸收大自然能量，\n從而揮灑出鮮麗的斬擊。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/120.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/150.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105072, /*equipment_name*/"熔岩烈鋒", /*description*/"寄宿著火之精靈的短劍。\n以堪比熔岩般灼熱的刀身\n來回刺出的一擊，令接觸到的敵人\n在那瞬間燒熔。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/405, /*sale_price*/27000, /*require_level*/72, /*hp*/40.0, /*atk*/115.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105073, /*equipment_name*/"常春國短劍", /*description*/"由妖精王族代代相傳的護身劍。\n劍刃上刻有妖精族秘傳的魔法，\n具備令使用者驅散邪惡氣息的\n力量。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/60.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105101, /*equipment_name*/"月之拳套", /*description*/"綻放神聖光華，\n造型如月的格鬥武器。\n拳上蘊含月光精華，\n據說能向敵人轟出強烈一擊。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/6.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105102, /*equipment_name*/"深紅之爪", /*description*/"寄宿著魔種的深紅色鉤爪。\n極度殘暴的一擊賦予任何活物的\n只剩絕望，據說能給予對陣的敵人\n絕對的死亡。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/130.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/16.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105103, /*equipment_name*/"冥河之爪", /*description*/"以冥府之冰精鍊而成的冰爪。\n被爪割裂的目標將在瞬間凍結，\n成為無法說話的冰凍雕像，\n最後變為冥府的居民。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/470, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/3.0, /*magic_def*/0.0, /*physical_critical*/14.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105131, /*equipment_name*/"白銀大翼弓", /*description*/"以白銀之鳥翼為模樣的弓。\n射出的箭有如飛鳥翔空，\n有如穿梭而過的一擊，\n能夠貫穿任何魔物。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/245, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/100.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/4.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105132, /*equipment_name*/"深淵之弓", /*description*/"受到魔族女王加持的妖弓。\n射出的箭會帶有邪異的魔力，\n如同在黑暗之中亂竄，\n從難以預測的地方射向敵人。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/110.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/3.0, /*physical_critical*/7.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105133, /*equipment_name*/"紅天弓", /*description*/"當傳說的弓手在弓折箭盡的關頭\n從天而降獲得的神聖之弓。\n能承受任何速射，弓手一旦瞄準，\n箭矢絕對會命中。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/126.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/15.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/3.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105161, /*equipment_name*/"零霜之槍", /*description*/"據傳被封印在傳說中的\n冰獄的冰霜之槍。\n被其一槍刺中的目標，\n據說會在哀嚎中逐漸冰封。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/98.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105162, /*equipment_name*/"雄風刺針", /*description*/"接受風神的恩惠，\n渾身正氣的大槍。\n刺出的一擊是\n眼力無法捕捉的銳風，\n能將一切吹散。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/305, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/92.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105163, /*equipment_name*/"護天之聖槍", /*description*/"由守護聖域者使用的神聖之槍。\n就算槍頭不用對準敵人，\n倒在腳下的邪惡屍骸就會\n延綿不絕。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/102.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105191, /*equipment_name*/"死靈之斧", /*description*/"封入有冥界之力的斧。掌管死亡\n的使者在為亡者引路時所使用，\n據說在其鈍重之聲響起的同時，\n就能將生者領往冥界。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/400, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/2.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/1.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105192, /*equipment_name*/"神判之聖斧", /*description*/"是天界施行裁決時所使用的\n神聖刃斧。\n目標的罪業越大、則威力越大，\n顯現神祇的威嚴。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/120.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105193, /*equipment_name*/"蓋亞之斧", /*description*/"由古代女戰士揮動的戰斧。\n此斧所屠殺之人，將被接引至\n眾神所居住的土地肥沃的\n理想國去。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/430, /*sale_price*/30000, /*require_level*/80, /*hp*/60.0, /*atk*/130.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/12.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105221, /*equipment_name*/"異域法杖", /*description*/"封入有異界魔力的杖。\n蘊含有爆發性的威力，\n據說若不是具備相當實力的人，\n無法解放其威力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/0.0, /*magic_str*/100.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/3.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105222, /*equipment_name*/"創世法杖", /*description*/"是世紀級大魔導士的愛用法杖。\N據說蘊含創造天地的至理，\n光是一揮就能令魔物\n點滴無存地消逝。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/0.0, /*magic_str*/110.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/12.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/20.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105223, /*equipment_name*/"克勞諾斯雷杖", /*description*/"將轟透天際的雷電之力封入的\n神聖之杖。雖是人手難以負荷的\n霸烈之杖，但每用一次都能把\n任何敵人燒毀。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/525, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/0.0, /*magic_str*/118.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/14.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/2.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105251, /*equipment_name*/"焰靈法杖", /*description*/"由愛所生的火焰女神所加持的杖。\n如燃燒般溢出的愛之光輝，\n據說不只能治癒受傷的夥伴，\n還能療癒心靈。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/120, /*sale_price*/24000, /*require_level*/64, /*hp*/0.0, /*atk*/0.0, /*magic_str*/95.0, /*def*/0.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/6.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105252, /*equipment_name*/"神花之聖杖", /*description*/"因慈愛的精神而\n衍生出靈花結晶的聖杖。\n蘊含著無窮無盡的治癒能力，\n見杖如見女神親臨。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/300, /*sale_price*/27000, /*require_level*/72, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/2.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/10.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/2.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105253, /*equipment_name*/"亞特蘭提斯杖", /*description*/"蘊含如母親般之大海生命的聖杖。\n據說此杖一揮，無論是\n任何傷患或疾病，都能夠\n立刻痊癒。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/460, /*sale_price*/30000, /*require_level*/80, /*hp*/0.0, /*atk*/0.0, /*magic_str*/100.0, /*def*/3.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105281, /*equipment_name*/"魅影鎧甲", /*description*/"令見者迷惑心智的魔性鎧甲。\n只要看到散發出的嫋嫋紫煙，\n就會被逃不了的深層幻覺\n給迷惑。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/349, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/32.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/4.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105282, /*equipment_name*/"九天之鎧", /*description*/"以失傳的太古技術製作的鎧甲。\n賦予穿戴者隱形的翅膀，\n在戰場上縱橫無阻、穿梭自如。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/392, /*sale_price*/27000, /*require_level*/77, /*hp*/80.0, /*atk*/42.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105311, /*equipment_name*/"蒼輝之鎧", /*description*/"由青翠透明的結晶製成的鎧甲。\n這種前所未見的結晶，\n以難以加工的強度著稱，\n連想要劃傷表面都做不到。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/511, /*sale_price*/24000, /*require_level*/70, /*hp*/120.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/9.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105312, /*equipment_name*/"聖櫻之鎧", /*description*/"是為拯救瀕臨危機的祖國而奮起\n的勇猛少女所穿著的鎧甲。\n具備不可思議的守護之力，\n能夠反彈任何攻擊。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/401, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/11.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105341, /*equipment_name*/"神盾之衣", /*description*/"是畢生未嘗一敗的女騎士\n所穿著的戰衣。據傳只要穿上身一次，\n在任何戰局都可以逆轉成\n必勝的戰果。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/509, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/34.0, /*magic_str*/0.0, /*def*/4.0, /*magic_def*/2.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105342, /*equipment_name*/"少女之服", /*description*/"只有一塵不染的少女，\n才能穿上的神聖服裝。\n隨著少女奉獻出無垢的祈願，\n能發揮出絕大的破邪之力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/355, /*sale_price*/27000, /*require_level*/77, /*hp*/160.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105371, /*equipment_name*/"純潔的巫女服", /*description*/"是將一切獻給神的巫女\n所穿著的裝束。據傳能令穿戴者集神之寵愛於一身，\n得到不屈服於魔威的\n守護之力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/365, /*sale_price*/24000, /*require_level*/70, /*hp*/120.0, /*atk*/0.0, /*magic_str*/20.0, /*def*/5.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/3.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105372, /*equipment_name*/"女帝之袍", /*description*/"是由過往的偉大女帝所穿著的\n長袍。每一縷絲線都貫注了\n女帝的魔力，令穿戴者能夠\n防護任何魔法。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/370, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/43.0, /*def*/5.0, /*magic_def*/7.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105401, /*equipment_name*/"星詠之圓盾", /*description*/"刻有天體圖的圓盾。\n寄宿有天上眾星的力量，\n能夠抵擋卸除任何攻擊，\n正是受到星光引導與呼喚。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/446, /*sale_price*/24000, /*require_level*/70, /*hp*/0.0, /*atk*/54.0, /*magic_str*/0.0, /*def*/5.0, /*magic_def*/3.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/2.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105431, /*equipment_name*/"煉獄之胄", /*description*/"稱霸各地的戰姬被敵劍斬倒\n為止，一直穿著的軍靴。\n差一步而無法得到世界的遺憾，\n使其蘊含著絕大的魔力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/380, /*sale_price*/27000, /*require_level*/77, /*hp*/60.0, /*atk*/30.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/6.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105521, /*equipment_name*/"忘哭之冠", /*description*/"滅亡於過去、已被遺忘的\n某國公主所戴的頭冠。令下一個持有者也持續\n感應到深切的哀痛之外，\n並賦予宏大的魔力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/370, /*sale_price*/24000, /*require_level*/70, /*hp*/100.0, /*atk*/0.0, /*magic_str*/48.0, /*def*/3.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/8.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105522, /*equipment_name*/"白祈之聖冠", /*description*/"受到庶民廣泛尊敬的聖女\n所贈送的冠冕。若穿戴者的\n心地清朗，就能發揮辟退惡賊的\n力量。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/425, /*sale_price*/27000, /*require_level*/77, /*hp*/0.0, /*atk*/0.0, /*magic_str*/22.0, /*def*/6.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/12.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105551, /*equipment_name*/"千歲耳環", /*description*/"從地齡數千年的地層\n發掘出的魔石耳環。\n其蘊藏浩大的自然之力，\n賦予佩戴者遼闊大地的加護。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/210, /*sale_price*/24000, /*require_level*/67, /*hp*/70.0, /*atk*/64.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/10.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/10.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105552, /*equipment_name*/"盛怒龍之鍊墜", /*description*/"從地齡數千年的地層\n發掘出的魔石耳環。\n其蘊藏浩大的自然之力，\n賦予佩戴者遼闊大地的加護。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/74, /*hp*/0.0, /*atk*/84.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/13.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/25.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105553, /*equipment_name*/"鬥神的荒腕", /*description*/"寄宿著鬥神荒魂的手環。能將大氣\n中的魔力轉換成力量，給予裝備者\n能夠擊碎群山的臂力，但必須擁有\n相應的武力才能使用。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/82, /*hp*/0.0, /*atk*/102.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/24.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105581, /*equipment_name*/"精靈王護石", /*description*/"受精靈王祝福的耳飾。\n據說有涵蓋一整座森林的\n守護之力，能夠辟除任何災厄。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/210, /*sale_price*/24000, /*require_level*/67, /*hp*/200.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/100.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105582, /*equipment_name*/"人魚公主的靈淚", /*description*/"是結晶化的人魚公主流淚\n形成寶玉而製成的首飾。\n牽掛永久的思念持續放出青色光華，\n能拒絕任何詛咒。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/74, /*hp*/60.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/12.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/2.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105583, /*equipment_name*/"英盾王的手環", /*description*/"在戰亂中守護國家而被稱為英盾王\n的君主喜愛的手環。能夠吸收周遭\n的魔力展開守護的力量，利用絕對\n的防禦力成為同胞的盾牌。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/82, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/10.0, /*magic_def*/10.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/2.0, /*enable_donation*/0, /*accuracy*/0.0);
