INSERT INTO `equipment_data` VALUES (/*equipment_id*/105612, /*equipment_name*/"聖獸之祈念", /*description*/"清純無垢的聖獸，\n獨角獸的加持所寄宿的首飾。\n角中蘊含的聖力能提升裝備者的\n魔力，賦予靈敏的感應力。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/280, /*sale_price*/27000, /*require_level*/74, /*hp*/140.0, /*atk*/0.0, /*magic_str*/84.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/10.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `equipment_data` VALUES (/*equipment_id*/105613, /*equipment_name*/"睿智手鐲", /*description*/"窮盡智慧後到達神的領域的賢者所\n留下的裝飾品。只有傑出的智者才\n能夠裝備，會奪取周遭的魔力無限\n地提供給使用者。", /*promotion_level*/5, /*craft_flg*/1, /*equipment_enhance_point*/420, /*sale_price*/30000, /*require_level*/82, /*hp*/0.0, /*atk*/0.0, /*magic_str*/92.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/14.0, /*wave_hp_recovery*/120.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
