INSERT INTO `event_navi_comment` VALUES (/*comment_id*/53003, /*where_type*/53, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"如果完成任務的話，\n就能夠獲得獎勵哦！\n請加油吧", /*voice_id*/1, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/3.414, /*change_face_type*/2, /*event_id*/0);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/53004, /*where_type*/53, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"將任務完成，\n並獲得獎勵吧！", /*voice_id*/2, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/3.099, /*change_face_type*/2, /*event_id*/0);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/54003, /*where_type*/54, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"似乎有能夠\n領取的獎勵了。\n請確認看看", /*voice_id*/3, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/2.801, /*change_face_type*/2, /*event_id*/0);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/54004, /*where_type*/54, /*character_id*/190801, /*face_type*/2, /*character_name*/"花凜", /*description*/"能夠領取獎勵了！\n請檢查看看", /*voice_id*/4, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/2, /*event_id*/0);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/55003, /*where_type*/55, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"這裡是工會管理協會！\n如果您持有討伐證的話，\n就能夠交換獎勵哦", /*voice_id*/1, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/5.506, /*change_face_type*/2, /*event_id*/0);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/55004, /*where_type*/55, /*character_id*/190801, /*face_type*/2, /*character_name*/"花凜", /*description*/"辛苦了！\n在這裡，能夠以討伐證\n來換取獎勵哦！", /*voice_id*/2, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/1.216, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/56003, /*where_type*/56, /*character_id*/190801, /*face_type*/2, /*character_name*/"花凜", /*description*/"辛苦了\n別忘記使用討伐證\n來進行交換哦！", /*voice_id*/3, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/1.539, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/56004, /*where_type*/56, /*character_id*/190801, /*face_type*/2, /*character_name*/"花凜", /*description*/"結束了呢─\n討伐證就是努力的證明！\n拿來換取獎勵吧！", /*voice_id*/4, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/1.875, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `event_navi_comment` VALUES (/*comment_id*/57003, /*where_type*/57, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"參加活動辛苦了！\n各位活躍的樣子\n我們始終非常感謝", /*voice_id*/5, /*start_time*/"2018/11/01 15:59:59", /*end_time*/"2018/11/20 15:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
