INSERT INTO `hatsune_description` VALUES (/*id*/1, /*event_id*/10001, /*type*/1, /*description*/"透過活動，可體驗特別的劇情。\n活動劇情會將於進入活動冒險後解放。");
INSERT INTO `hatsune_description` VALUES (/*id*/2, /*event_id*/10001, /*type*/1, /*description*/"隨著活動冒險的進行，BOSS也會登場。\n可使用BOSS挑戰券，向BOSS發起挑戰\n擊破BOSS後，會獲得大量討伐証，現在立即前往挑戰吧！");
INSERT INTO `hatsune_description` VALUES (/*id*/3, /*event_id*/10001, /*type*/1, /*description*/"交換討伐證將是得到獎勵的契機。\n獎勵之中，也包含在活動中登場之角色的記憶碎片。\n多多蒐集，將角色變成夥伴吧。");
