INSERT INTO `login_bonus_detail` VALUES (/*id*/27, /*login_bonus_id*/40001, /*count*/1, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107501, /*character_name*/"貪吃佩可", /*description*/"今天是紀念日，要慶祝才行呢！\n燒肉、壽司、披薩、蛋糕～☆\n能和你一起開派對，\n我好幸福～♪", /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/28, /*login_bonus_id*/40001, /*count*/2, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107601, /*character_name*/"可可蘿", /*description*/"能夠在今天迎來這個紀念日，\n全拜主人的器量所賜。\n真不愧是主人呢。\n真了不起真了不起♪", /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/29, /*login_bonus_id*/40001, /*count*/3, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107801, /*character_name*/"凱留", /*description*/"你還挺能幹的嘛！\n我有點…還蠻…非、非常\n對你刮目相看了呢。\n我就誇誇你吧♪", /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/30, /*login_bonus_id*/40001, /*count*/4, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107701, /*character_name*/"鈴莓", /*description*/"對您和我來說，\n今天是個特別的日子。\n因為有了您的支持，\n我才能夠如此努力！", /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/31, /*login_bonus_id*/40001, /*count*/5, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107901, /*character_name*/"珠希", /*description*/"今天對你來說是個\n特別的紀念日呢喵。\n對於你的活躍，我總是\n以貓的方式在感謝你喵～♪", /*voice_id*/1, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/32, /*login_bonus_id*/40001, /*count*/6, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107501, /*character_name*/"貪吃佩可", /*description*/"嗚～吃了太多的刨冰，\n身體變得好冷哦…\n請用你的體溫溫暖我吧！\n抱緊──♪", /*voice_id*/2, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/33, /*login_bonus_id*/40001, /*count*/7, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107601, /*character_name*/"可可蘿", /*description*/"夏天總是充滿著許多的危險…\n但是在下會好好地\n保護著主人的哦。", /*voice_id*/2, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/34, /*login_bonus_id*/40001, /*count*/8, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107801, /*character_name*/"凱留", /*description*/"嗯～到底為什麼會和你\n一起度過了一整個夏天啊…\n雖然我很開心就是了！\n來吧，我們要繼續玩囉！", /*voice_id*/2, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/35, /*login_bonus_id*/40001, /*count*/9, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107701, /*character_name*/"鈴莓", /*description*/" 和您一起度過的第一個夏天…\n我現在就已經心跳不已了呢。\n快樂又美好的回憶，\n又增加一個了呢♪", /*voice_id*/2, /*bg_id*/0);
INSERT INTO `login_bonus_detail` VALUES (/*id*/36, /*login_bonus_id*/40001, /*count*/10, /*reward_type*/8, /*reward_id*/91002, /*reward_num*/150, /*character_id*/107901, /*character_name*/"珠希", /*description*/"明明夏天是最好賺錢的時候，\n但今年我卻想和你\n一起度過呢喵。你能和我一起\n創造愉快的回憶喵？", /*voice_id*/2, /*bg_id*/0);
