INSERT INTO `navi_comment` VALUES (/*comment_id*/7001, /*where_type*/7, /*character_id*/106001, /*face_type*/1, /*character_name*/"凱留", /*description*/"偶爾參加共鬥\n好像也不錯吼？\n我也助你一臂之力吧。", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/7002, /*where_type*/7, /*character_id*/105901, /*face_type*/1, /*character_name*/"可可蘿", /*description*/"似乎可以和許多人\n協力戰鬥呢。\n要是主人的話，\n想必能夠非常活躍吧。", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/7003, /*where_type*/7, /*character_id*/105801, /*face_type*/1, /*character_name*/"貪吃佩可", /*description*/"和許多人一起\n共同戰鬥！\n一起加油吧☆　", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10001, /*where_type*/10, /*character_id*/106001, /*face_type*/1, /*character_name*/"凱留", /*description*/"寶石要好好想清楚\n再使用哦！\n別亂花喔！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10002, /*where_type*/10, /*character_id*/105901, /*face_type*/1, /*character_name*/"可可蘿", /*description*/"用主人的寶石\n可以買到各種貴重的物品\n雖然如此，浪費還是\n『壞壞』呦。", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/10003, /*where_type*/10, /*character_id*/105801, /*face_type*/1, /*character_name*/"貪吃佩可", /*description*/"用寶石可以換食物…\n不對，是換貴重物品啦！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/12001, /*where_type*/12, /*character_id*/105801, /*face_type*/1, /*character_name*/"貪吃佩可", /*description*/"達到特定條件的話\n可以得到報酬喔！\n一起加油吧♪　", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-416.5, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13001, /*where_type*/13, /*character_id*/106001, /*face_type*/1, /*character_name*/"凱留", /*description*/"雖然經歷了各種事，\n回頭一看，\n感覺卻挺不賴吶？", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13002, /*where_type*/13, /*character_id*/105901, /*face_type*/1, /*character_name*/"可可蘿", /*description*/"和主人度過的快樂每一天……\n到這裡可以隨意\n想回想幾次就回想幾次♪　", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/13003, /*where_type*/13, /*character_id*/105801, /*face_type*/1, /*character_name*/"貪吃佩可", /*description*/"看到你有很多\n和女生共度的回憶哦！\n嗚呼嗚呼♪\n你很受歡迎呢☆", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14001, /*where_type*/14, /*character_id*/106001, /*face_type*/1, /*character_name*/"凱留", /*description*/"攻略冒險話，\n聽說就能得到道具\n能拿的東西，\n我們就要拿到！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14002, /*where_type*/14, /*character_id*/105901, /*face_type*/1, /*character_name*/"可可蘿", /*description*/"這是可以獲得培育\n所需的道具的冒險。\n為了主人，在下\n一定會變強的呦", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/14003, /*where_type*/14, /*character_id*/105801, /*face_type*/1, /*character_name*/"貪吃佩可", /*description*/"攻略這個冒險的話，\n可以得到道具哦\n蒐集許多道具，變得\n越來越強吧！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/15001, /*where_type*/15, /*character_id*/105901, /*face_type*/1, /*character_name*/"可可蘿", /*description*/"主人，做好行前準備\n一起完成充實的冒險吧。", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16001, /*where_type*/16, /*character_id*/106001, /*face_type*/1, /*character_name*/"凱留", /*description*/"似乎可以加入戰隊哦\n機會難得，加入看看吧？\n好像挺好玩的哦♪", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16002, /*where_type*/16, /*character_id*/105901, /*face_type*/1, /*character_name*/"可可蘿", /*description*/"在這裡可以加入戰隊，\n有很多厲害的戰隊，\n真令人目不暇給。", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/16003, /*where_type*/16, /*character_id*/105801, /*face_type*/1, /*character_name*/"貪吃佩可", /*description*/"要加入戰隊看看嗎？\n和大家一起冒險\n非常開心哦！", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/17002, /*where_type*/17, /*character_id*/105901, /*face_type*/1, /*character_name*/"可可蘿", /*description*/" 在這邊可以回顧\n與同伴一同參加戰隊競賽時\n大顯身手的樣子。", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/17003, /*where_type*/17, /*character_id*/105801, /*face_type*/1, /*character_name*/"貪吃佩可", /*description*/"可以在這邊看到\n至今擊敗的大魔王的資訊\n說不定可以成為之後戰鬥\n的參考呦☆", /*voice_id*/1, /*start_time*/"2015/12/17 15:00:00", /*end_time*/"2099/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/18001, /*where_type*/18, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"歡迎！\n在這裡提供主頁或\n公會小屋播放音樂的\n變更與購買服務喔。", /*voice_id*/1, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/18002, /*where_type*/18, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"BGM的變更與購買\n都可以在這裡進行喔\n我們為您準備了，\n很多動人的樂曲喔！", /*voice_id*/2, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/19001, /*where_type*/19, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"主頁或公會小屋的BGM\n都可以更改喔\n您喜歡的樂曲，\n也請讓我聽聽看吧。", /*voice_id*/3, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/19002, /*where_type*/19, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"在這裡，\n可以將BGM更改喔\n您要更改為什麼樂曲呢？\n呵呵呵，我也很期待呢♪", /*voice_id*/4, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/20001, /*where_type*/20, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"在這裡，\n可以讓您購買BGM喔，\n也可以讓您試聽，\n請仔細挑選吧。", /*voice_id*/5, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/20002, /*where_type*/20, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"歡迎光臨！\n請到這裡購買BGM\n有很多優秀的樂曲，\n讓人挑得目不暇給呢。", /*voice_id*/6, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-470.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/21001, /*where_type*/21, /*character_id*/106001, /*face_type*/1, /*character_name*/"凱留", /*description*/"在這裡好像可以用取得的道具\n來製作專用裝備呢\n如果收集到了道具\n就立刻來試做吧！", /*voice_id*/1, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/21002, /*where_type*/21, /*character_id*/105901, /*face_type*/1, /*character_name*/"可可蘿", /*description*/"攻略時有可能\n獲得稀有的道具喔\n有了這類道具\n好像可以用來製作專用裝備。", /*voice_id*/1, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/21003, /*where_type*/21, /*character_id*/105801, /*face_type*/1, /*character_name*/"貪吃佩可", /*description*/"好像可以獲得\n製作專用裝備的必要道具\n多多收集、\n越裝就會越強大！", /*voice_id*/1, /*start_time*/"2019/03/06 16:00:00", /*end_time*/"2030/12/17 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/0.0, /*change_face_type*/1, /*event_id*/0);
