INSERT INTO `navi_comment` VALUES (/*comment_id*/55001, /*where_type*/55, /*character_id*/190801, /*face_type*/1, /*character_name*/"花凜", /*description*/"這裡是工會管理協會！\n如果您持有討伐證的話，\n就能夠交換獎勵哦", /*voice_id*/1, /*start_time*/"2018/08/24 16:00:00", /*end_time*/"2018/09/19 15:59:00", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/5.506, /*change_face_type*/2, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/55002, /*where_type*/55, /*character_id*/190801, /*face_type*/2, /*character_name*/"花凜", /*description*/"辛苦了！\n在這裡，能夠以討伐證\n來換取獎勵哦！", /*voice_id*/2, /*start_time*/"2018/08/24 16:00:00", /*end_time*/"2018/09/19 15:59:00", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/1.216, /*change_face_type*/1, /*event_id*/0);
INSERT INTO `navi_comment` VALUES (/*comment_id*/56001, /*where_type*/56, /*character_id*/190801, /*face_type*/2, /*character_name*/"花凜", /*description*/"辛苦了\n別忘記使用討伐證\n來進行交換哦！", /*voice_id*/3, /*start_time*/"2018/08/24 16:00:00", /*end_time*/"2018/09/19 15:59:00", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/1.539, /*change_face_type*/1, /*event_id*/0);
