INSERT INTO `room_item_detail` VALUES (/*room_item_id*/509, /*level*/1, /*item_detail*/"沒有靠背的藍色圓椅子。既簡單又可愛的設計，在廣泛的族群中都相當有人氣！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/510, /*level*/1, /*item_detail*/"通用性很高的吊棚。可以吊在許多地方相當的方便！　也很適合用在想為牆壁增添色彩時！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/511, /*level*/1, /*item_detail*/"散發著上流感的白色桌子。在這張桌子上放上一封信的話也許會有種很富有的氣氛也說不定…！？　另外還附有藍色的桌巾相當的划算！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/512, /*level*/1, /*item_detail*/"讓人想起夏天的白色桌子。皇家的藍色桌巾與裝飾於中央的花，就讓今天成為特別的一天吧！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/513, /*level*/1, /*item_detail*/"用色使人聯想到度假勝地的牆壁。窺視窗外的話，會讓人有種彷彿能看見海的錯覺，真是不可思議！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/514, /*level*/1, /*item_detail*/"顯眼的藍色地板。相當適合搭配上冷色系的裝飾品！　外觀看起來也相當涼爽，因此特別推薦設置於炎熱的時候！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/601, /*level*/1, /*item_detail*/"既鮮豔又美麗的水槽。即使只是靜靜地看著，心情也能夠平穩下來！　是件治癒效果相當拔群的家具！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/602, /*level*/1, /*item_detail*/"柔軟的彈力是其魅力的海龍娃娃。不只是小孩，不論是女性或男性，在男女老幼間都很有人氣！　既可以當作裝飾品也可以當作舒緩心情的道具，是個相當多功能的娃娃！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/603, /*level*/1, /*item_detail*/"能夠體會到人魚氛圍的貝殼坐墊。魚兒們朝氣蓬勃地游泳的樣子彷彿在眼前浮現！　坐上去後也許能成為海中的王子殿下哦！？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/604, /*level*/1, /*item_detail*/"有一點冰冰的凳子。能夠給予你未曾體會過的坐感！　這股魅力只有坐過的人才懂！　還能提供你最適宜的涼爽感哦！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/605, /*level*/1, /*item_detail*/"彷彿剪下海的一部份的隔板。既有水族箱功能，又不失分隔的功能，可謂是裝飾品中的優良品！　佈滿於房間內的話彷彿置身海洋中！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/606, /*level*/1, /*item_detail*/"美麗的珊瑚擺設物。鮮豔的顏色，最適合當作裝飾品！　想讓房間成為海洋風的話首選就是這個！　光是放著就似乎能體會到當魚的感覺！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/607, /*level*/1, /*item_detail*/"能讓許多地方的美麗凸顯出來的魚兒貼紙。貼在房間內的話，魚兒天國就完成了！　對愛魚的你來說是個必需品！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/608, /*level*/1, /*item_detail*/"神秘氛圍的背景。睜開雙眼後彷彿置身於海之國中！？　招待朋友來的話定能讓他們又驚又喜！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/609, /*level*/1, /*item_detail*/"展現出非日常的海底牆壁。只要有了這個就一定能夠享受到海底散步！　相當適合搭配上熱帶魚的貼紙！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/610, /*level*/1, /*item_detail*/"能夠充分享受海底空間的地板。能夠讓你的房間，搖身一變出現遠離日常的景色！　歡迎來到海底世界！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/611, /*level*/1, /*item_detail*/"能夠體會到人魚氛圍的貝殼坐墊。魚兒們優雅地游泳的樣子彷彿在眼前浮現！　坐上去後也許能成為海中的公主殿下哦！？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/612, /*level*/1, /*item_detail*/"蘭德索爾白米職人所監製，特別訂製的飯糰布偶裝。不僅僅是講究細節而已，那每一粒的米裡面都藏著職人誠意。同時也對實用性相當講究，無人能及的極致穿著感受，正代表了飯糰本身的意義。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/701, /*level*/1, /*item_detail*/"【王宮騎士團（NIGHTMARE）】修練時所使用的大砲。不僅外觀與實物一模一樣，大砲還會發射可愛的星星來代替砲彈。應該也很適合拿來當作派對的整人道具！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/702, /*level*/1, /*item_detail*/"【王宮騎士團（NIGHTMARE）】御用的木人樁。原本是見習騎士修練時使用的道具，但現在一般家庭也拿來當作運動器材，相當受到矚目，相信將來會成為一個流行商品。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/703, /*level*/1, /*item_detail*/"獅子噴泉以牠銳利的眼神迎接訪客。湍急的水流帶來的不是療癒效果，而是一種緊張感。據說只要背向水池將 1 盧幣投入噴泉，願望就能實現。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/704, /*level*/1, /*item_detail*/"純擺放在王宮中的備用鎧甲。光只是擺在那裡，就會產生一股不尋常的威嚴。鎧甲上一點灰塵也沒有，由此可知鎧甲有定時保養。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/705, /*level*/1, /*item_detail*/"茉莉修練時使用的備用盾牌。它是以輕盈的材質所製成，即使是小孩都能輕鬆使用。盾牌上處處可見經過嚴酷的修練所留下的傷痕。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/706, /*level*/1, /*item_detail*/"上樓用的宮廷風樓梯。由大陸首屈一指的職人設計，打造得極為美麗。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/707, /*level*/1, /*item_detail*/"下樓用的宮廷風樓梯。被擦拭得亮晶晶的樓梯，請留意不要滑倒！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/708, /*level*/1, /*item_detail*/"和王宮中的掛毯一樣，由特殊布料所織成的高雅掛毯。鮮紅的色澤，更添加了房間的高貴及風情。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/709, /*level*/1, /*item_detail*/"王宮中常備的壁燈。間接照明是時尚空間必備！　使用後房間品味立刻升級，你也試試看吧？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/710, /*level*/1, /*item_detail*/"高雅獨具的宮廷風牆壁。以純白為底，整體散發出一股高貴的氣息。連窗外吹進來的風，似乎都有那麼一點不同。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/711, /*level*/1, /*item_detail*/"整潔的宮廷風地板。走在上面會發出清脆的聲響，因此也會營造出高格調的悅耳聲響。", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/801, /*level*/1, /*item_detail*/"充滿各式機關的萬聖節快樂籃，不知究竟會發生甚麼事呢？有了這個，一定天天都開心！　大家一起喊Trick or treat！", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
INSERT INTO `room_item_detail` VALUES (/*room_item_id*/802, /*level*/1, /*item_detail*/"讓人興奮不已的萬聖節餐桌。要完糖果後就圍著這張桌子舉辦派對吧！　說不定能和快樂的怪物們變成好朋友喔！？", /*lvup_trigger_type*/0, /*lvup_trigger_id*/0, /*lvup_trigger_value*/0, /*lvup_item1_type*/0, /*lvup_item1_id*/0, /*lvup_item1_num*/0, /*lvup_time*/0);
