INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130011, /*equipment_name*/"喵王腰帶", /*description*/"日和配戴的腰帶。\n有著猛獸睥睨眼神的扣環，\n能鼓動鬥爭本能，\n令拳頭更有勁道。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/94.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/20.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130021, /*equipment_name*/"繁花之願", /*description*/"優衣配戴的髮箍。\n造型可愛的花蕾絲與裝飾，\n讓守護夥伴的治療之力\n更為提升。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/0.0, /*atk*/0.0, /*magic_str*/46.0, /*def*/5.0, /*magic_def*/5.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/5.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130031, /*equipment_name*/"雄風手套", /*description*/"怜愛用的手套。這副攻守兼備的手套\n所打出的正拳突刺，\n不論對上什麼敵人\n都能夠打倒。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/110.0, /*atk*/95.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130211, /*equipment_name*/"善舉之鈴", /*description*/"胡桃當作護身符的鈴。\n從莊嚴雅正的外觀\n到奏出的可愛音調，\n都能令怯懦的心\n生出勇氣。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/100.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/8.0, /*magic_def*/8.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/200.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
