INSERT INTO `unique_equipment_data` VALUES (/*equipment_id*/130291, /*equipment_name*/"傑出揚聲器", /*description*/"小望專用的手持麥克風。在可愛的緞帶中央\n畫有【慈樂之音】\n的標誌。拿到手上\n就能向頂級偶像邁進。", /*promotion_level*/98, /*craft_flg*/1, /*equipment_enhance_point*/0, /*sale_price*/0, /*require_level*/1, /*hp*/300.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/6.0, /*magic_def*/6.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/1.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*enable_donation*/0, /*accuracy*/0.0);
