INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130011, /*equipment_name*/"喵王腰帶", /*description*/"日和配戴的腰帶。\n有著猛獸睥睨眼神的扣環，\n能鼓動鬥爭本能，\n令拳頭更有勁道。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130021, /*equipment_name*/"繁花之願", /*description*/"優衣配戴的髮箍。\n造型可愛的花蕾絲與裝飾，\n讓守護夥伴的治療之力\n更為提升。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.3, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130031, /*equipment_name*/"雄風手套", /*description*/"怜愛用的手套。這副攻守兼備的手套\n所打出的正拳突刺，\n不論對上什麼敵人\n都能夠打倒。", /*promotion_level*/98, /*hp*/5.5, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130061, /*equipment_name*/"惡魔三叉戟", /*description*/"通體注滿茜里之愛的魔槍。\n藏在武器裡的強大魔力，\n能讓小惡魔變成煽動的魔女，\n並華麗地變身。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.6, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130091, /*equipment_name*/"螺旋靈擊疾風飛劍", /*description*/"由疾風之冥姬操縱的魔裝。\n若是凡夫俗子，碰到一瞬間\n就會走火入魔成為廢人。\n不祥的闇黑色刀刃泛起光彩的剎那，\n黑暗將化為疾風，將愚者引入黃泉。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.5, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130111, /*equipment_name*/"尖鋒箭", /*description*/"蘊含熾烈的火焰、是璃乃專用的魔法箭。\n熱情的意念就是火焰的源頭，\n射出的一擊，\n具有決定戰果的強大威力。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.6, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130211, /*equipment_name*/"善舉之鈴", /*description*/"胡桃當作護身符的鈴。\n從莊嚴雅正的外觀\n到奏出的可愛音調，\n都能讓怯懦的心\n生出勇氣。", /*promotion_level*/98, /*hp*/5.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/10.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130221, /*equipment_name*/"惡魔標槍", /*description*/"為了讓依里的魔力增幅\n而經過特化的魔槍。以生命力換取得來的魔力，\n具備一揮動即可\n讓周圍化為灰燼的威力。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130271, /*equipment_name*/"無情", /*description*/"將過盛的情感轉換為破壞力\n是用惠理子最狂烈的愛所鍊成的靈斧。\n每一閃都會奏起破滅的狂想曲。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130281, /*equipment_name*/"榮耀之羽", /*description*/"咲戀愛用的髮飾。\n溫柔之中蘊含的強大正是其象徵。\n當翅膀振動時，\n能令身體從重力解放。", /*promotion_level*/98, /*hp*/1.25, /*atk*/5.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130291, /*equipment_name*/"傑出揚聲器", /*description*/"小望專用的手持麥克風。在可愛的緞帶中央\n畫有【慈樂之音】\n的標誌。拿到手上\n就能向頂級偶像邁進。", /*promotion_level*/98, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.3, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.05, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130301, /*equipment_name*/"紅蓮爆炎扇", /*description*/"妮諾愛用的軍配扇。\n染成紅色的軍配扇\n所捲起的風會變成爆焰，\n在阻擋著的一切被燒盡之前，\n都不會熄滅。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130311, /*equipment_name*/"凜魂之袍", /*description*/"青藍色的靈力火焰籠罩、\n是忍愛用的靈衣。\n以靈體附著的特殊線材紡織而成，\n可以得到眾靈體的協助。", /*promotion_level*/98, /*hp*/12.5, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130321, /*equipment_name*/"紅輝石紫藤", /*description*/"裝飾秋乃的胸針。\n紫藤廬家代代相傳的寶石，\n賦予持有者高潔的精神，\n以及無與倫比的魔力。", /*promotion_level*/98, /*hp*/7.5, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130341, /*equipment_name*/"永恆之盅", /*description*/"優花梨永不離手的酒盅。再怎麼喝都會湧出的\n生命之水，\n能滲透持有者的身體，賦予身體\n不停湧現的活力。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.6, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130461, /*equipment_name*/"魅影鯛魚燒", /*description*/"珠希所重視的首飾。\n鯛魚燒形的護符所包覆的魔石，\n能讓使用者獲得\n快到只能遺留幻影的身法。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.25, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130471, /*equipment_name*/"騎士鎖甲", /*description*/"被正義的烈火包覆的純的鎧甲。\n賦予的魔力能讓\n敵人的任何攻擊都無法靠近，\n給予同伴們勇氣與活力。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130481, /*equipment_name*/"溟龍槍利維坦", /*description*/"美冬愛用的長鉞。\n一揮動就能讓槍尖湧出水花\n讓持有者憑著水力的加持\n而能流利地防守。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130491, /*equipment_name*/"神聖之刃", /*description*/"靜流愛用的十字劍。\n當有希望幫助弱者的高貴靈魂\n寄宿在劍上，將發動防禦的術式\n以保護目標。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130511, /*equipment_name*/"詛咒薔薇", /*description*/"以妖豔的薔薇為裝飾，\n是深月愛用的眼帶。\n灌入魔力後將散發七色光華，\n讓對手的能力減退。", /*promotion_level*/98, /*hp*/5.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130581, /*equipment_name*/"公主之劍", /*description*/"貪吃佩可所使用『皇家裝備』\n真正的樣貌。原本已經足夠強大，\n但若解放了那沉睡之力，\n將會賦予更強的力量。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.5, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130591, /*equipment_name*/"愛梅斯護符", /*description*/"可可蘿配戴的花飾。\n受到被祀奉為神的偉大存在之加持，\n花中飄出的溫和香氣，\n可以賦予戰鬥的勇氣。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.5, /*magic_str*/0.0, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130601, /*equipment_name*/"混沌魔典", /*description*/"凱留攜帶的魔導書。\n只要控制書中滿載的浩大魔力，\n就能自由操控所記載的\n強大術式。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.7, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130631, /*equipment_name*/"泰坦之后", /*description*/"亞里莎愛用的大弓。妖精之間傳頌的傳說之弓，\n只要舉起來\n就能停止目標的動作，\n射出的箭能夠貫穿任何物質。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.8, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
