INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130011, /*equipment_name*/"喵王腰帶", /*description*/"日和配戴的腰帶。\n有著猛獸睥睨眼神的扣環，\n能鼓動鬥爭本能，\n令拳頭更有勁道。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130021, /*equipment_name*/"繁花之願", /*description*/"優衣配戴的髮箍。\n造型可愛的花蕾絲與裝飾，\n讓守護夥伴的治療之力\n更為提升。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.3, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130031, /*equipment_name*/"雄風手套", /*description*/"怜愛用的手套。這副攻守兼備的手套\n所打出的正拳突刺，\n不論對上什麼敵人\n都能夠打倒。", /*promotion_level*/98, /*hp*/5.5, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130041, /*equipment_name*/"驚嚇箱", /*description*/"禊充滿自信的最高傑作驚嚇箱。\n別以為只是小孩的玩具而輕視，\n外觀的驚嚇度與衝擊性的音效，\n肯定能夠嚇破敵人的膽子。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.35, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.1, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130051, /*equipment_name*/"正義勳章", /*description*/"茉莉所珍惜的騎士團致贈\n勳章。每當看到閃亮的勳章\n茉莉的正義之心就會強化，\n能夠對抗任何邪惡。", /*promotion_level*/98, /*hp*/6.5, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130061, /*equipment_name*/"惡魔三叉戟", /*description*/"通體注滿茜里之愛的魔槍。\n藏在武器裡的強大魔力，\n能讓小惡魔變成煽動的魔女，\n並華麗地變身。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.6, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130071, /*equipment_name*/"幽靈香甜鬼魂布丁", /*description*/"將宮子的最愛大型化，連每樣\n素材都仔細講究的究極布丁。\n那已達至高領域的超級美味，\n入口時感覺身體都要變透明了。", /*promotion_level*/98, /*hp*/11.5, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.15, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130081, /*equipment_name*/"閃亮隨身鏡", /*description*/"小雪愛用的隨身鏡。\n炫目的裝飾能引出小雪的\n光輝，能夠對全人類造成\n巨大的影響。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/3.15, /*def*/0.2, /*magic_def*/0.2, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130091, /*equipment_name*/"螺旋靈擊疾風飛劍", /*description*/"由疾風之冥姬操縱的魔裝。\n若是凡夫俗子，碰到一瞬間\n就會走火入魔成為廢人。\n不祥的闇黑色刀刃泛起光彩的剎那，\n黑暗將化為疾風，將愚者引入黃泉。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.5, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130111, /*equipment_name*/"尖鋒箭", /*description*/"蘊含熾烈的火焰、是璃乃專用的魔法箭。\n熱情的意念就是火焰的源頭，\n射出的一擊，\n具有決定戰果的強大威力。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.6, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130121, /*equipment_name*/"流星☆緞帶", /*description*/"栞送給初音的妹妹手工製\n緞帶。灌入閃亮星的栞的\n思念，會呼應姊姊的魔力\n令敵人的魔法防禦力下降。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.4, /*def*/0.05, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130151, /*equipment_name*/"神愛罩紗", /*description*/"美里穿戴的罩紗。\n清爽的罩紗帶出美里的\n慈愛，能令眾人提振精神，\n提昇魔法暴擊傷害。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/2.55, /*def*/0.15, /*magic_def*/0.35, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.15, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130171, /*equipment_name*/"球陽唐手秘傳之書", /*description*/"記載有香織所使用的琉球空手道\n奧義的卷軸。經過特殊的術法，\n只對高手的氣脈有所反應隨而\n浮現文字。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.7, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130181, /*equipment_name*/"魅力長鞭", /*description*/"由伊緒作為教鞭所使用的長鞭。\n無論多麼乏味的課題，\n只要經過伊緒的教導，\n就能轉變為一段開心的時光。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130201, /*equipment_name*/"兔子先生之刃", /*description*/"美美愛用的劍。\n若沉溺於美美揮舞那可愛的劍\n時，那與外表不符的攻擊範圍\n將會讓敵人嘗到苦頭。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.4, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130211, /*equipment_name*/"善舉之鈴", /*description*/"胡桃當作護身符的鈴。\n從莊嚴雅正的外觀\n到奏出的可愛音調，\n都能讓怯懦的心\n生出勇氣。", /*promotion_level*/98, /*hp*/5.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.4, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/10.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130221, /*equipment_name*/"惡魔標槍", /*description*/"為了讓依里的魔力增幅\n而經過特化的魔槍。以生命力換取得來的魔力，\n具備一揮動即可\n讓周圍化為灰燼的威力。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.25, /*def*/0.0, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130231, /*equipment_name*/"偉大的熊王噗吉", /*description*/"不用說也知道是綾音的夥伴，\n提升力量後的姿態。獲得\n絕大力量的噗吉的攻擊\n能令敵人長時間失去意識。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.5, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.2, /*physical_critical*/0.6, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130251, /*equipment_name*/"皇家女僕裝", /*description*/"能體現侍奉心意的圍裙裝。\n圍裙所散發對咲戀的忠誠心，\n能提高鈴莓的魔力，\n令敵人的物理防禦力下降。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.8, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130261, /*equipment_name*/"寶氣圓栗", /*description*/"以蘊含龐大魔力的傳說之栗\n加裝飾而成的首飾。對小鈴\n放出的魔法增幅，對周圍的\n夥伴提供巨大的力量。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.5, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130271, /*equipment_name*/"無情", /*description*/"將過盛的情感轉換為破壞力\n是用惠理子最狂烈的愛所鍊成的靈斧。\n每一閃都會奏起破滅的狂想曲。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130281, /*equipment_name*/"榮耀之羽", /*description*/"咲戀愛用的髮飾。\n溫柔之中蘊含的強大正是其象徵。\n當翅膀振動時，\n能令身體從重力解放。", /*promotion_level*/98, /*hp*/1.25, /*atk*/5.25, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130291, /*equipment_name*/"傑出揚聲器", /*description*/"小望專用的手持麥克風。在可愛的緞帶中央\n畫有【慈樂之音】\n的標誌。拿到手上\n就能向頂級偶像邁進。", /*promotion_level*/98, /*hp*/15.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.3, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.05, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130301, /*equipment_name*/"紅蓮爆炎扇", /*description*/"妮諾愛用的軍配扇。\n染成紅色的軍配扇\n所捲起的風會變成爆焰，\n在阻擋著的一切被燒盡之前，\n都不會熄滅。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.75, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130311, /*equipment_name*/"凜魂之袍", /*description*/"青藍色的靈力火焰籠罩、\n是忍愛用的靈衣。\n以靈體附著的特殊線材紡織而成，\n可以得到眾靈體的協助。", /*promotion_level*/98, /*hp*/12.5, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130321, /*equipment_name*/"紅輝石紫藤", /*description*/"裝飾秋乃的胸針。\n紫藤廬家代代相傳的寶石，\n賦予持有者高潔的精神，\n以及無與倫比的魔力。", /*promotion_level*/98, /*hp*/7.5, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.1, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130331, /*equipment_name*/"伊麗莎白罩袍", /*description*/"以愛牛・伊麗莎白為形象\n設計的真陽的罩袍。\n穿上後裝傻之力倍增，\n能令對手陷入混亂。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.25, /*magic_str*/0.0, /*def*/0.05, /*magic_def*/0.05, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130341, /*equipment_name*/"永恆之盅", /*description*/"優花梨永不離手的酒盅。再怎麼喝都會湧出的\n生命之水，\n能滲透持有者的身體，賦予身體\n不停湧現的活力。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.6, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130361, /*equipment_name*/"宇宙之藍法杖", /*description*/"鏡華愛用的法杖。寶珠裡寄宿\n著高階的水龍之力，傳聞中會\n根據使用者的不同而改變武器\n的型態。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.4, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.9, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130371, /*equipment_name*/"天劍白嵐", /*description*/"御久間流所傳承的寶劍。\n如羽般輕盈的劍能呼應\n智的技巧，颳起將多數\n敵人掃除的旋風。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130381, /*equipment_name*/"初音手工製守護石", /*description*/"由初音送給栞的姊姊手工製\n守護石。貫注了初音的思念\n給予妹妹勇氣，能發揮出\n超越平常時的力量。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.15, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130401, /*equipment_name*/"BB團羈絆貝雷帽", /*description*/"碧穿戴的貝雷帽。\n一雙成對的翅膀是堅實羈絆的\n象徵。將羈絆置於胸口，令碧\n的戰意堅強，貫穿敵人。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.0, /*magic_str*/0.0, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130431, /*equipment_name*/"青狼之刃", /*description*/"有青狼魂魄寄宿的大劍。\n此劍能顯出使用者的膽魄，\n風捲殘雲般襲向敵人，\n同時還能喚醒沉眠的力量。", /*promotion_level*/98, /*hp*/0.0, /*atk*/5.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.6, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130441, /*equipment_name*/"闇斧夜之牙", /*description*/"經年累月陪伴著伊莉亞的斧頭。\n散發出紅色魔力的巨刃，只需\n一揮便能殲滅敵人，同時吸盡\n飛散的鮮血並化為主人的糧食。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.0, /*def*/0.0, /*magic_def*/0.2, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.05, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130451, /*equipment_name*/"愉悅頸環", /*description*/"空花所穿戴的可兼做拘束器\n的頸環。藉由封鎖自己動作\n而引誘敵人，貪求不為人知\n的快感。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.35, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.6, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130461, /*equipment_name*/"魅影鯛魚燒", /*description*/"珠希所重視的首飾。\n鯛魚燒形的護符所包覆的魔石，\n能讓使用者獲得\n快到只能遺留幻影的身法。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.25, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130471, /*equipment_name*/"騎士鎖甲", /*description*/"被正義的烈火包覆的純的鎧甲。\n賦予的魔力能讓\n敵人的任何攻擊都無法靠近，\n給予同伴們勇氣與活力。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.0, /*magic_str*/0.0, /*def*/0.4, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130481, /*equipment_name*/"溟龍槍利維坦", /*description*/"美冬愛用的長鉞。\n一揮動就能讓槍尖湧出水花\n讓持有者憑著水力的加持\n而能流利地防守。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.75, /*magic_str*/0.0, /*def*/0.15, /*magic_def*/0.15, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130491, /*equipment_name*/"神聖之刃", /*description*/"靜流愛用的十字劍。\n當有希望幫助弱者的高貴靈魂\n寄宿在劍上，將發動防禦的術式\n以保護目標。", /*promotion_level*/98, /*hp*/0.0, /*atk*/3.2, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130501, /*equipment_name*/"魔眼杖‧魔女之眼", /*description*/"美咲的武器兼夥伴的杖。\n雖然完成了各種進化，且是把\n有生命的魔杖，但悲哀的是，\n現在卻變成了化妝的實驗台。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.85, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.4, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130511, /*equipment_name*/"詛咒薔薇", /*description*/"以妖豔的薔薇為裝飾，\n是深月愛用的眼帶。\n灌入魔力後將散發七色光華，\n讓對手的能力減退。", /*promotion_level*/98, /*hp*/5.0, /*atk*/4.0, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.25, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.1, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130521, /*equipment_name*/"慾望洋裝", /*description*/"希望哪天能穿看看…！貫注了\n莉瑪的夢想特別訂製的長衫。\n做夢少女的妄想能力，\n此刻，立即覺醒。", /*promotion_level*/98, /*hp*/0.0, /*atk*/1.25, /*magic_str*/0.0, /*def*/0.45, /*magic_def*/0.35, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130531, /*equipment_name*/"白翼之服", /*description*/"莫妮卡穿著的大衣。\n其奢華的造型，令跟在\n背後的人身心亢奮，\n使全體攻擊力大幅提升。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.6, /*magic_str*/0.0, /*def*/0.3, /*magic_def*/0.2, /*physical_critical*/0.2, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130571, /*equipment_name*/"亞實基倫", /*description*/"追求劍道極峰的吉塔所獲得的\n單手劍。在寂滅的世界中\n能舞出自在的劍勢之人，\n將會被譽為劍聖。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.5, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/1.1, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.05, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130581, /*equipment_name*/"公主之劍", /*description*/"貪吃佩可所使用『皇家裝備』\n真正的樣貌。原本已經足夠強大，\n但若解放了那沉睡之力，\n將會賦予更強的力量。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/0.0, /*def*/0.5, /*magic_def*/0.45, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130591, /*equipment_name*/"愛梅斯護符", /*description*/"可可蘿配戴的花飾。\n受到被祀奉為神的偉大存在之加持，\n花中飄出的溫和香氣，\n可以賦予戰鬥的勇氣。", /*promotion_level*/98, /*hp*/0.0, /*atk*/2.5, /*magic_str*/0.0, /*def*/0.25, /*magic_def*/0.25, /*physical_critical*/0.0, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.25, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130601, /*equipment_name*/"混沌魔典", /*description*/"凱留攜帶的魔導書。\n只要控制書中滿載的浩大魔力，\n就能自由操控所記載的\n強大術式。", /*promotion_level*/98, /*hp*/0.0, /*atk*/0.0, /*magic_str*/4.7, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.0, /*magic_critical*/0.5, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
INSERT INTO `unique_equipment_enhance_rate` VALUES (/*equipment_id*/130631, /*equipment_name*/"泰坦之后", /*description*/"亞里莎愛用的大弓。妖精之間傳頌的傳說之弓，\n只要舉起來\n就能停止目標的動作，\n射出的箭能夠貫穿任何物質。", /*promotion_level*/98, /*hp*/0.0, /*atk*/4.75, /*magic_str*/0.0, /*def*/0.0, /*magic_def*/0.0, /*physical_critical*/0.8, /*magic_critical*/0.0, /*wave_hp_recovery*/0.0, /*wave_energy_recovery*/0.0, /*dodge*/0.0, /*physical_penetrate*/0.0, /*magic_penetrate*/0.0, /*life_steal*/0.0, /*hp_recovery_rate*/0.0, /*energy_recovery_rate*/0.0, /*energy_reduce_rate*/0.0, /*accuracy*/0.0);
