INSERT INTO `unit_comments` VALUES (/*id*/1008008, /*unit_id*/100831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"沒有我美什麼的\n也不是什麼丟臉的事啦。\n根本也沒必要說\n不是當然的嗎？那種事♪");
