INSERT INTO `unit_comments` VALUES (/*id*/1001003, /*unit_id*/100101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.5, /*change_face*/2, /*description*/"無論什麼時候元氣都是最重要的！\n所以累了的話得要好好休息唷？\n我嗎……還很有精神的呢。");
