INSERT INTO `unit_comments` VALUES (/*id*/1002001, /*unit_id*/100201, /*use_type*/1, /*voice_id*/1, /*face_id*/4, /*change_time*/6.0, /*change_face*/5, /*description*/"那、那個……\n讓我來當傾聽的對象，\n真的可以嗎？\n……這樣啊。謝謝……！");
