INSERT INTO `unit_comments` VALUES (/*id*/1008003, /*unit_id*/100801, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/7.0, /*change_face*/1, /*description*/"啊，你！　來得正好！\n我的美麗失控地\n停不下來！\n快阻止它！　不，別阻止！");
INSERT INTO `unit_comments` VALUES (/*id*/1008004, /*unit_id*/100801, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/4.5, /*change_face*/2, /*description*/"能跟這麼可愛的我\n在一起，要高興喔？\n因為你是，世界上\n最幸運的人♪");
INSERT INTO `unit_comments` VALUES (/*id*/1008005, /*unit_id*/100801, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/4.0, /*change_face*/2, /*description*/"我想跟你在一起\n所以才在這裡的呦。\n你並不只是所謂的\n配角，放心吧！");
INSERT INTO `unit_comments` VALUES (/*id*/1008006, /*unit_id*/100831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"不管怎樣的寶石還是寶物……\n都贏不過我的\n美與光芒……\n真是罪惡啊，我♪");
INSERT INTO `unit_comments` VALUES (/*id*/1008007, /*unit_id*/100831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"啊啊，我真是太美了♪\n如果要形容我的美……\n不，根本無法用言語來形容啦！");
