INSERT INTO `unit_comments` VALUES (/*id*/1016003, /*unit_id*/101601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"今天啊ー，在等等的活動上\n人家有份模特兒的工作ー\n如果方便的話就來看看吧！");
INSERT INTO `unit_comments` VALUES (/*id*/1016004, /*unit_id*/101601, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/7.0, /*change_face*/2, /*description*/"只要跟秀才聊天，總覺得\n被治癒……好溫暖～\n咦？　人家才沒說謊勒，真的真的！\n真心不騙啦～！");
