INSERT INTO `unit_data` VALUES (/*unit_id*/100301, /*unit_name*/"怜", /*kana*/"怜", /*prefab_id*/100301, /*rarity*/1, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/250, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin_1*/100301, /*cutin_2*/0, /*guild_id*/7, /*exskill_display*/0, /*comment*/"【物理】位於前衛，能使出單點突破來討伐敵方，是一名孤高的劍士。\n必殺技與一般技能，皆為了打倒眼前敵人而特化，\n並驅使攻擊技能和反擊技能來壓制前線。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100401, /*unit_name*/"禊", /*kana*/"禊", /*prefab_id*/100401, /*rarity*/1, /*motion_type*/1, /*se_type*/1, /*move_speed*/450, /*search_area_width*/205, /*atk_type*/1, /*normal_atk_cast_time*/2.17, /*cutin_1*/100401, /*cutin_2*/0, /*guild_id*/13, /*exskill_display*/0, /*comment*/"【物理】位於最前列，可擾亂敵人物理攻擊的愛惡作劇的女孩。\n能使眼前的敵人陷入黑暗狀態，讓敵人之命中率大幅下降。且還擁有能夠降低敵人全體物理攻擊力的技能。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
