INSERT INTO `unit_data` VALUES (/*unit_id*/100801, /*unit_name*/"雪", /*kana*/"雪", /*prefab_id*/100801, /*rarity*/2, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/805, /*atk_type*/2, /*normal_atk_cast_time*/2.07, /*cutin_1*/100801, /*cutin_2*/0, /*guild_id*/12, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，能輔助物理隊伍，是個活生生的藝術品。\n除了能提早發動己方必殺技的技能，還擁有能使敵人陷入黑暗狀態，藉此封鎖敵人物理攻擊的技能。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100901, /*unit_name*/"杏奈", /*kana*/"杏奈", /*prefab_id*/100901, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/440, /*atk_type*/2, /*normal_atk_cast_time*/2.25, /*cutin_1*/100901, /*cutin_2*/0, /*guild_id*/11, /*exskill_display*/0, /*comment*/"【魔法】可從中衛發動壓倒性的魔力來攻擊敵人，人稱之疾風之冥姬。\n施展必殺技能雖能給予敵軍全體大規模的傷害，\n然而卻因這份魔力過於強大，對自己也有相當的風險。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101001, /*unit_name*/"真步", /*kana*/"真步", /*prefab_id*/101001, /*rarity*/3, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/795, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin_1*/101001, /*cutin_2*/0, /*guild_id*/4, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，負責支援己方的童話少女。\n擁有可加強己方全體，恢復 ＴＰ 之必殺技，以及可恢復 HP ，使敵方陷入黑暗的技能，能使戰況好轉。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101101, /*unit_name*/"璃乃", /*kana*/"璃乃", /*prefab_id*/101101, /*rarity*/3, /*motion_type*/8, /*se_type*/8, /*move_speed*/450, /*search_area_width*/700, /*atk_type*/1, /*normal_atk_cast_time*/1.97, /*cutin_1*/101101, /*cutin_2*/0, /*guild_id*/6, /*exskill_display*/0, /*comment*/"【物理】能從後衛降下箭雨，是一名妹妹屬性的弓箭手。\n擁有能提高自己的暴擊率的一般技能，\n還能使傷害次數較高的必殺技，擁有更顯著的效果。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
