INSERT INTO `unit_data` VALUES (/*unit_id*/105701, /*unit_name*/"吉塔", /*kana*/"じーた", /*prefab_id*/105701, /*rarity*/3, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/245, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin_1*/105701, /*cutin_2*/0, /*guild_id*/4, /*exskill_display*/0, /*comment*/"【物理】位於前衛，不斷使出先下手為強的利害技能的騎空士。\n因為一般技能是能恢復 TP，使得必殺技在戰鬥的序盤之中\n就能使用，而能對敵方前衛造成劇烈的傷害。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105801, /*unit_name*/"貪吃佩可", /*kana*/"貪吃佩可", /*prefab_id*/105801, /*rarity*/1, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/155, /*atk_type*/1, /*normal_atk_cast_time*/2.25, /*cutin_1*/105801, /*cutin_2*/0, /*guild_id*/1, /*exskill_display*/0, /*comment*/"【物理】位於前衛，全力保護同伴，是一位貪吃的劍士。\n能夠增加 HP，也能恢復自己的 HP，\n擔任己方的保護牆的角色。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105901, /*unit_name*/"可可蘿", /*kana*/"可可蘿", /*prefab_id*/105901, /*rarity*/1, /*motion_type*/6, /*se_type*/6, /*move_speed*/450, /*search_area_width*/500, /*atk_type*/1, /*normal_atk_cast_time*/2.34, /*cutin_1*/105901, /*cutin_2*/0, /*guild_id*/1, /*exskill_display*/0, /*comment*/"【物理】位於中衛，無論是輔助還是攻擊皆能支援，是一位負責指引迷津的巫女。\n必殺技能增加己方全體的攻擊力，\n且有使自己恢復的功能，因此能持續支援。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/106001, /*unit_name*/"凱留", /*kana*/"凱留", /*prefab_id*/106001, /*rarity*/1, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/750, /*atk_type*/2, /*normal_atk_cast_time*/2.07, /*cutin_1*/106001, /*cutin_2*/0, /*guild_id*/1, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，能施以強力的全體攻擊，是一隻頑強的貓娘。\n技能可以使敵人的物理、魔法防禦力雙雙減弱，\n對於綜合編成隊伍的戰鬥中有著奇效。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
