INSERT INTO `unit_data` VALUES (/*unit_id*/100101, /*unit_name*/"日和", /*kana*/"日和", /*prefab_id*/100101, /*rarity*/1, /*motion_type*/1, /*se_type*/1, /*move_speed*/450, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.295, /*cutin_1*/100101, /*cutin_2*/0, /*guild_id*/7, /*exskill_display*/0, /*comment*/"【物理】位於前衛，壓制敵方前線，精神百倍並帶著笑容的女孩。\n擁有能大幅度攻擊前衛的必殺技，以及能加強自身攻擊力的技能的攻擊者。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100201, /*unit_name*/"優衣", /*kana*/"優衣", /*prefab_id*/100201, /*rarity*/1, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/800, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin_1*/100201, /*cutin_2*/0, /*guild_id*/7, /*exskill_display*/0, /*comment*/"【魔法】位於最後列，恢復及輔助己方的魔法師。\n打擊力量微弱，但擁有高度魔法攻擊力，當 Rank 上升，其攻擊力及輔助能力將會取得平衡。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100301, /*unit_name*/"怜", /*kana*/"怜", /*prefab_id*/100301, /*rarity*/1, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/250, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin_1*/100301, /*cutin_2*/0, /*guild_id*/7, /*exskill_display*/0, /*comment*/"【物理】位於前衛，能使出單點突破來討伐敵方，是一名孤高的劍士。\n必殺技與一般技能，皆為了打倒眼前敵人而特化，\n並驅使攻擊技能和反擊技能來壓制前線。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100401, /*unit_name*/"禊", /*kana*/"禊", /*prefab_id*/100401, /*rarity*/1, /*motion_type*/1, /*se_type*/1, /*move_speed*/450, /*search_area_width*/205, /*atk_type*/1, /*normal_atk_cast_time*/2.17, /*cutin_1*/100401, /*cutin_2*/0, /*guild_id*/13, /*exskill_display*/0, /*comment*/"【物理】在最前線干擾敵人物理攻擊的淘氣少女。\n擁有能使眼前的敵人陷入黑暗狀態、命中率大幅下降之外，\n還能使一名敵人的物理攻擊力下降的技能。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100501, /*unit_name*/"茉莉", /*kana*/"茉莉", /*prefab_id*/100501, /*rarity*/2, /*motion_type*/2, /*se_type*/2, /*move_speed*/450, /*search_area_width*/185, /*atk_type*/1, /*normal_atk_cast_time*/2.125, /*cutin_1*/100501, /*cutin_2*/0, /*guild_id*/2, /*exskill_display*/0, /*comment*/"【物理】以機敏的行動在敵陣穿梭的先鋒見習騎士。\n以必殺技衝進對手陣地中央，\n再攻擊自己周圍的技能來進攻對手。", /*only_disp_owned*/0, /*start_time*/"2019/01/22 16:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100601, /*unit_name*/"茜里", /*kana*/"茜里", /*prefab_id*/100601, /*rarity*/2, /*motion_type*/6, /*se_type*/6, /*move_speed*/450, /*search_area_width*/570, /*atk_type*/2, /*normal_atk_cast_time*/2.19, /*cutin_1*/100601, /*cutin_2*/0, /*guild_id*/14, /*exskill_display*/0, /*comment*/"【魔法】位於中衛，擔任魔法隊中強化與恢復的角色，是雙胞胎中的妹妹。\n擁有能大幅降低敵人魔法防禦力的技能，以及可提升魔法攻擊力及擁有恢復效果的必殺技。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100701, /*unit_name*/"宮子", /*kana*/"宮子", /*prefab_id*/100701, /*rarity*/2, /*motion_type*/3, /*se_type*/3, /*move_speed*/450, /*search_area_width*/125, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin_1*/100701, /*cutin_2*/0, /*guild_id*/14, /*exskill_display*/0, /*comment*/"【物理】在最前線專門閃避敵人攻擊的幽靈少女。\n以技能變身成為幽靈來躲避敵人攻擊，\n必殺技則是把敵人變成布丁吃掉。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100801, /*unit_name*/"雪", /*kana*/"雪", /*prefab_id*/100801, /*rarity*/2, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/805, /*atk_type*/2, /*normal_atk_cast_time*/2.07, /*cutin_1*/100801, /*cutin_2*/0, /*guild_id*/12, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，能輔助物理隊伍，是個活生生的藝術品。\n除了能提早發動己方必殺技的技能，還擁有能使敵人陷入黑暗狀態，藉此封鎖敵人物理攻擊的技能。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/100901, /*unit_name*/"杏奈", /*kana*/"杏奈", /*prefab_id*/100901, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/440, /*atk_type*/2, /*normal_atk_cast_time*/2.25, /*cutin_1*/100901, /*cutin_2*/0, /*guild_id*/11, /*exskill_display*/0, /*comment*/"【魔法】可從中衛發動壓倒性的魔力來攻擊敵人，人稱之疾風之冥姬。\n施展必殺技能雖能給予敵軍全體大規模的傷害，\n然而卻因這份魔力過於強大，對自己也有相當的風險。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101001, /*unit_name*/"真步", /*kana*/"真步", /*prefab_id*/101001, /*rarity*/3, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/795, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin_1*/101001, /*cutin_2*/0, /*guild_id*/4, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，負責支援己方的童話少女。\n擁有可加強己方全體，恢復 ＴＰ 之必殺技，以及可恢復 HP ，使敵方陷入黑暗的技能，能使戰況好轉。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101101, /*unit_name*/"璃乃", /*kana*/"璃乃", /*prefab_id*/101101, /*rarity*/3, /*motion_type*/8, /*se_type*/8, /*move_speed*/450, /*search_area_width*/700, /*atk_type*/1, /*normal_atk_cast_time*/1.97, /*cutin_1*/101101, /*cutin_2*/0, /*guild_id*/6, /*exskill_display*/0, /*comment*/"【物理】能從後衛降下箭雨，是一名妹妹屬性的弓箭手。\n擁有能提高自己的暴擊率的一般技能，\n還能使傷害次數較高的必殺技，擁有更顯著的效果。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101201, /*unit_name*/"初音", /*kana*/"初音", /*prefab_id*/101201, /*rarity*/3, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/755, /*atk_type*/2, /*normal_atk_cast_time*/2.07, /*cutin_1*/101201, /*cutin_2*/0, /*guild_id*/9, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，擅長對付物理攻擊型敵軍，\n是一位超能力少女。\n因為能對物理攻擊型的敵軍發揮真正的價值，\n因此可將物理攻擊型的敵軍給一網打盡。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101301, /*unit_name*/"七七香", /*kana*/"七七香", /*prefab_id*/101301, /*rarity*/2, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/740, /*atk_type*/2, /*normal_atk_cast_time*/2.07, /*cutin_1*/101301, /*cutin_2*/0, /*guild_id*/9, /*exskill_display*/0, /*comment*/"【魔法】從後衛位置施放多采多姿的魔法，是一名魔法宅少女。\n針對體力最多的對象所使用的必殺技或技能，\n是會依敵人的 HP 狀況改變攻擊目標的高威力魔法攻擊。", /*only_disp_owned*/0, /*start_time*/"2019/03/27 16:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101501, /*unit_name*/"美里", /*kana*/"美里", /*prefab_id*/101501, /*rarity*/2, /*motion_type*/9, /*se_type*/7, /*move_speed*/450, /*search_area_width*/735, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin_1*/101501, /*cutin_2*/0, /*guild_id*/9, /*exskill_display*/0, /*comment*/"【魔法】位於後衛支援使用魔法的角色，是大家的老師。\n負責必殺技的 HP 恢復及提升魔法防禦，持續恢復前方角色 HP，大幅提升己方的持久度。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101601, /*unit_name*/"鈴奈", /*kana*/"鈴奈", /*prefab_id*/101601, /*rarity*/2, /*motion_type*/8, /*se_type*/8, /*move_speed*/450, /*search_area_width*/705, /*atk_type*/1, /*normal_atk_cast_time*/1.97, /*cutin_1*/101601, /*cutin_2*/0, /*guild_id*/15, /*exskill_display*/0, /*comment*/"【物理】會從後衛進行暴擊攻擊的超人氣模特兒。\n暴擊的傷害不僅能使傷害增倍，再加上還能靠技能提升攻擊力，使破壞力更甚。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101701, /*unit_name*/"香織", /*kana*/"香織", /*prefab_id*/101701, /*rarity*/2, /*motion_type*/1, /*se_type*/1, /*move_speed*/450, /*search_area_width*/145, /*atk_type*/1, /*normal_atk_cast_time*/2.17, /*cutin_1*/101701, /*cutin_2*/0, /*guild_id*/4, /*exskill_display*/0, /*comment*/"【物理】在最前線利用累積技能賺傷害的南國少女。\n一旦發動「精神統一」技能，香織的攻擊會隨次數\n對敵人累積效果，後續的攻擊會逐漸增加。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/101801, /*unit_name*/"伊緒", /*kana*/"伊緒", /*prefab_id*/101801, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/715, /*atk_type*/2, /*normal_atk_cast_time*/2.6, /*cutin_1*/101801, /*cutin_2*/0, /*guild_id*/15, /*exskill_display*/0, /*comment*/"【魔法】在後方誘惑全體敵人的性感老師。\n以奪取敵人ＴＰ技能使其必殺技延遲發動，\n或施放誘惑的必殺技，使戰場陷入大混亂。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/102001, /*unit_name*/"美美", /*kana*/"美美", /*prefab_id*/102001, /*rarity*/2, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/360, /*atk_type*/1, /*normal_atk_cast_time*/2.125, /*cutin_1*/102001, /*cutin_2*/0, /*guild_id*/13, /*exskill_display*/0, /*comment*/"【物理】位於中衛，崩毀敵方前衛，是一隻小兔兔女孩。\n必殺技為，在給予離自己最近的敵人大幅傷害後，\n能順便攻擊到該對象旁邊的敵軍，能有效早期攻略敵人的前衛。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/102101, /*unit_name*/"胡桃", /*kana*/"胡桃", /*prefab_id*/102101, /*rarity*/1, /*motion_type*/3, /*se_type*/3, /*move_speed*/450, /*search_area_width*/240, /*atk_type*/1, /*normal_atk_cast_time*/1.675, /*cutin_1*/102101, /*cutin_2*/0, /*guild_id*/3, /*exskill_display*/0, /*comment*/"【物理】位於前衛，能守護己方，是一名極度害羞的女孩子。\n技能能提高自己周圍的物理、魔法防禦力，\n並且能使接近的敵方暈眩難以靠近。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
