INSERT INTO `unit_data` VALUES (/*unit_id*/102301, /*unit_name*/"綾音", /*kana*/"綾音", /*prefab_id*/102301, /*rarity*/2, /*motion_type*/3, /*se_type*/3, /*move_speed*/450, /*search_area_width*/210, /*atk_type*/1, /*normal_atk_cast_time*/1.425, /*cutin_1*/102301, /*cutin_2*/0, /*guild_id*/3, /*exskill_display*/0, /*comment*/"【物理】位於前衛，能使戰場混亂，是一個熊孩子。\n在以必殺技將對手吹飛的同時，以暈眩及給予大幅度的傷害使敵方陣形大亂。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/102501, /*unit_name*/"鈴莓", /*kana*/"鈴莓", /*prefab_id*/102501, /*rarity*/1, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/720, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin_1*/102501, /*cutin_2*/0, /*guild_id*/3, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，同時擅長恢復擊攻擊的冒失女僕。\n可一邊恢復己方 HP 一邊提高物理攻擊力的技能，適合與負責攻擊的中衛、後衛搭配。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/102601, /*unit_name*/"鈴", /*kana*/"鈴", /*prefab_id*/102601, /*rarity*/2, /*motion_type*/6, /*se_type*/6, /*move_speed*/450, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.34, /*cutin_1*/102601, /*cutin_2*/0, /*guild_id*/3, /*exskill_display*/0, /*comment*/"【物理】中衛，能加強物理攻擊的怕麻煩的少女。\n能同時提升己方的物理攻擊力及魔法防禦，能同時支援隊伍的攻守兩方。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/102701, /*unit_name*/"惠理子", /*kana*/"惠理子", /*prefab_id*/102701, /*rarity*/2, /*motion_type*/3, /*se_type*/3, /*move_speed*/450, /*search_area_width*/230, /*atk_type*/1, /*normal_atk_cast_time*/1.425, /*cutin_1*/102701, /*cutin_2*/0, /*guild_id*/11, /*exskill_display*/0, /*comment*/"【物理】位於前衛，以毒技能粉碎敵人，通稱破壞者。\n擁有最頂級的攻擊力，用必殺技消滅敵人時，攻擊力會一次次的向上提升。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/102801, /*unit_name*/"咲戀", /*kana*/"咲戀", /*prefab_id*/102801, /*rarity*/3, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/430, /*atk_type*/1, /*normal_atk_cast_time*/2.09, /*cutin_1*/102801, /*cutin_2*/0, /*guild_id*/3, /*exskill_display*/0, /*comment*/"【物理】位於中衛，能瞬間將局勢翻盤，是大家的咲戀媽媽。\n有著 HP 越低，傷害力就越高的必殺技\n和可以恢復己方的技能，一口氣逆轉局勢。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/102901, /*unit_name*/"望", /*kana*/"望", /*prefab_id*/102901, /*rarity*/3, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/160, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin_1*/102901, /*cutin_2*/0, /*guild_id*/10, /*exskill_display*/0, /*comment*/"【物理】位於前衛，成為大家的防護的偶像望望。\n當她立於舞台上時，能夠大大提升己方的物理攻擊力，\n並且還可以一人擔下敵方的注意。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/103001, /*unit_name*/"妮諾", /*kana*/"妮諾", /*prefab_id*/103001, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/415, /*atk_type*/1, /*normal_atk_cast_time*/2.25, /*cutin_1*/103001, /*cutin_2*/0, /*guild_id*/12, /*exskill_display*/0, /*comment*/"【物理】位於中衛，能反覆操作強力的範圍攻擊，是擅用忍術的熟手。\n當範圍攻擊的技能將敵人擊敗時，可大幅地累積 TP，\n發動強力必殺技的頻率也隨之提高。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/103101, /*unit_name*/"忍", /*kana*/"忍", /*prefab_id*/103101, /*rarity*/2, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/365, /*atk_type*/1, /*normal_atk_cast_time*/2.25, /*cutin_1*/103101, /*cutin_2*/0, /*guild_id*/14, /*exskill_display*/0, /*comment*/"【物理】位於中衛，能召喚父親的骷髏頭戰鬥的通靈少女。\n召喚來的父親除了能和自己分開攻擊之外，\n還能夠替自己抵擋傷害。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/103201, /*unit_name*/"秋乃", /*kana*/"秋乃", /*prefab_id*/103201, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/180, /*atk_type*/1, /*normal_atk_cast_time*/2.125, /*cutin_1*/103201, /*cutin_2*/0, /*guild_id*/8, /*exskill_display*/0, /*comment*/"【物理】位於前衛，能大鬧攻擊，是一名千金小姐劍士。\n能夠在自身周遭創造出能使夥伴恢復的領域。而挺身佇立前線的無畏身影，更不負身為名家之名。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/103301, /*unit_name*/"真陽", /*kana*/"真陽", /*prefab_id*/103301, /*rarity*/2, /*motion_type*/6, /*se_type*/6, /*move_speed*/450, /*search_area_width*/395, /*atk_type*/1, /*normal_atk_cast_time*/2.34, /*cutin_1*/103301, /*cutin_2*/0, /*guild_id*/5, /*exskill_display*/0, /*comment*/"【物理】位於中衛，可擾亂敵人動作的牧場主人。\n能將敵方的前衛擊退，進而崩壞敵方陣形，逼迫其他敵人成為對象攻擊範圍。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/103401, /*unit_name*/"優花梨", /*kana*/"優花梨", /*prefab_id*/103401, /*rarity*/1, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/405, /*atk_type*/1, /*normal_atk_cast_time*/2.4, /*cutin_1*/103401, /*cutin_2*/0, /*guild_id*/8, /*exskill_display*/0, /*comment*/"【物理】位於中衛，能以屏障和恢復守護己方的曾經的聖戰士。\n能夠操作受傷的己方角色恢復 HP 與 TP 的技能，進而在你來我往的消耗戰中，給出相當關鍵的支援。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/103601, /*unit_name*/"鏡華", /*kana*/"鏡華", /*prefab_id*/103601, /*rarity*/3, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/810, /*atk_type*/2, /*normal_atk_cast_time*/2.07, /*cutin_1*/103601, /*cutin_2*/0, /*guild_id*/0, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，能施以強力的魔法攻擊，是一個小小優等生。\n藉由技能，能瞬間提高魔法攻擊力後施放「宇宙藍色閃光」，對單方敵人的威力卓越。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/103701, /*unit_name*/"智", /*kana*/"智", /*prefab_id*/103701, /*rarity*/3, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/220, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin_1*/103701, /*cutin_2*/0, /*guild_id*/2, /*exskill_display*/0, /*comment*/"【物理】在前線施展快速斬擊屠殺敵人的劍術少女。\n必殺技則是使自己的攻擊力和行動速度上昇，\n將其他攻擊技能的殲滅能力提高到極限來輾壓敵人。", /*only_disp_owned*/0, /*start_time*/"2019/01/09 16:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/103801, /*unit_name*/"栞", /*kana*/"栞", /*prefab_id*/103801, /*rarity*/2, /*motion_type*/8, /*se_type*/8, /*move_speed*/450, /*search_area_width*/710, /*atk_type*/1, /*normal_atk_cast_time*/1.97, /*cutin_1*/103801, /*cutin_2*/0, /*guild_id*/5, /*exskill_display*/0, /*comment*/"【物理】從最後方連續施展高火力技能的狙擊手。\n擁有造成傷害後接著恢復ＴＰ的技能，\n可以快速發動強力的必殺技。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104001, /*unit_name*/"碧", /*kana*/"碧", /*prefab_id*/104001, /*rarity*/1, /*motion_type*/8, /*se_type*/8, /*move_speed*/450, /*search_area_width*/785, /*atk_type*/1, /*normal_atk_cast_time*/1.97, /*cutin_1*/104001, /*cutin_2*/0, /*guild_id*/9, /*exskill_display*/0, /*comment*/"【物理】從最後方射出異常狀態之箭，孤獨的射手。\n透過和傷害共同施加的毒或麻痺等追加效果，\n就算是防御力高的敵人也能確實地削減其ＨＰ。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104201, /*unit_name*/"千歌", /*kana*/"千歌", /*prefab_id*/104201, /*rarity*/2, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/790, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin_1*/104201, /*cutin_2*/0, /*guild_id*/10, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，能支持前衛戰鬥的吟遊詩人。\n能以必殺技，召喚能幫助己方恢復，以及攻擊敵人的妖精到最前線。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104301, /*unit_name*/"真琴", /*kana*/"真琴", /*prefab_id*/104301, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/165, /*atk_type*/1, /*normal_atk_cast_time*/2.125, /*cutin_1*/104301, /*cutin_2*/0, /*guild_id*/4, /*exskill_display*/0, /*comment*/"【物理】位於前衛，能削弱對手的防禦力，是一名正義的獸人。\n能夠降低敵方的防禦力，並且確確實實地給予傷害，\n以便盡早突破敵方防守。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104401, /*unit_name*/"伊莉亞", /*kana*/"伊莉亞", /*prefab_id*/104401, /*rarity*/3, /*motion_type*/3, /*se_type*/3, /*move_speed*/450, /*search_area_width*/425, /*atk_type*/2, /*normal_atk_cast_time*/1.425, /*cutin_1*/104401, /*cutin_2*/0, /*guild_id*/12, /*exskill_display*/0, /*comment*/"【魔法】位於中衛，削弱生命來攻擊的「統御黑夜之人」。\n以自己 HP 交換而發出的技能擁有高度殲滅能力，範圍攻擊的威力無人能及。", /*only_disp_owned*/0, /*start_time*/"2018/10/22 16:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104501, /*unit_name*/"空花", /*kana*/"空花", /*prefab_id*/104501, /*rarity*/2, /*motion_type*/2, /*se_type*/2, /*move_speed*/450, /*search_area_width*/130, /*atk_type*/1, /*normal_atk_cast_time*/2.375, /*cutin_1*/104501, /*cutin_2*/0, /*guild_id*/12, /*exskill_display*/0, /*comment*/"【物理】在前方擔任誘餌，吸收攻擊後瘋狂爆發的超Ｍ少女。\n透過必殺技對自己張開魔法屏障，\n吸收敵人的魔法攻擊來恢復ＨＰ。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104601, /*unit_name*/"珠希", /*kana*/"珠希", /*prefab_id*/104601, /*rarity*/2, /*motion_type*/2, /*se_type*/2, /*move_speed*/450, /*search_area_width*/215, /*atk_type*/1, /*normal_atk_cast_time*/2.25, /*cutin_1*/104601, /*cutin_2*/0, /*guild_id*/8, /*exskill_display*/0, /*comment*/"【物理】位於前衛，擅長魔法隊伍的隨心所欲的貓娘。\n專門攻擊「魔法攻擊力最高」的敵人，\n以降低傷害及魔法攻擊力，並吸收 TP。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104701, /*unit_name*/"純", /*kana*/"純", /*prefab_id*/104701, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/135, /*atk_type*/1, /*normal_atk_cast_time*/2.125, /*cutin_1*/104701, /*cutin_2*/0, /*guild_id*/2, /*exskill_display*/0, /*comment*/"【物理】在最前方施展完全防禦技能的騎士團長。\n必殺技則可展開任何傷害都能\n吸收的屏障。尤其以長期戰中的壓倒性防禦能力自豪。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104801, /*unit_name*/"美冬", /*kana*/"美冬", /*prefab_id*/104801, /*rarity*/2, /*motion_type*/6, /*se_type*/6, /*move_speed*/450, /*search_area_width*/420, /*atk_type*/1, /*normal_atk_cast_time*/2.19, /*cutin_1*/104801, /*cutin_2*/0, /*guild_id*/8, /*exskill_display*/0, /*comment*/"【物理】位於中衛，擁有暈眩與恢復的技能的傭兵戰士。\n擁有高度防禦能力及自我恢復能力，\n能使中衛的防守更為堅固，支撐整個隊伍。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/104901, /*unit_name*/"靜流", /*kana*/"靜流", /*prefab_id*/104901, /*rarity*/3, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/285, /*atk_type*/1, /*normal_atk_cast_time*/2.375, /*cutin_1*/104901, /*cutin_2*/0, /*guild_id*/6, /*exskill_display*/0, /*comment*/"【物理】位於前衛，可守護己方免於物理攻擊，是一位姊姊。\n一般技能可以展開物理無效的屏障，不僅能夠保護同伴全員，還能再於自身添加物理吸收屏障，進而挺身討伐敵方前衛。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105001, /*unit_name*/"美咲", /*kana*/"美咲", /*prefab_id*/105001, /*rarity*/1, /*motion_type*/7, /*se_type*/7, /*move_speed*/450, /*search_area_width*/760, /*atk_type*/2, /*normal_atk_cast_time*/2.07, /*cutin_1*/105001, /*cutin_2*/0, /*guild_id*/15, /*exskill_display*/0, /*comment*/"【魔法】位於後衛，能施放範圍攻擊，是一名小淑女．魔女。\n擁有能多次範圍攻擊的技能，以及能攻擊\n敵方全體的必殺技，特化為範圍殲滅。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105101, /*unit_name*/"深月", /*kana*/"深月", /*prefab_id*/105101, /*rarity*/2, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/565, /*atk_type*/1, /*normal_atk_cast_time*/2.25, /*cutin_1*/105101, /*cutin_2*/0, /*guild_id*/11, /*exskill_display*/0, /*comment*/"【物理】位於中衛，能將敵方領域弱化，是一位單眼惡魔。\n領域的持續時間雖然不長，但卻能大幅度減少\n範圍內的敵方之物理防禦力。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105201, /*unit_name*/"莉瑪", /*kana*/"莉瑪", /*prefab_id*/105201, /*rarity*/1, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/105, /*atk_type*/1, /*normal_atk_cast_time*/2.215, /*cutin_1*/105201, /*cutin_2*/0, /*guild_id*/5, /*exskill_display*/0, /*comment*/"【物理】在最前方守護夥伴的戀愛少女。\n戰鬥開始後不久就會朝最前線\n突進，挺身擋在敵人眼前。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105301, /*unit_name*/"莫妮卡", /*kana*/"莫妮卡", /*prefab_id*/105301, /*rarity*/3, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/410, /*atk_type*/1, /*normal_atk_cast_time*/2.24, /*cutin_1*/105301, /*cutin_2*/0, /*guild_id*/12, /*exskill_display*/0, /*comment*/"【物理】位於中衛，能鼓舞鼓舞己方攻擊，是一位女軍人。\n不僅能夠大幅度增加全體的物理、魔法攻擊力，就連攻擊速度都能夠提升，使得戰鬥能提前結束。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105401, /*unit_name*/"紡希", /*kana*/"紡希", /*prefab_id*/105401, /*rarity*/2, /*motion_type*/1, /*se_type*/1, /*move_speed*/450, /*search_area_width*/195, /*atk_type*/1, /*normal_atk_cast_time*/2.42, /*cutin_1*/105401, /*cutin_2*/0, /*guild_id*/10, /*exskill_display*/0, /*comment*/"【物理】位於前衛，特化妨礙敵人技能的裁縫師女孩。\n除了可強制移動敵人使敵方陣形瓦解，還可\n降低對手的移動速度、行動速度，使其反擊變得遲緩。", /*only_disp_owned*/0, /*start_time*/"2018/11/08 16:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105601, /*unit_name*/"流夏", /*kana*/"流夏", /*prefab_id*/105601, /*rarity*/3, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/158, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin_1*/105601, /*cutin_2*/0, /*guild_id*/2, /*exskill_display*/0, /*comment*/"【物理】在前方為夥伴揮劍的豪氣大姊。\n擁有能夠反擊對手的挑釁招數、降低敵人防禦力的\n必殺技，在攻防兩面都能發揮實力。", /*only_disp_owned*/0, /*start_time*/"2019/03/13 16:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105701, /*unit_name*/"吉塔", /*kana*/"吉塔", /*prefab_id*/105701, /*rarity*/3, /*motion_type*/4, /*se_type*/4, /*move_speed*/450, /*search_area_width*/245, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin_1*/105701, /*cutin_2*/0, /*guild_id*/4, /*exskill_display*/0, /*comment*/"【物理】在前方先下手為強施展大招的騎空士。\n藉由技能可恢復ＴＰ，加快必殺技的發動\n從戰鬥初期就能對敵方前線造成大傷害。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105801, /*unit_name*/"貪吃佩可", /*kana*/"貪吃佩可", /*prefab_id*/105801, /*rarity*/1, /*motion_type*/5, /*se_type*/5, /*move_speed*/450, /*search_area_width*/155, /*atk_type*/1, /*normal_atk_cast_time*/2.25, /*cutin_1*/105801, /*cutin_2*/0, /*guild_id*/1, /*exskill_display*/0, /*comment*/"【物理】位於前衛，全力保護同伴，是一位貪吃的劍士。\n能夠增加 HP，也能恢復自己的 HP，\n擔任己方的保護牆的角色。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
INSERT INTO `unit_data` VALUES (/*unit_id*/105901, /*unit_name*/"可可蘿", /*kana*/"可可蘿", /*prefab_id*/105901, /*rarity*/1, /*motion_type*/6, /*se_type*/6, /*move_speed*/450, /*search_area_width*/500, /*atk_type*/1, /*normal_atk_cast_time*/2.34, /*cutin_1*/105901, /*cutin_2*/0, /*guild_id*/1, /*exskill_display*/0, /*comment*/"【物理】位於中衛，無論是輔助還是攻擊皆能支援，是一位負責指引迷津的巫女。\n必殺技能增加己方全體的攻擊力，\n且有使自己恢復的功能，因此能持續支援。", /*only_disp_owned*/0, /*start_time*/"2015/04/01 15:00:00", /*end_time*/"2030/12/31 15:00:00");
