INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203100, /*unit_name*/"幼年巨蜥", /*prefab_id*/203100, /*motion_type*/0, /*se_type*/17, /*move_speed*/270, /*search_area_width*/800, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】近距離攻擊\n儘管還年幼仍然會吐著強力\n的火焰，是巨蜥的幼獸。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203200, /*unit_name*/"小惡魔", /*prefab_id*/203200, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】近距離攻擊\n使用與身長比例不符、異常巨大的爪子\n做為攻擊，是仍在見習中的惡魔。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203300, /*unit_name*/"哥布靈守衛", /*prefab_id*/203300, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/150, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n以逼近身高高度的巨大的盾牌\n守護著同伴的，見習哥布靈士兵。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203301, /*unit_name*/"叢林護衛", /*prefab_id*/203301, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/150, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n以看似臉譜的大型盾牌嚇阻\n入侵者，是叢林的守護者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203400, /*unit_name*/"鬼燈蟲", /*prefab_id*/203400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/800, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【恢復】單方恢復\n儲存於尾部的蜜，能夠成為\n魔物的營養源，是種不可思議的螢火蟲。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203500, /*unit_name*/"鑽頭鼠", /*prefab_id*/203500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n能從地底伸手，從腳下開始\n攻擊的鼴型魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203700, /*unit_name*/"堅果射手", /*prefab_id*/203700, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/350, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】範圍攻擊\n將吃完後堅果殼給砸向敵人，\n一種充滿惡意的松鼠魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203800, /*unit_name*/"獸人", /*prefab_id*/203800, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n身著從被擊倒的冒險家身上搶來的鎧甲，\n且揮舞著巨斧的一種凶暴的森林獸人。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203801, /*unit_name*/"強獸人", /*prefab_id*/203801, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n因反覆且嚴厲的鍛鍊，而獲得強韌肉體，\n獸人裡頭的上位種。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203900, /*unit_name*/"邪惡大砲", /*prefab_id*/203900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1080, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n因為長年的使用而寄生著惡魔之魂，\n擁有生命的砲台。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204000, /*unit_name*/"小小格里芬", /*prefab_id*/204000, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n羽毛還沒長齊，\n還飛不起來的雛獅鷲。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204200, /*unit_name*/"女中音之鳥", /*prefab_id*/204200, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/450, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【恢復】範圍恢復\n那治癒人的歌聲甚至能傳送至遠方，\n在鳥魔物界中是一位知名的歌手");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204300, /*unit_name*/"毒豆莢", /*prefab_id*/204300, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n用那血盆大口之中所蘊含的腐蝕液體，\n將捕獲的獵物吃掉。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204400, /*unit_name*/"惡魔寶寶", /*prefab_id*/204400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻擊\n用才剛學會的魔法，不管三七二十一\n就到處攻擊的幼年惡魔。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204500, /*unit_name*/"巨蟹之槌", /*prefab_id*/204500, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n以大型的爪子以及堅硬的殼，\n面對各種巨大的對手的螃蟹魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204600, /*unit_name*/"魔法射線", /*prefab_id*/204600, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/1080, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】範圍攻擊\n以頭部石塊發射超音波，\n輕鬆狩獵的魟魚魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204700, /*unit_name*/"葉豬", /*prefab_id*/204700, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n將堅硬的葉子如鎧甲般纏繞身體，\n碰到即會帶來劇烈疼痛的大型野豬。\n但終歸只是葉子。無法防禦魔法。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204701, /*unit_name*/"沙蟒", /*prefab_id*/204701, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n將沙子作為鎧甲纏繞於身上，\n極為身為不畏捨身的戰士的鐵證。\n但終歸只是沙子。無法防禦魔法。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205100, /*unit_name*/"土蛇", /*prefab_id*/205100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n躲在暗處吸取自各個角落飛來的魔力，\n以填飽肚子的魔物蛇的變種。\n魔力越高越容易被它盯上。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205200, /*unit_name*/"海馬騎士", /*prefab_id*/205200, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n奪取在海中溺水的騎士的茅，\n劃界警備的海馬戰士");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205300, /*unit_name*/"冰封鳥", /*prefab_id*/205300, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/2, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻擊\n以尾部羽毛上發射冰霜攻擊的\n鳥型魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205500, /*unit_name*/"地底毒蛇", /*prefab_id*/205500, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n挖掘洞穴，靜待獵物掉入洞穴，\n再以毒牙將其斃命的蛇的魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300100, /*unit_name*/"牛頭怪", /*prefab_id*/300100, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以迷宮作為巢穴，牡牛座的魔星獸。\n不斷揮舞巨大的戰斧，奪取冒險家的性命。\n\n・對全體敵人造成大幅度的物理傷害，\n並造成火傷。\n・對 1 名敵人造成大幅度的物理傷害，\n並在擊退敵方的同時使其暈眩。\n・對前方範圍內的敵人造成中幅度的魔法傷害，\n使其麻痺。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300300, /*unit_name*/"黑羊", /*prefab_id*/300300, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】住在夢魔之館，牡羊座的魔星獸。\n將冒險家束縛在夢的世界中，並啃噬他們的靈魂。\n\n・對全體敵人造成大幅度的魔法傷害，\n並對遭束縛的敵人造成小幅度的魔法傷害。\n・對前方範圍內的敵人造成中幅度的魔法傷害。\n・束縛 １ 名敵人。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300500, /*unit_name*/"蜥蜴人", /*prefab_id*/300500, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能自在地揮舞手中\n的兩把劍刃，\n還可以使用煙霧彈來模糊\n對手視野的巨蜥戰士。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300600, /*unit_name*/"爬行者", /*prefab_id*/300600, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】從頭前施放的劇毒與藤蔓\n所組成的範圍物理攻擊，能將貿然接近\n的冒險家的 HP 給一口氣略奪而盡，\n如同惡魔般的花朵。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300601, /*unit_name*/"芒刺爬行者", /*prefab_id*/300601, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的爬行者。\n以帶有許多刺的藤蔓綑綁，限制敵人的行動。\n\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並使其呈現麻痺狀態。\n・對前方範圍內的敵人造成物理防禦力下降，\n並使其呈現中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300602, /*unit_name*/"芒刺爬行者", /*prefab_id*/300602, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的爬行者。\n以帶有許多刺的藤蔓綑綁，限制敵人的行動。\n\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並使其呈現麻痺狀態。\n・對前方範圍內的敵人造成物理防禦力下降，\n並使其呈現中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300700, /*unit_name*/"獸人隊長", /*prefab_id*/300700, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以隊長的姿態鼓舞著群體，\n以能震裂大地般的強力連擊造成\n敵人大幅度的物理傷害，\n可說是獸人中的獸人。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300701, /*unit_name*/"獸人首領", /*prefab_id*/300701, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的獸人隊長。\n以巨大的盾牌，持續使出攻防合一的攻勢。\n\n・對前方範圍內的敵人造成大幅度的物理傷害，\n並使其暈眩。\n・對前方範圍內的敵人造成大幅度的防禦力下降。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/"遠古之角", /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】一旦開始狂怒地踐踏震地，\n便無法控制，聽說那嘶吼\n甚至能響徹天際，\n是傳說中的野角。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300801, /*unit_name*/"靈魂號角", /*prefab_id*/300801, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的遠古之角。\n其角富有魔力，可放出大於打擊之力的強力一擊。\n\n・對前方範圍內的敵人造成大幅度的魔法傷害，\n在擊退敵方的同時使其暈眩。\n・對前方範圍內的敵人造成小幅度的魔法傷害，\n並小幅度降低敵人的魔法防禦與攻擊速度。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/"遠古暴君", /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能以強力的頭錘擊碎大地，\n對全體造成大幅度的傷害。\n無論是身體還是硬殼都擁有大幅度的進化，\n是如同岩山一般的硬殼暴君。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301000, /*unit_name*/"希德雷克", /*prefab_id*/301000, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】吐出強力冰之吐息\n將前衛瞬間冰凍，\n接著將身軀一口咬碎的\n凶猛水龍。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301100, /*unit_name*/"殘暴之熊", /*prefab_id*/301100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以與體格相異的靈敏度\n以及撕咬攻擊奪取體力，\n並以巨大的爪子撕裂敵人，\n是殘虐無道的森林支配者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301101, /*unit_name*/"狂怒之熊", /*prefab_id*/301101, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的殘暴之熊\n充滿憤怒的大力之拳，能使敵方地面裂開。\n\n・對前方範圍內的敵人造成大幅度的物理傷害，\n並使其暈眩。\n・對前方敵人造成小幅度的物理傷害，\n並對敵人造成中幅度的物理防禦力下降。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/"碎裂魔像", /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能無止盡地召喚出魔像，\n並以岩石削弱體力與精神。\n用魔力將無數的魔像殘骸\n再建而成的巨魔像。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301500, /*unit_name*/"狂暴狼怪", /*prefab_id*/301500, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以狼嚎使獵物麻痺，\n再趁其膽怯時用爪子撕裂。\n止住無數冒險家的腳步，\n可謂殘虐至極的魔界之狼。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301600, /*unit_name*/"鶺鴟", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】帶來強烈暴風的大怪鳥。\n逃出魔物圖鑑，做盡破壞之能事。\n\n・根據角色的最高 HP，對全體敵人\n造成大幅度的傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並且使其暈眩。");
