INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205401, /*unit_name*/"熔岩魚", /*prefab_id*/205401, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n以連岩石都能咬碎的強韌下顎，\n將周遭事物給食之殆盡的魚之魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205500, /*unit_name*/"沙陸毒蛇", /*prefab_id*/205500, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n挖掘洞穴，靜待獵物掉入洞穴，\n再以毒牙將其斃命的蛇的魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205600, /*unit_name*/"狂犬", /*prefab_id*/205600, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n與主人一同被創造出來，一隻兇暴的改造犬。\n像為得支撐住那大頭所流露的猙獰面容，極為恐怖。");
