INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206000, /*unit_name*/"海獅怪", /*prefab_id*/206000, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n不論體型還態度，皆為條漢子的北\n海之怪。以厚重的脂肪層來抵禦且\n壓制敵人的物理攻擊。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206100, /*unit_name*/"睿智權杖", /*prefab_id*/206100, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1080, /*atk_type*/2, /*normal_atk_cast_time*/4.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】遠距離攻擊\n以被噴到就會凍結的寒氣魔法，\n剝奪敵人戰意的鸚鵡螺型魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206200, /*unit_name*/"流氓水獺", /*prefab_id*/206200, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n人一不留意時就拿貝殼亂丟亂砸，\n一種性格惡劣的北海小混混。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206300, /*unit_name*/"露娜聚光燈", /*prefab_id*/206300, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/1110, /*atk_type*/0, /*normal_atk_cast_time*/10.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【恢復】單體ＴＰ恢復\n與露娜之塔所建時一同打造出來的一種能量增幅器。\n將散布各處的魔力濃縮，並做為強化供給於守護者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206400, /*unit_name*/"小魔眼", /*prefab_id*/206400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n侍奉獨眼族之王的小小侍從。\n對於大王所給予的金光閃閃裝備特別的鍾愛。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206500, /*unit_name*/"擲岩猴老大", /*prefab_id*/206500, /*motion_type*/0, /*se_type*/0, /*move_speed*/300, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n擲岩猴更加成長的姿態。從小就持續投擲大岩石\n的一雙粗臂能持續造成強烈的打擊。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206700, /*unit_name*/"日和的闇影", /*prefab_id*/206700, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.295, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"有著日和樣貌的特殊魔物\n\n‧對面前的 1 名敵人造成大幅度的物理傷害，\n　並對附近小範圍內所有敵人造成\n　中幅度的物理傷害。\n‧對面前的 1 名敵人造成中幅度的物理傷害。\n‧對面前的 1 名敵人造成小幅度的物理傷害，\n　並中幅度提升自己的物理攻擊力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206701, /*unit_name*/"怜（新年）的闇影", /*prefab_id*/206701, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/400, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"有著怜（新年）樣貌的特殊魔物\n\n‧對全體敵人造成中幅度的物理傷害，\n　並且使其暈眩。\n‧【挑釁】對全體敵人挑釁。\n　若在挑釁後的動作中受到攻擊，\n　則對範圍內的所有敵人造成中幅度的物理傷害，\n　再小幅度降低其物理防禦力。\n‧對自己周圍的全體夥伴\n　展開魔法無效的屏障，\n　並中幅度提升自己的魔法防禦力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206702, /*unit_name*/"優衣（新年）的闇影", /*prefab_id*/206702, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/600, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"有著優衣（新年）樣貌的特殊魔物\n\n‧對全體夥伴展開物理/魔法無效的屏障，\n　並施加持續恢復 HP 的狀態，\n　但會大幅度降低自己的魔法攻擊力。\n‧大幅度降低從前方算起第 2 位的 1 名敵人\n　的物理攻擊力和魔法攻擊力。\n‧大幅度提升全體夥伴中魔法攻擊角色\n　的魔法攻擊力。");
