INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300100, /*unit_name*/"牛頭怪", /*prefab_id*/300100, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】獲得了更多力量的牡羊座之魔星獸。\n超越了力量極限的戰斧一擊，能破壞所有的一切。\n\n・對全體敵人造成大幅度的物理傷害，並使其\n　火傷。\n・對1名敵人造成大幅度的物理傷害，並在擊退\n　敵方的同時使其暈眩。\n‧對前方範圍內的敵人造成中幅度的魔法傷害\n　使其麻痺。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300101, /*unit_name*/"牛頭怪", /*prefab_id*/300101, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】獲得了更多力量的牡羊座之魔星獸。\n超越了力量極限的戰斧一擊，能破壞所有的一切。\n\n・對全體敵人造成大幅度的物理傷害，並使其\n　火傷。\n・對1名敵人造成大幅度的物理傷害，並在擊退\n　敵方的同時使其暈眩。\n‧對前方範圍內的敵人造成中幅度的魔法傷害，\n　使其麻痺。\n・大幅度提高自己的物理攻擊力與魔法攻擊力，\n　對前方第１位與第２位的角色\n　造成大幅度的物理傷害。\n　發動技能後，使自己的物理防禦力與\n　魔法防禦力大幅度下降。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300200, /*unit_name*/"天蠍毒者", /*prefab_id*/300200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"揮舞猛毒之尾棘來降下災厄，天蠍座的魔星獸。\n守護沙漠之蟲神，因信仰的流失而化為了邪神。\n\n・對敵方全體造成大量的物理傷害。\n　每當Boss使用了一次必殺技，\n　便會提升給予敵人的傷害。\n　此效果，最多累積上限為3次。\n・對前方範圍內的敵人給予中量物理傷害，\n　並使其中毒。\n・將自身所被施放的降低素質的效果解除，\n　並且再額外增加自身的物理防禦力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300300, /*unit_name*/"梅莎路提姆", /*prefab_id*/300300, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】獲得了更多力量的牡羊座之魔星獸。在鬱悶難耐的睡夢牢獄中，享盡冒險者們的魂魄。\n\n‧對全體敵人造成大幅度的魔法傷害，\n　並對遭束縛的敵人造成小幅度的魔法傷害。\n‧對前方範圍內的敵人造成中幅度的魔法傷害。\n‧束縛 1 名敵人。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300301, /*unit_name*/"梅莎路提姆", /*prefab_id*/300301, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】獲得了更多力量的牡羊座之魔星獸。在鬱悶難耐的睡夢牢獄中，享盡冒險者們的魂魄。\n\n‧對全體敵人造成大幅度的魔法傷害，\n　並對遭束縛的敵人造成小幅度的魔法傷害。\n‧對前方範圍內的敵人造成中幅度的魔法傷害。\n‧束縛 1 名敵人。\n・使剩餘 HP 最低的 1 名敵人，成為被挑釁的\n  狀態。\n  此技能所造成的狀態異常，還能夠額外造成\n  其傷害無效化。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300400, /*unit_name*/"阿爾珈提", /*prefab_id*/300400, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"以妖異的邪教圖謀支配世界，魔羯座的魔星獸。\n對在沙漠受誘惑而迷途的人，以惡魔的儀式進行俘虜。\n\n‧必殺技對可全體敵人造成大幅度的魔法傷害，\n　並且降低物理攻擊角色的物理攻擊力，\n　與降低魔法攻擊角色的魔法防禦力。\n‧對物理攻擊力最高的 1 名角色造成\n　特大幅度的魔法傷害，並降低其 TP。\n‧對前方的 2 名角色造成小幅度的魔法傷害，\n　並且施加沉默狀態。\n\n‧再對全體敵人造成中幅度的魔法傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300500, /*unit_name*/"蜥蜴人", /*prefab_id*/300500, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能自在地揮舞\n手中的兩把劍刃，\n還可以使用煙霧彈來模糊\n對手視野的巨蜥戰士。\n\n‧對隨機 1 名敵人造成中幅度的物理傷害。\n\n‧對以隨機敵人為中心的小範圍內，\n　造成小幅度的物理傷害，\n　並且施加黑暗狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300600, /*unit_name*/"爬行者", /*prefab_id*/300600, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能從頭部施放劇毒\n與藤蔓所組成的範圍物理攻擊，\n能將貿然接近的冒險家的 HP \n給一口氣奪走的魔性之花。\n\n‧對前方的 2 名角色造成中幅度的物理傷害。\n\n‧對前方的 3 名角色降低其物理防禦力，\n　並且施加中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300601, /*unit_name*/"芒刺爬行者", /*prefab_id*/300601, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的爬行者。\n以帶有許多刺的藤蔓綑綁，限制敵人的行動。\n\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並使其呈現麻痺狀態。\n・對前方範圍內的敵人造成物理防禦力下降，\n並使其呈現中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300602, /*unit_name*/"芒刺爬行者", /*prefab_id*/300602, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的爬行者。\n以帶有許多刺的藤蔓綑綁，限制敵人的行動。\n\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並使其呈現麻痺狀態。\n・對前方範圍內的敵人造成物理防禦力下降，\n並使其呈現中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300700, /*unit_name*/"獸人領導者", /*prefab_id*/300700, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以隊長的姿態鼓舞著群體，\n以能震裂大地般的強力連擊\n造成敵人大幅度的物理傷害，\n是獸人中的獸人。\n\n‧對隨機的 3 名角色\n　造成中幅度的物理傷害，\n　再提升物理防禦力。\n\n‧提升全體夥伴的物理攻擊力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300701, /*unit_name*/"獸人酋長", /*prefab_id*/300701, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的獸人隊長。\n以巨大的盾牌，持續使出攻防合一的攻勢。\n\n・對前方範圍內的敵人造成大幅度的物理傷害，\n並使其暈眩。\n・對前方範圍內的敵人造成大幅度的防禦力下降。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/"角鹿長老", /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】一旦開始狂怒地踐踏震地，\n便無法控制，聽說那嘶吼\n甚至能響徹天際，\n是傳說中的狂野角鹿。\n\n‧對全體敵人造成中幅度的物理傷害，\n　並且以小距離向後擊飛。\n\n‧對前方的 3 名角色造成中幅度的物理傷害，\n　並降低其物理攻擊力\n　與物理防禦力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300801, /*unit_name*/"聖靈角鹿", /*prefab_id*/300801, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的遠古之角。\n其角富有魔力，可放出大於打擊之力的強力一擊。\n\n・對前方範圍內的敵人造成大幅度的魔法傷害，\n在擊退敵方的同時使其暈眩。\n・對前方範圍內的敵人造成小幅度的魔法傷害，\n並小幅度降低敵人的魔法防禦與攻擊速度。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/"暴君長老", /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能以強力的頭錘擊碎大地，\n對全體造成大幅度的傷害。\n無論是身體還是硬殼都擁有大幅度的進化，\n是如同岩山一般的硬殼暴君。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301000, /*unit_name*/"希德雷克", /*prefab_id*/301000, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】吐出強力的寒氣\n將前衛瞬間冰凍，\n接著將身軀一口咬碎的\n凶猛深海龍。\n\n‧對前方的 3 名角色造成中幅度的物理傷害。\n\n‧對前方的 2 名角色造成中幅度的物理傷害，\n　並且施加凍結狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301100, /*unit_name*/"殘暴狂熊", /*prefab_id*/301100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以不符合體格的靈敏度\n以及撕咬攻擊奪取體力，\n並以巨大的爪子撕裂敵人，\n是慘無人道的森林支配者。\n\n‧對前方的 4 名角色造成大幅度的物理傷害。\n\n‧對前方的 1 名角色造成小幅度的物理傷害，\n　並降低其物理攻擊力。\n　再提高自己的物理攻擊力。\n　");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301101, /*unit_name*/"狂焰戰熊", /*prefab_id*/301101, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的殘暴之熊\n充滿憤怒的大力之拳，能使敵方地面裂開。\n\n・對前方範圍內的敵人造成大幅度的物理傷害，\n並使其暈眩。\n・對前方敵人造成小幅度的物理傷害，\n並對敵人造成中幅度的物理防禦力下降。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/"碎裂魔像", /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能無止盡地召喚出魔像，\n並以尖銳岩石削弱其體力與精神。\n用魔力將無數的魔像殘骸\n重塑而成的巨大魔像。\n\n‧對前方的4名角色造成中幅度物理傷害，\n　並減少其 TP。\n\n‧召喚魔像。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301300, /*unit_name*/"大胃王", /*prefab_id*/301300, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"惡名昭彰的暴食之兇獸，是為天秤座的魔星獸。\n秤量被剝奪之魂的善惡，以品嘗之間差異為樂。\n\n・不具有對敵人的攻擊所造成的麻痺、暈眩、\n　束縛，以及行動速度低下的抗性。\n・對敵方全體造成大量的魔法傷害，並且對自\n　身的物理與魔法攻擊力給予特大量上升。\n・對後方範圍內的敵人造成中量的魔法傷害。\n・對前方範圍內的敵人造成中量的魔法傷害，並\n　小幅度降低TP。　並且使自身的TP，中幅度\n　上升。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301400, /*unit_name*/"美杜莎", /*prefab_id*/301400, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"以無數毒蛇組成的長髮，是為處女座的魔星獸。\n悲戀之痛，使祂拒萬物於千里且使其幻化成石。\n\n・對敵方全體造成大量魔法傷害，並使前方範圍\n　內的敵人陷入石化狀態。\n・對前方範圍內的敵人造成物理傷害，並再對陷\n　入石化狀態的敵人，降低大量的TP。\n・對物理攻擊力最高的1名敵方角色，造成大量\n　魔法傷害，使其陷入中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301500, /*unit_name*/"狂戰之狼", /*prefab_id*/301500, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以嚎叫使獵物麻痺，\n再趁其膽怯時用爪子撕裂。\n止住無數冒險家的腳步，\n是極度殘虐的魔界之狼。\n\n‧對隨機的 2 名角色造成大幅度的物理傷害。\n\n‧對前方的 3 名角色造成小幅度的魔法傷害，\n　並且施加麻痺狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301600, /*unit_name*/"席茲", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】帶來強烈暴風的大怪鳥。\n逃出魔物圖鑑，做盡破壞之能事。\n\n・根據角色的最高 HP，對全體敵人\n造成大幅度的傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並且使其暈眩。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301601, /*unit_name*/"席茲", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】帶來強烈暴風的大怪鳥。\n逃出魔物圖鑑，做盡破壞之能事。\n\n・根據角色的最高 HP，對全體敵人\n造成大幅度的傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並且使其暈眩。\n・根據角色的最高 HP，對前方範圍內的敵人\n造成中幅度的傷害，並降低其物理防禦力。\n・當席茲本身的 HP 降至 50％ 以下，\n其攻擊力將會越發殘暴。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301700, /*unit_name*/"賈巴沃克", /*prefab_id*/301700, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301800, /*unit_name*/"人馬座", /*prefab_id*/301800, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"將衝向天際的許多標的射倒，射手座的魔星獸。\n令纏繞著電芒的箭，在剎那間貫穿一切的至高絕技。\n\n‧必殺技可對所有敵人依最大 HP\n　造成魔法傷害。\n　可對黑暗狀態的敵人造成 3 倍傷害。\n‧對全體敵人造成中幅度的魔法傷害，\n　並對剩餘 HP 比例最低的 1 名敵人\n　施加強力黑暗狀態。\n‧對前方的 3 名敵人造成物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301900, /*unit_name*/"嘯浪虎鯨", /*prefab_id*/301900, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以銳利的眼神追尋獵物\n及浪濤的海中獵人。\n利用魔力讓潮水不斷湧出\n進而削弱敵方的鋒芒和氣力。\n\n‧對前方的 4 名角色造成中幅度的物理傷害，\n　並降低其物理攻擊力。\n\n‧對前方的 2 名角色造成小幅度的魔法傷害，\n　並降低其 TP。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302000, /*unit_name*/"狂暴格里芬", /*prefab_id*/302000, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在嚴酷的環境中進化的小小格里芬。以龍捲風將獵物捲起，並在空中獵捕。\n\n・對前方範圍內的敵人造成中幅度的魔法傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302001, /*unit_name*/"狂暴格里芬", /*prefab_id*/302001, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在嚴酷的環境中進化的小小格里芬。以龍捲風將獵物捲起，並在空中獵捕。\n\n・對前方範圍內的敵人造成中幅度的魔法傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302100, /*unit_name*/"飛龍", /*prefab_id*/302100, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】使用大面皮翼，於空中飛翔的翼龍。\n吐出巨大的火球，阻擋入侵者的動作。\n\n・對前方範圍內的敵人造成中幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302101, /*unit_name*/"飛龍", /*prefab_id*/302101, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】使用大面皮翼，於空中飛翔的翼龍。\n吐出巨大的火球，阻擋入侵者的動作。\n\n・對前方範圍內的敵人造成中幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302200, /*unit_name*/"逝者・卡拉迪翁", /*prefab_id*/302200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】由於露娜的魔力而甦醒，古代的機械兵器。\n無慈悲地釋放而出的魔力，阻礙著入侵者。\n\n・對敵方全體造成特大的魔法傷害，並大幅度地\n　降低魔法防禦力。\n・對前方範圍內的敵人造成大量物理傷害。\n・對前方範圍內的敵人造成大量的魔法傷害，\n　使其陷入麻痺狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302300, /*unit_name*/"睿智之鴞", /*prefab_id*/302300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302400, /*unit_name*/"電弧守護者", /*prefab_id*/302400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302500, /*unit_name*/"奇美拉", /*prefab_id*/302500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.4, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302600, /*unit_name*/"野豬雙胞胎", /*prefab_id*/302600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】獲得更多力量的雙子座的魔星獸。\n在前線一邊防禦，一邊用熟練的技能進行攻擊。\n\n・根據角色的最高 HP，對全體敵人造成\n大幅度的物理傷害。\n・對後衛的敵人造成中幅度的物理傷害，\n　使其攻擊速度小幅度下降。\n・對全體敵人，造成小幅度的物理的傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302601, /*unit_name*/"野豬雙胞胎", /*prefab_id*/302601, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】獲得更多力量的雙子座的魔星獸。\n在前線一邊防禦，一邊用熟練的技能進行攻擊。\n\n・根據角色的最高 HP，對全體敵人造成\n大幅度的物理傷害。\n・對後衛的敵人造成中幅度的物理傷害，\n　使其攻擊速度小幅度下降。\n・對敵方全體，造成小幅度的物理的傷害。\n　暴食中，極大幅度降低兩名和自己的物理防禦力與\n　魔法防禦力。\n　之後極大幅度提高自己的TP上升與暴擊率，\n　對全體敵人造成\n　大幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302700, /*unit_name*/"加爾基諾司", /*prefab_id*/302700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】盤踞在船墳的海灣中，巨蟹座的魔星獸。\n其巨大剪刀的一擊，可使無數的船隻沉沒。\n\n・對敵方全體造成大幅度的魔法傷害，並降低\n　的物理防禦力。\n・對前方範圍內的全體敵人造成中幅度的物理傷害\n　，並使其呈現暈眩狀態。\n・對敵方全體造成中幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302800, /*unit_name*/"奧爾獅", /*prefab_id*/302800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"頗負盛名的熱帶草原霸者，獅子座的魔星獸。\n對其刀刃相向者，將焚毀於灼熱的咆哮中。\n\n・對敵方全體，依照最大HP造成大幅度並無\n　視防禦力的物理傷害，並對剩餘HP較少的敵人\n　賦予火傷狀態。\n・對敵方1名角色造成大幅度物理傷害，\n　若為火傷狀態時則再追加額外傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害，\n　若為火傷狀態時則再追加額外傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302900, /*unit_name*/"水之里歐斯", /*prefab_id*/302900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"淪陷於寄宿在神瓶之中那魔力的水瓶座之魔星獸。\n沉溺於強大的力量中，卻因非得支配大海而暴走。\n\n‧必殺技，可對全體敵人造成共計六次的小幅度\n　魔法傷害。\n　當角色的 HP 低於 80% 時，能對其造成擊退，\n　而當低於 40% 時，除了擊退之外還能夠其陷入\n　暈眩狀態。\n・對面前的 3 名敵人造成中幅度的物理傷害。\n・能將自身的素質降低效果，與敵方全體的素質\n　提升的效果打消解除，並且還能夠增加自己的\n　TP。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303000, /*unit_name*/"托爾披頓", /*prefab_id*/303000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"使大海原暴走，並喚起狂風暴雨的雙魚座之魔星獸。\n周邊小弟身上的紅魚鰭，是扁人剛好啦！的證明。\n\n・更加地容易受到物理攻擊力提升、降低的效果\n　影響。\n‧必殺技，能夠對全體敵人造成大幅度的物理傷害，\n　並且能夠再使自己的行動速度與物理攻擊力\n　提升。\n・對敵方全體造成小幅度的物理傷害。\n・對前方的 2 名敵人造成中幅度的物理傷害，\n　並小幅度降低其物理防禦力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303100, /*unit_name*/"冬眠之熊", /*prefab_id*/303100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】躲藏於冰穴最深處，\n決意要終身冬眠的巨大野熊。\n長年累月在睡夢中度過，\n僅於狩獵的一瞬間甦醒過來。\n\n‧對全體敵人造成大幅度的魔法傷害，\n　並降低其物理攻擊力。\n\n‧對前方的 3 名角色造成小幅度的魔法傷害。\n　並且將前方的 1 名角色\n　大幅度擊飛，再施加凍結狀態\n　降低其移動速度。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303200, /*unit_name*/"寒冰孔雀夫人", /*prefab_id*/303200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】伴隨著暴風雪現身，\n會呼朋引伴的襲擊\n侵犯地盤的人，\n是雪國之中最華麗的女士。\n\n‧對前方的 4 名角色造成中幅度的魔法傷害，\n　並降低其物理攻擊力。\n\n‧對隨機的 2 名角色造成小幅度的魔法傷害，\n　並且施加\n　凍結狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303300, /*unit_name*/"石像鬼", /*prefab_id*/303300, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】遺留在腐朽的遺跡裡，守護著\n主人死去的地方，謎樣的鳥型雕像。\n隨著城鎮、命令的逝去，現在的他們\n無論來者是誰，全部予以火焰攻擊。\n\n‧對前方的 5 名角色造成中幅度的魔法傷害，\n　並且施加火傷狀態。\n　再降低其魔法防禦力。\n\n‧對前方的 3 名角色造成小幅度的魔法傷害，\n　再對其施加黑暗狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303400, /*unit_name*/"沙蜥蜴", /*prefab_id*/303400, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在生存競爭激烈的\n沙漠中的王者，是隻巨大蜥蜴。\n使用劇毒，活用沙子的戰略，\n就是嚴酷環境中習得的生存之道。\n\n‧對全體敵人造成中幅度的物理傷害，\n　並降低其行動速度。\n　再提升全體夥伴的物理攻擊力。\n\n‧對以前方 1 名角色為中心的\n　小範圍內造成小幅度的物理傷害，\n　並施加中毒狀態。再降低其物理防禦力。\n　");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303401, /*unit_name*/"岩之錘", /*prefab_id*/303401, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】身負月之魔力而產生變異的巨大蜥蜴。\n棘狀且堅硬的鱗片，能輕易削去敵人的體力與鎧甲。\n\n‧對全體敵人造成大幅度的物理傷害，並且減少其\n　TP。\n‧對前方的 2 名角色造成中幅度的物理傷害，\n　並且使其中毒。\n‧對前方的 3 名角色造成物理傷害。\n　並對前方的 1 名敵人造成大幅度的物理傷害，\n　並中幅度降低其物理防禦力和魔法防禦力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303402, /*unit_name*/"岩之錘", /*prefab_id*/303402, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】身負月之魔力而產生變異的巨大蜥蜴。\n棘狀且堅硬的鱗片，能輕易削去敵人的體力與鎧甲。\n\n‧對全體敵人造成大幅度的物理傷害，並且減少其\n　TP。\n‧對前方的 2 名角色造成中幅度的物理傷害，\n　並且使其中毒。\n‧對前方的 3 名角色造成物理傷害。\n　並對前方的 1 名敵人造成大幅度的物理傷害，\n　並中幅度降低其物理防禦力和魔法防禦力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303500, /*unit_name*/"怒蛇", /*prefab_id*/303500, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】從吸取他人的魔力之中，\n體悟出了妖術的魔物。\n能從敵人的傷口上奪取魔力，\n進而獲得更大的力量。\n\n‧對全體敵人造成中幅度的魔法傷害，\n　並降低全體敵人中\n　魔法攻擊角色的 TP。\n\n‧對以前方 1 名角色為中心的\n　小範圍內造成小幅度的魔法傷害，\n　並降低其魔法防禦力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303501, /*unit_name*/"怒蛇", /*prefab_id*/303501, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】從吸取他人的魔力之中，\n體悟出了妖術的魔物。\n能夠從敵人的傷口上奪取魔力，\n進而獲得更大的力量。\n\n‧對全體敵人造成中幅度魔法傷害，\n　並減少全體敵人中魔法攻擊\n　角色的TP。\n‧對前方 2 名角色造成小幅度的魔法傷害，\n　並降低其魔法防禦力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303600, /*unit_name*/"熾焰巨獸龍", /*prefab_id*/303600, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】靠著強韌的雙腳與銳利的爪子，\n可在一瞬間捕獲獵物的兇猛恐龍。\n在滅亡的大地上吞食熔岩，\n找出生存之道的孤高存在。\n\n‧對全體敵人造成中幅度的物理傷害，\n　並提升自己的行動速度。\n\n‧對前方的 3 名敵人造成中幅度的物理傷害，\n　並降低其行動速度。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303700, /*unit_name*/"地獄獵犬", /*prefab_id*/303700, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/0.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】儘管墜入了那火山之中，\n依舊身負地獄之炎復甦的雙頭魔犬。\n與愛犬波尼一同在地盤上奔馳著，\n並讓入侵者親身體驗那灼熱的洗禮。\n\n‧對全體敵人造成中幅度的物理傷害，\n　並施加火傷狀態。\n　再恢復召喚怪物的 HP。\n\n‧召喚波尼。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303800, /*unit_name*/"阿爾瑪", /*prefab_id*/303800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】遠古時用來守護迷宮的防衛兵器。作為最後的陷阱，靜待入侵者。\n・根據角色的最高 HP，對全體敵人\n造成中幅度的魔法傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303801, /*unit_name*/"阿爾瑪", /*prefab_id*/303800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】遠古時用來守護迷宮的防衛兵器。作為最後的陷阱，靜待入侵者。\n・根據角色的最高 HP，對全體敵人\n造成特大幅度的魔法傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害。\n・根據角色的最高 HP，對剩餘 HP 最高的敵人\n造成中幅度的魔法傷害，並使其暈眩。\n・當古老惡靈衛兵本身的 HP 降至 50％ 以下，\n其攻擊力將會越發殘暴。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303900, /*unit_name*/"獨眼巨人", /*prefab_id*/303900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"以怪力引以為傲，兇暴的獨眼巨人。\n將魔力凝聚於瞳中，進而釋放強力的怪異光線。\n\n・對敵方全體造成大幅度魔法傷害。\n　對剩餘 HP 為 100% 的角色提升傷害，\n　並且降低其行動速度。\n・對物理攻擊力最高的 1 名角色，依照其最大 HP\n　造成中幅度的物理傷害，並且減少其TP。\n");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303901, /*unit_name*/"獨眼巨人", /*prefab_id*/303901, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"以怪力引以為傲，兇暴的獨眼巨人。\n將魔力凝聚於瞳中，進而釋放強力的怪異光線。\n\n・對敵方全體造成大幅度魔法傷害。\n　對剩餘 HP 為 100% 的角色提升傷害，\n　並且降低其行動速度。\n・對物理攻擊力最高的 1 名角色，依照其最大 HP\n　造成中幅度的物理傷害，並且減少其TP。\n");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/304000, /*unit_name*/"夢魘", /*prefab_id*/304000, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在虛幻中誕生，徘徊於黑暗中的魔獸。\n將獵物綑綁於惡夢之牢中，並津津有味地吃著其生息。\n\n・對物理最高的1名敵方角色造成特大幅度的\n　魔法攻擊，並使其TP特大幅度下降。\n・以物理攻擊力最高的1名敵方角色為中心，對範圍\n　內的全體敵人造成中幅度的魔法傷害，並賦予\n　詛咒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/304100, /*unit_name*/"泰坦龍龜", /*prefab_id*/304100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"就連甲殼上都能扎起大樹之根的遠古陸龜。\n持續忍受著攻擊，伺機用憤怒之擊還以顏色。\n\n・對敵方全體造成大量的物理傷害。\n・將躲在甲殼裡時所受到的傷害量，給予敵人相\n　應的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/304200, /*unit_name*/"加爾格", /*prefab_id*/304200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】啃食屍體的殘暴的大型魔獸。\n強韌的刺佈滿全身，追捕獵物的執念甚深。\n・根據角色的最高 HP，對全體敵人\n造成中幅度的物理傷害。\n・對後方範圍的敵人造成中幅度的物理傷害，\n並使其暈眩。");
