INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/304900, /*unit_name*/"電光・怪人", /*prefab_id*/304900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】由於露娜的魔力而甦醒，巨腕的改造人類。\n小型反應爐於最大能量輸出時，甚至能使自身感電。\n\n・對前方範圍內的敵人造成大量物理傷害＆麻痺。\n　將自己身上的素質降低效果給解除，並使\n　其物理、魔法防禦力中量的下降。\n・對敵方全體造成大量的魔法傷害，\n　並對自身物理攻擊力長時間大幅上升。\n・使己方全體行動速度大幅度上升。");
