INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305100, /*unit_name*/"獨角兔", /*prefab_id*/305100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"驅動著巨大身軀肆虐的巨兔魔物。\n重複打出的一擊消耗劇烈，似乎是精力過剩。\n\n‧必殺技能對全體敵人造成大幅度的\n　物理傷害，並且造成擊退。\n　之後，在頭暈目眩期間，將大幅度降低\n　自己的物理防禦力與魔法防禦力。\n‧對全體敵人造成中幅度的物理傷害，\n　並且令前方範圍內的敵人暈眩。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305200, /*unit_name*/"奧拉姆", /*prefab_id*/305200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在遠古之前便被生產出的防衛兵器。\n因惡黨的陰謀，迎來了那悠久之沉睡的終焉。\n\n‧對全體敵人造成對應最大 HP 的\n　大幅度物理傷害，\n　同時大幅度降低自己的物理攻擊力。\n‧對前方中範圍內的敵人造成中幅度的物理傷害，\n　並大幅度降低其物理攻擊力。\n‧對前方廣範圍內的敵人造成中幅度的物理傷害，\n　並施加麻痺狀態。\n　當該角色的 HP 低於 70% 以下時，\n　麻痺的效果時間將會延長。\n‧當奧拉姆自身的 HP 低於 40% 之時，\n　它的攻擊將會變得更加兇暴。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305201, /*unit_name*/"奧拉姆", /*prefab_id*/305200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在遠古之前便被鍛造而出的防衛兵器。\n因惡黨的陰謀，迎來了那悠久之沉睡的終焉。\n\n・對敵方全體角色造成相應於最大HP的\n　大量物理傷害之際，使得自身　\n的物理攻擊力也受到大幅度下降。\n・對前方中範圍內的敵人造成中量物理傷害，\n　使其物理攻擊力大幅下降。");
