INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305300, /*unit_name*/"高等加爾格", /*prefab_id*/305300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】身負露娜之魔力而產生變異的猙獰魔獸。\n那尖銳的利牙，就連歷練的冒險家都能輕易撕裂。\n\n・必殺技能對敵方全體造成大量的物理傷害，並且\n　使自身的HP恢復。\n・對後方範圍內的敵人造成物理傷害，使其暈眩。\n　　並且使敵方全體的物理防禦力中幅度下降。\n・對前方範圍內的敵人造成物理傷害，使其中毒。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305400, /*unit_name*/"冰角", /*prefab_id*/305400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】此獨角獸強力的冰之頭刺，\n就連牢固的盾也能貫穿的獨角獸。\n將冒險者逼上了極北末端的冰山，\n再將他們推落寒冰地獄。\n\n‧對全體敵人造成大幅度的魔法傷害\n，並降低其物理防禦力。　\n再提升所有夥伴的 TP 上昇。\n‧對前方小範圍內造成小幅度的魔法\n傷害，並施加凍結狀態。　\n再對全體造成少量魔法傷害\n，並將前方 3 名角色稍微擊飛。　");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305500, /*unit_name*/"霧魔獸", /*prefab_id*/305500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/2160, /*atk_type*/2, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】在許多的傳說之中留下名號，記憶的大精靈。\n如霧狀般的不具實體，可將一切覆蓋隱藏。\n\n‧必殺技可對所有敵人\n　造成大幅度的魔法傷害，\n　當敵人處於詛咒狀態時，\n　傷害會上升。\n‧對全體敵人造成中幅度的魔法傷害。\n　並且施加詛咒狀態。\n‧從分身體（紫）（紅）（藍）之中\n　任意召喚 1 匹。\n　召喚時將全體敵人擊退。\n‧【物理】分身體（紫）可對自已展開\n　吸收物理/魔法的屏障。\n‧【物理】分身體（紅）可對全體敵人\n　造成中幅度的魔法傷害。\n‧【物理】分身體（藍）可對全體敵人\n　造成中幅度的魔法傷害。\n　再施加詛咒狀態。\n　分身體（藍） 的 HP 在 30% 以下時，\n　恢復霧魔獸的 300 TP。\n‧當霧魔獸自身的 HP 低於 \n　50% 時，牠的攻擊將會\n　變得更加兇暴。");
