INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305600, /*unit_name*/"邪惡領主", /*prefab_id*/305600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】覬覦月之魔力而現身的異界魔物。\n以魔眼與率領的兵力，放眼於征服露娜之塔。\n\n‧對全體敵人造成大幅度的魔法傷害，\n　並中幅度降低全體敵人的魔法防禦力。\n‧隨機分 6 次對 1 名角色造成小幅度的\n　魔法傷害。\n‧對從後方算起第 3 名角色造成中幅度的\n　魔法傷害，並施加長時間的拘束狀態。\n　再對全體造成小幅度的的魔法傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305700, /*unit_name*/"哥布林王者", /*prefab_id*/305700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"豪邁地揮舞著戰斧，老練的哥布林戰士。\n單手抱著年幼的戰士，展現畢生的絕技。\n\n‧必殺技可中幅度提升自己的物理攻擊力\n　與小幅度提升物理防禦力，\n　並對面前的 1 名角色造成特大幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305800, /*unit_name*/"涅普堤利昂", /*prefab_id*/305800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"傳說中被視為海神的使者，不可思議的軟體魔物。\n據說身上所負的幾乎全都是蘊含魔力的海水。\n\n‧必殺技，能夠對全體敵人造成大幅度的\n　魔法傷害。\n‧對從前方算起第 2 與第 3 名角色造成中幅度的\n　魔法傷害\n・在一定的範圍之內，展開能夠降低物理攻擊力\n　還有魔法防禦力，並且同時造成持續魔法傷害\n　的領域。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/305900, /*unit_name*/"稜鏡夫人", /*prefab_id*/305900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.2, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"散發著壓倒性的魅力，外貌亮麗的極彩色之豔鳥。\n若臣服於那美貌成為階下囚，將會開始襲擊伙伴。\n\n‧必殺技，將會對誘惑狀態以外的敵人角色，\n　造成大幅度的物理傷害。\n・對前方 2 名角色造成小幅度的魔法傷害和誘惑狀態， \n　之後還能大幅度提升自己物理攻擊力與\n　魔法攻擊力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306000, /*unit_name*/"邪餅神鏡餅", /*prefab_id*/306000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/2160, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】由邪念的殘餘物聚集所化成的邪神。\n將其具現的煩惱給全數淨化，以迎接新年吧！\n\n‧必殺技可對全體敵人造成大幅度的\n　物理傷害。\n‧對前方的 3 名角色造成中幅度的\n　魔法傷害，並降低其物理攻擊力。\n‧在前方中距離召喚定時炸彈年糕。\n‧當炸彈年糕被召喚出來，\n　8 秒後造成大爆炸，對範圍內的全體敵人\n　造成中幅度的物理傷害。\n　但是若炸彈年糕在爆炸前的 HP 低於 50% 時，\n　將小幅度恢復全體敵人的 HP，\n　並當場消失。\n‧當邪餅神鏡餅自身的 HP 低於 \n　50% 時，祂的攻擊將會\n　變得更加兇暴。");
