INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306100, /*unit_name*/"貪婪豺狼", /*prefab_id*/306100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在黑暗中亂竄，在村鎮裡\n靠竊盜為生的魔物頭目。\n會將偷來的武器毫不留情的丟出，\n是開拓的過程中必須除掉的麻煩。\n\n‧對前方的 3 名敵人造成中幅度的物理傷害，\n　並施加麻痺狀態。\n\n‧對全體敵人造成小幅度的物理傷害，\n　並降低其物理防禦力與行動速度。\n　再對前方小範圍內施加中毒狀態。\n　");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306200, /*unit_name*/"大王‧山狸", /*prefab_id*/306200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】盤踞森林深處，與手下支\n配著周遭一帶，老奸巨猾的大狸。\n使人陷入不存在的虛幻，巧妙地操縱\n侵蝕精神的幻影攻擊。\n\n‧對全體敵人造成中幅度魔法傷害，\n　且召喚出 2 隻小狸貓。\n\n・對全體敵人造成小幅度魔法傷害，\n　一併消滅不處於無敵狀態之中的召\n　喚魔物。\n　且當敵人 HP 在 40% 以下時，\n　使其陷入麻痺狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306300, /*unit_name*/"擲岩猴仙", /*prefab_id*/306300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】統領著使用月之魔力的擲岩猴們的仙猴。\n能自由使用寶武具之棍，將敵人一掃而空。\n\n‧對全體敵人造成大幅度的物理傷害。\n　此攻擊對於對火傷狀態的敵人\n　傷害為 1.8 倍。\n‧對前方中範圍內的敵人造成中幅度的物理傷害。\n‧對前方小範圍內的敵人造成中幅度的魔法傷害，\n　並施加火傷狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306301, /*unit_name*/"擲岩猴仙", /*prefab_id*/306301, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】統領著使用月之魔力的擲岩猴們的仙猴。\n能自由使用寶武具之棍，將敵人一掃而空。\n\n‧對全體敵人造成大幅度的物理傷害，\n　並降低物理攻擊角色的物理攻擊力。\n　此攻擊對於對火傷狀態的敵人\n　傷害為 1.8 倍。\n‧對前方中範圍內的敵人造成中幅度的物理傷害。\n‧對前方小範圍內的敵人造成中幅度的魔法傷害，\n　並施加火傷狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306400, /*unit_name*/"野蠻之爪", /*prefab_id*/306400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以捕食者之姿君臨於荒山中\n，一隻兇惡的怪鳥獸。\n用著那日以繼夜的狩獵鍛鍊而出的\n臂力與利爪，將入侵者擒獲。\n\n‧對全體敵人造成大幅度物理傷害\n，並且降低其 TP 上升。　\n每次發動技能的同時，還能夠\n提升傷害。這個效果最多累積次\n數為 3 次。\n\n‧對從後方算起第 2 名角色為中心\n的小範圍，造成小幅度的物理傷害\n，並且使其麻痺。");
