INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306704, /*unit_name*/"醜陋可愛", /*prefab_id*/306700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【MODE1】將巧克力大噴射，並且隱藏身軀。\n若持續受到敵人的攻擊，將會炸裂憤怒的一擊。\n\n‧對全體敵人造成小幅度的物理傷害，並且在戰鬥\n　期間造成火傷狀態。\n・在躲於巧克力保壘之中的期間，醜陋可愛將會\n　中幅度提升自身的物理防禦力。\n　在巧克力保壘的狀態解除之際，\n　將會依照在狀態中所受到傷害量，\n　對敵人來造成物理傷害。\n　並且醜陋可愛自身的物理防禦力，將會大幅度\n　的降低。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306705, /*unit_name*/"醜陋可愛", /*prefab_id*/306700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【MODE2】可可亞烏拉挺身而出，\n 一頭栽進了死地。\n 皆是為了從被激化的魔物手\n 中，保護最珍惜的人們。\n\n‧必殺技，能夠對全體敵人依照其最大 HP\n　造成大幅度的物理傷害。\n・可可亞烏拉將會以夥伴的身份出現。\n　當可可亞烏拉出現時，將會中幅度提升\n　全體角色的物理攻擊力、魔法攻擊力和行動速度。\n・對全體敵人造成小幅度魔法傷害，\n　並且大幅度的降低行動速度。\n");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306706, /*unit_name*/"醜陋可愛", /*prefab_id*/306700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/2160, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【MODE3】震耳欲聾的咆哮，壟罩著整個會場。\n交織愛恨情仇的情人節事變終究迎向了大結局。\n\n‧必殺技，能夠對全體敵人依照其最大 HP\n　造成大幅度的物理傷害，並且若對方為\n　火傷的狀態時，將有更多的追加傷害。\n‧對前方的 3 名角色造成中幅度的物理傷害，\n　並且使其暈眩。\n　且當其角色的 HP 在 50%以下時，\n　使其陷入火傷狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306800, /*unit_name*/"哀嘆之女神", /*prefab_id*/306800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】因失意與絕望，而墮落於魔的悲哀王女。\n冀求著慟哭之聲，將恐懼與詛咒滿溢至一切。\n\n‧必殺技，能夠對全體敵人依照其最大 HP\n　造成大幅度的魔法傷害，並且降低其\n　魔法防禦力。\n‧對前方第 2 名、第 3 名的角色，造成\n　中幅度的魔法傷害，並且使其暈眩。\n‧對前方的 2 名角色造成小幅度的魔法傷害，\n　降低其物理攻擊力，且減少其ＴＰ。\n　並且施加詛咒狀態。\n‧當哀嘆之女神自身的 HP\n　低於 40% 時，她的攻擊將會變得更加\n　兇暴。");
