INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306804, /*unit_name*/"哀嘆之女神", /*prefab_id*/306800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【MODE1】將【哞哞自衛隊】的成員做為闇影召喚。\n以斷絕羈絆為企圖，撥弄挑戰者內心深處的弱點。\n\n・使闇影現身。\n・哀嘆之女神自身將不會進行攻擊。");
