INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307005, /*unit_name*/"元核‧巨靈", /*prefab_id*/307002, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/5.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【MODE1】將【美食殿堂】的成員做為闇影\n召喚。以旅途的回憶做為擋箭牌，試圖\n煽動 3 人心中的弱點。\n\n・使闇影現身。\n‧對前方的 3 名角色造成中幅度的物理傷害。\n・對全體敵人造成物理傷害，並大距離向後擊飛\n　且使其暈眩。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307006, /*unit_name*/"元核‧巨靈", /*prefab_id*/307002, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/5.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【MODE2】那強韌的肉體，不論是何等\n攻擊皆能凌駕於上。以憤怒為糧，巨人會讓人\n見識排山倒海般的大反擊。\n\n・更加地容易受到物理攻擊力提升、降低的效果\n　影響。\n‧必殺技，能夠對全體敵人造成大幅度的物理傷害。\n‧對前方 3 名角色造成中幅度的物理傷害。\n・於一定時間內保持著防禦的架式，而在之後將會\n　依照這段間內所受到的傷害量，對全體敵人造成\n　物理傷害，使前方 2 名角色麻痺。\n　並且再提高自己的物理攻擊力。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307007, /*unit_name*/"元核‧巨靈", /*prefab_id*/307002, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【MODE3】弩級的巨人，將全身所有的力量\n完全解放。在無與倫比的破壞力面前\n，任何的小技倆皆無用。\n\n・更加地容易受到物理攻擊力提升、降低的效果\n　影響。\n‧必殺技，能夠對全體敵人造成大幅度的物理傷害。\n‧對前方 3 名角色造成中幅度的物理傷害。\n・於一定時間內保持著防禦的架式，而在之後將會\n　依照這段間內所受到的傷害量，對全體敵人造成\n　物理傷害，使前方 2 名角色麻痺。\n　並且再提高自己的物理攻擊力。\n・對全體敵人造成物理傷害，並大距離向後擊飛\n　且使其暈眩。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307100, /*unit_name*/"機械莉瑪（模式1）", /*prefab_id*/307100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/2.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"堅固且擁有多樣招式的謎之巨大兵器的第一型態。\n以蘋果型補給機能夠充填未知的能量，並且還能\n使出將廣範圍焦土化的連續攻擊，請小心！\n\n‧必殺技可對全體敵人造成大幅度的物理傷害。\n‧對前方的 1 名角色造成小幅度的物理傷害。\n・在一定的範圍之內，展開能夠降低物理攻擊力與\n　迴避，並且造成物理傷害的領域。\n・對前方範圍內造成中幅度的物理傷害，\n　且提升自己的 TP 上升。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307101, /*unit_name*/"機械莉瑪（模式2）", /*prefab_id*/307101, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/0.1, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"完全地切換到進攻模式的謎之巨大兵器的第二型態。\n核心莉瑪反應爐所釋放的光線，能將大地燃燒殆盡。\n趁機用大量傷害將無防備的巨大兵器狠狠揍一頓吧！\n\n・必殺技，能對前方小範圍內造成特大規模\n　的物理傷害，中範圍處造成大規模物理傷害，\n　廣範圍處則是造成中規模的物理傷害\n　，並且大距離向後擊飛。\n‧對前方的 1 名角色造成小幅度的物理傷害。\n‧對隨機 1 名敵人造成小幅度的物理傷害。\n・在一定範圍內，展開能夠降低 TP 上升與迴避，\n　並且造成魔法傷害的領域。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307102, /*unit_name*/"機械莉瑪（模式3）", /*prefab_id*/307102, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/5.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"能夠自我修復耗損的謎之巨大兵器的第三型態。\n對破綻百出的本體攻擊時，將會有防衛機器人阻撓。\n用範圍攻擊滅掉那些機器人，並且把握這個好機會！\n\n・將全體敵人大距離向後擊飛，\n　並且召喚 3 隻迷你機械莉瑪。\n‧迷你機械莉瑪能夠對前方的 1 名\n　角色造成小幅度的物理傷害。\n‧迷你機械莉瑪能夠對隨機的 1 名角色，\n　造成中幅度的魔法傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/307200, /*unit_name*/"巴西利斯克", /*prefab_id*/307200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】追求月之魔力因而飛來的大怪鳥。\n統馭著雛鳥，且揮舞著石化的詛咒之劍。\n\n・對未處於石化狀態的全體角色，造成大幅度的\n　魔法傷害，且短時間內石化。\n　對石化狀態的全體角色，造成最大 HP 80%\n　的傷害。\n‧對前方 3 名角色造成小幅度的魔法傷害。\n　並且此技能傷害所影響造成的中毒狀態，\n　將會使角色陷入長時間的石化。\n・對前方範圍內的敵人給予中幅度的物魔法傷害，\n　並且使其中毒。\n　當自身的 HP 在 50% 以下時，額外將全體敵人\n　向後擊飛，並且召喚咯咯。\n・咯咯能夠對前方範圍內物理攻擊力最高的\n　1 名角色，造成小幅度的物理傷害，並且使其\n　陷入中毒狀態。\n　另外當自己的HP低於最大HP35%時，\n　將會對物理攻擊力最高的同伴中幅度的提高 TP，\n　而自己將會無法繼續戰鬥。");
