INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/202904, /*unit_name*/"火蜥蜴", /*prefab_id*/202904, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/0.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n靠著尾巴上永遠燃燒著的火焰\n能常保身體溫熱，並不停使出二連擊\n的蜥蜴魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203000, /*unit_name*/"軟泥怪", /*prefab_id*/203000, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/2, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】近距離攻擊\n用物理攻擊相當難以擊中，\n非常濕黏的果凍狀魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203001, /*unit_name*/"閃焰軟泥怪", /*prefab_id*/203001, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/2, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】近距離攻擊\n被森林大火給捲入而著火，\n因此變得更加凶暴的軟泥怪。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203100, /*unit_name*/"幼年巨蜥", /*prefab_id*/203100, /*motion_type*/0, /*se_type*/17, /*move_speed*/270, /*search_area_width*/800, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】近距離攻擊\n儘管還年幼仍然會吐著強力\n的火焰，是巨蜥的幼獸。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203200, /*unit_name*/"搗蛋惡魔", /*prefab_id*/203200, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】近距離攻擊\n使用與身長比例不符、異常巨大的爪子\n做為攻擊，是仍在見習中的惡魔。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203300, /*unit_name*/"哥布林護衛", /*prefab_id*/203300, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/150, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n以逼近身高高度的巨大的盾牌\n守護著同伴的，見習哥布靈士兵。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203301, /*unit_name*/"叢林護衛", /*prefab_id*/203301, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/150, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n以看似臉譜的大型盾牌嚇阻\n入侵者，是叢林的守護者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203400, /*unit_name*/"鬼燈螢", /*prefab_id*/203400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/800, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【恢復】單方恢復\n儲存於尾部的蜜，能夠成為\n魔物的營養源，是種不可思議的螢火蟲。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203500, /*unit_name*/"鑽頭鼴鼠", /*prefab_id*/203500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n能從地底伸手，從腳下開始\n攻擊的鼴型魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203501, /*unit_name*/"寒冰鼴鼠", /*prefab_id*/203501, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n以靈活的利爪刨碎冰塊，\n並作為攻擊的雪鼴型魔物");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203700, /*unit_name*/"堅果射手", /*prefab_id*/203700, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/350, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】範圍攻擊\n將吃完後堅果殼給砸向敵人，\n一種充滿惡意的松鼠魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203800, /*unit_name*/"獸人", /*prefab_id*/203800, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n身著從被擊倒的冒險家身上搶來的鎧甲，\n且揮舞著巨斧的一種凶暴的森林獸人。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203801, /*unit_name*/"高等獸人", /*prefab_id*/203801, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n因反覆且嚴厲的鍛鍊，而獲得強韌肉體，\n獸人裡頭的上位種。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/203900, /*unit_name*/"惡魔加農", /*prefab_id*/203900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1080, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n因為長年的使用而寄生著惡魔之魂，\n擁有生命的砲台。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204000, /*unit_name*/"小小格里芬", /*prefab_id*/204000, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n羽毛還沒長齊，\n還飛不起來的雛獅鷲。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204200, /*unit_name*/"女中音之鳥", /*prefab_id*/204200, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/450, /*atk_type*/2, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【恢復】範圍恢復\n那治癒人的歌聲甚至能傳送至遠方，\n在鳥魔物界中是一位知名的歌手");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204300, /*unit_name*/"毒莢花", /*prefab_id*/204300, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n用那血盆大口之中所蘊含的腐蝕液體，\n將捕獲的獵物吃掉。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204400, /*unit_name*/"惡魔寶寶", /*prefab_id*/204400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/750, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻擊\n用才剛學會的魔法，不管三七二十一\n就到處攻擊的幼年惡魔。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204500, /*unit_name*/"重鎚巨蟹", /*prefab_id*/204500, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n以大型的爪子以及堅硬的殼，\n面對各種巨大的對手的螃蟹魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204600, /*unit_name*/"魔法射線", /*prefab_id*/204600, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/1080, /*atk_type*/2, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】範圍攻擊\n以頭部石塊發射超音波，\n輕鬆狩獵的魟魚魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204700, /*unit_name*/"綠葉野豬", /*prefab_id*/204700, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n將堅硬的葉子如鎧甲般纏繞身體，\n碰到即會帶來劇烈疼痛的大型野豬。\n但終歸只是葉子。無法防禦魔法。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204701, /*unit_name*/"沙塵野豬", /*prefab_id*/204701, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n將沙子作為鎧甲纏繞於身上，\n極為身為不畏捨身的戰士的鐵證。\n但終歸只是沙子。無法防禦魔法。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204702, /*unit_name*/"狂野野豬", /*prefab_id*/204702, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/170, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n生活於熱地，原始的大野豬。\n在不停衝撞岩漿岩後，\n習得了連鎧甲都可粉碎的牙之一擊。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/204800, /*unit_name*/"廢墟人偶", /*prefab_id*/204800, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/150, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n跟隨並且聽令於逝者‧卡拉迪翁的古代自動人偶。\n不具意志，在自身瓦解前將會永無止境的進行戰鬥。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205100, /*unit_name*/"土蛇", /*prefab_id*/205100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n躲在暗處吸取自各個角落飛來的魔力，\n以填飽肚子的魔物蛇的變種。\n魔力越高越容易被它盯上。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205200, /*unit_name*/"海馬騎士", /*prefab_id*/205200, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n奪取在海中溺水的騎士的茅，\n劃界警備的海馬戰士");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205300, /*unit_name*/"寒冰雛鳥", /*prefab_id*/205300, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/2, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】中距離攻擊\n以尾部羽毛上發射冰霜攻擊的\n鳥型魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205400, /*unit_name*/"冰魚", /*prefab_id*/205400, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n鑿開冰河作為巢穴，對所有接近的人，\n無差別進行襲擊的魚之魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205401, /*unit_name*/"熔岩魚", /*prefab_id*/205401, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/2.4, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n以連岩石都能咬碎的強韌下顎，\n將周遭事物給食之殆盡的魚之魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205500, /*unit_name*/"沙陸毒蛇", /*prefab_id*/205500, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/550, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻擊\n挖掘洞穴，靜待獵物掉入洞穴，\n再以毒牙將其斃命的蛇的魔物。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205600, /*unit_name*/"狂犬", /*prefab_id*/205600, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n與主人一同被創造出來，一隻兇暴的改造犬。\n像為得支撐住那大頭所流露的猙獰面容，極為恐怖。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/205700, /*unit_name*/"波尼", /*prefab_id*/205700, /*motion_type*/0, /*se_type*/15, /*move_speed*/270, /*search_area_width*/50, /*atk_type*/1, /*normal_atk_cast_time*/0.4, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"召喚地獄獵犬用怪物");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206000, /*unit_name*/"海獅怪", /*prefab_id*/206000, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/4.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近距離攻擊\n不論體型還態度，皆為條漢子的北\n海之怪。以厚重的脂肪層來抵禦且\n壓制敵人的物理攻擊。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206300, /*unit_name*/"露娜聚光燈", /*prefab_id*/206300, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/1110, /*atk_type*/0, /*normal_atk_cast_time*/10.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【恢復】單體ＴＰ恢復\n與露娜之塔所建時一同打造出來的一種能量增幅器。\n將散布各處的魔力濃縮，並做為強化供給於守護者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300100, /*unit_name*/"牛頭怪", /*prefab_id*/300100, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以迷宮作為巢穴，牡牛座的魔星獸。\n不斷揮舞巨大的戰斧，奪取冒險家的性命。\n\n・對全體敵人造成大幅度的物理傷害，\n並造成火傷。\n・對 1 名敵人造成大幅度的物理傷害，\n並在擊退敵方的同時使其暈眩。\n・對前方範圍內的敵人造成中幅度的魔法傷害，\n使其麻痺。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300300, /*unit_name*/"梅莎路提姆", /*prefab_id*/300300, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】住在夢魔之館，牡羊座的魔星獸。\n將冒險家束縛在夢的世界中，並啃噬他們的靈魂。\n\n・對全體敵人造成大幅度的魔法傷害，\n並對遭束縛的敵人造成小幅度的魔法傷害。\n・對前方範圍內的敵人造成中幅度的魔法傷害。\n・束縛 １ 名敵人。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300500, /*unit_name*/"蜥蜴人", /*prefab_id*/300500, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能自在地揮舞手中\n的兩把劍刃，\n還可以使用煙霧彈來模糊\n對手視野的巨蜥戰士。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300600, /*unit_name*/"爬行者", /*prefab_id*/300600, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】從頭前施放的劇毒與藤蔓\n所組成的範圍物理攻擊，能將貿然接近\n的冒險家的 HP 給一口氣略奪而盡，\n如同惡魔般的花朵。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300601, /*unit_name*/"芒刺爬行者", /*prefab_id*/300601, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的爬行者。\n以帶有許多刺的藤蔓綑綁，限制敵人的行動。\n\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並使其呈現麻痺狀態。\n・對前方範圍內的敵人造成物理防禦力下降，\n並使其呈現中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300602, /*unit_name*/"芒刺爬行者", /*prefab_id*/300602, /*motion_type*/0, /*se_type*/2, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的爬行者。\n以帶有許多刺的藤蔓綑綁，限制敵人的行動。\n\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並使其呈現麻痺狀態。\n・對前方範圍內的敵人造成物理防禦力下降，\n並使其呈現中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300700, /*unit_name*/"獸人領導者", /*prefab_id*/300700, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以隊長的姿態鼓舞著群體，\n以能震裂大地般的強力連擊造成\n敵人大幅度的物理傷害，\n可說是獸人中的獸人。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300701, /*unit_name*/"獸人酋長", /*prefab_id*/300701, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的獸人隊長。\n以巨大的盾牌，持續使出攻防合一的攻勢。\n\n・對前方範圍內的敵人造成大幅度的物理傷害，\n並使其暈眩。\n・對前方範圍內的敵人造成大幅度的防禦力下降。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/"角鹿長老", /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】一旦開始狂怒地踐踏震地，\n便無法控制，聽說那嘶吼\n甚至能響徹天際，\n是傳說中的野角。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300801, /*unit_name*/"聖靈角鹿", /*prefab_id*/300801, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的遠古之角。\n其角富有魔力，可放出大於打擊之力的強力一擊。\n\n・對前方範圍內的敵人造成大幅度的魔法傷害，\n在擊退敵方的同時使其暈眩。\n・對前方範圍內的敵人造成小幅度的魔法傷害，\n並小幅度降低敵人的魔法防禦與攻擊速度。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/"暴君長老", /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能以強力的頭錘擊碎大地，\n對全體造成大幅度的傷害。\n無論是身體還是硬殼都擁有大幅度的進化，\n是如同岩山一般的硬殼暴君。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301000, /*unit_name*/"希德雷克", /*prefab_id*/301000, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】吐出強力冰之吐息\n將前衛瞬間冰凍，\n接著將身軀一口咬碎的\n凶猛水龍。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301100, /*unit_name*/"殘暴狂熊", /*prefab_id*/301100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以與體格相異的靈敏度\n以及撕咬攻擊奪取體力，\n並以巨大的爪子撕裂敵人，\n是殘虐無道的森林支配者。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301101, /*unit_name*/"狂焰戰熊", /*prefab_id*/301101, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】因奇異的力量而變質的殘暴之熊\n充滿憤怒的大力之拳，能使敵方地面裂開。\n\n・對前方範圍內的敵人造成大幅度的物理傷害，\n並使其暈眩。\n・對前方敵人造成小幅度的物理傷害，\n並對敵人造成中幅度的物理防禦力下降。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/"碎裂魔像", /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】能無止盡地召喚出魔像，\n並以岩石削弱體力與精神。\n用魔力將無數的魔像殘骸\n再建而成的巨魔像。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301300, /*unit_name*/"大胃王", /*prefab_id*/301300, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"惡名昭彰的暴食之兇獸，是為天秤座的魔星獸。\n秤量被剝奪之魂的善惡，以品嘗之間差異為樂。\n\n・不具有對敵人的攻擊所造成的麻痺、暈眩、\n　束縛，以及行動速度低下的抗性。\n・對敵方全體造成大量的魔法傷害，並且對自\n　身的物理與魔法攻擊力給予特大量上升。\n・對後方範圍內的敵人造成中量的魔法傷害。\n・對前方範圍內的敵人造成中量的魔法傷害，並\n　小幅度降低TP。　並且使自身的TP，中幅度\n　上升。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301400, /*unit_name*/"美杜莎", /*prefab_id*/301400, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"以無數毒蛇組成的長髮，是為處女座的魔星獸。\n悲戀之痛，使祂拒萬物於千里且使其幻化成石。\n\n・對敵方全體造成大量魔法傷害，並使前方範圍\n　內的敵人陷入石化狀態。\n・對前方範圍內的敵人造成物理傷害，並再對陷\n　入石化狀態的敵人，降低大量的TP。\n・對物理攻擊力最高的1名敵方角色，造成大量\n　魔法傷害，使其陷入中毒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301500, /*unit_name*/"狂戰之狼", /*prefab_id*/301500, /*motion_type*/0, /*se_type*/12, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以狼嚎使獵物麻痺，\n再趁其膽怯時用爪子撕裂。\n止住無數冒險家的腳步，\n可謂殘虐至極的魔界之狼。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301600, /*unit_name*/"席茲", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】帶來強烈暴風的大怪鳥。\n逃出魔物圖鑑，做盡破壞之能事。\n\n・根據角色的最高 HP，對全體敵人\n造成大幅度的傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並且使其暈眩。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301601, /*unit_name*/"席茲", /*prefab_id*/301600, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】帶來強烈暴風的大怪鳥。\n逃出魔物圖鑑，做盡破壞之能事。\n\n・根據角色的最高 HP，對全體敵人\n造成大幅度的傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害，\n並且使其暈眩。\n・根據角色的最高 HP，對前方範圍內的敵人\n造成中幅度的傷害，並降低其物理防禦力。\n・當席茲本身的 HP 降至 50％ 以下，\n其攻擊力將會越發殘暴。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301900, /*unit_name*/"嘯浪虎鯨", /*prefab_id*/301900, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】以銳利的眼神追尋物品\n及波浪的海中獵人。\n利用魔力讓潮水不斷湧出，\n進而削弱敵方的刀刃和力量。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302000, /*unit_name*/"狂暴格里芬", /*prefab_id*/302000, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在嚴酷的環境中進化的小小格里芬。以龍捲風將獵物捲起，並在空中獵捕。\n\n・對前方範圍內的敵人造成中幅度的魔法傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302001, /*unit_name*/"狂暴格里芬", /*prefab_id*/302001, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在嚴酷的環境中進化的小小格里芬。以龍捲風將獵物捲起，並在空中獵捕。\n\n・對前方範圍內的敵人造成中幅度的魔法傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302100, /*unit_name*/"飛龍", /*prefab_id*/302100, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】使用大面皮翼，於空中飛翔的翼龍。\n吐出巨大的火球，阻擋入侵者的動作。\n\n・對前方範圍內的敵人造成中幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302101, /*unit_name*/"飛龍", /*prefab_id*/302101, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】使用大面皮翼，於空中飛翔的翼龍。\n吐出巨大的火球，阻擋入侵者的動作。\n\n・對前方範圍內的敵人造成中幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302200, /*unit_name*/"逝者・卡拉迪翁", /*prefab_id*/302200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】以古老技術製成，遺跡的守護者。\n以雙手獲得魔力，並一口氣施放。\n\n・對前方範圍內的敵人造成大幅度的魔法傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302300, /*unit_name*/"睿智之鴞", /*prefab_id*/302300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302400, /*unit_name*/"電弧守護者", /*prefab_id*/302400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302500, /*unit_name*/"奇美拉", /*prefab_id*/302500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.4, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302600, /*unit_name*/"野豬雙胞胎", /*prefab_id*/302600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】不斷行竊的雙子座魔星獸。\n以卓越的合作，偷襲組織的入侵者。\n\n・根據角色的最高 HP，對全體敵人造成\n大幅度的物理傷害。\n・對敵方後衛造成中幅度的物理傷害，\n並小幅度降低其攻擊速度。\n・對全體敵人造成小幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302601, /*unit_name*/"野豬雙胞胎", /*prefab_id*/302601, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】不斷行竊的雙子座魔星獸。\n以卓越的合作，偷襲組織的入侵者。\n\n・根據角色的最高 HP，對全體敵人造成\n大幅度的物理傷害。\n・對敵方後衛造成中幅度的物理傷害，\n並小幅度降低其攻擊速度。\n・對全體敵人造成小幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302700, /*unit_name*/"加爾基諾司", /*prefab_id*/302700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】盤踞在船墳的海灣中，巨蟹座的魔星獸。\n其巨大剪刀的一擊，可使無數的船隻沉沒。\n\n・對敵方全體造成大幅度的魔法傷害，並降低\n　的物理防禦力。\n・對前方範圍內的全體敵人造成中幅度的物理傷害\n　，並使其呈現暈眩狀態。\n・對敵方全體造成中幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302800, /*unit_name*/"奧爾獅", /*prefab_id*/302800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"頗負盛名的熱帶草原霸者，獅子座的魔星獸。\n對其刀刃相向者，將焚毀於灼熱的咆哮中。\n\n・對敵方全體，依照最大HP造成大幅度並無\n　視防禦力的物理傷害，並對剩餘HP較少的敵人\n　賦予火傷狀態。\n・對敵方1名角色造成大幅度物理傷害，\n　若為火傷狀態時則再追加額外傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害，\n　若為火傷狀態時則再追加額外傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303100, /*unit_name*/"冬眠之熊", /*prefab_id*/303100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】躲藏於冰穴的最深處，\n決意要終身冬眠的巨大野熊。\n長年在幻夢與現實的交錯中度日，\n僅於捕獲獵物的一瞬，覺醒。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303200, /*unit_name*/"寒冰孔雀夫人", /*prefab_id*/303200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】伴隨著暴風雪現身，\n會呼朋引伴的襲擊侵犯地盤的人，\n是雪國之中最華麗的女士。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303300, /*unit_name*/"石像鬼", /*prefab_id*/303300, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】遺留在腐朽的遺跡裡，\n守護著主人死去的地方，是個謎樣的鳥類雕像。\n隨著城鎮、命令的逝去，\n現在的他們無論來者是，誰全部予以火焰攻擊。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303400, /*unit_name*/"沙蜥蜴", /*prefab_id*/303400, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】生存競爭激烈的沙漠中\n的王者，巨大蜥蜴。\n使用劇毒，活用沙子的戰略，\n就是嚴酷環境中習得的生存之道。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303500, /*unit_name*/"怒蛇", /*prefab_id*/303500, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理/魔法】以持續吸取他人魔力，\n習得妖術的魔物。\n傷害敵人並奪取體力，\n進而奪取性命。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303600, /*unit_name*/"緋紅火焰龍", /*prefab_id*/303600, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】靠著強韌的雙腳與銳利的\n爪子，可一瞬間捕獲獵物，兇猛的恐龍。\n在滅亡的大地上吞食熔岩，藉以找尋\n生存之道的孤高存在。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303700, /*unit_name*/"地獄獵犬", /*prefab_id*/303700, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/0.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】儘管墜入了那火山之中，\n依舊身負地獄之炎復甦的雙頭魔犬。\n與愛犬波尼一同在地盤上奔馳著，\n並讓入侵者親身體驗那灼熱的洗禮。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303800, /*unit_name*/"阿爾瑪", /*prefab_id*/303800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】遠古時用來守護迷宮的防衛兵器。作為最後的陷阱，靜待入侵者。\n・根據角色的最高 HP，對全體敵人\n造成中幅度的魔法傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303801, /*unit_name*/"阿爾瑪", /*prefab_id*/303800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】遠古時用來守護迷宮的防衛兵器。作為最後的陷阱，靜待入侵者。\n・根據角色的最高 HP，對全體敵人\n造成特大幅度的魔法傷害。\n・對前方範圍內的敵人造成中幅度的物理傷害。\n・根據角色的最高 HP，對剩餘 HP 最高的敵人\n造成中幅度的魔法傷害，並使其暈眩。\n・當古老惡靈衛兵本身的 HP 降至 50％ 以下，\n其攻擊力將會越發殘暴。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303900, /*unit_name*/"獨眼巨人", /*prefab_id*/303900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以怪力為傲的兇暴的獨眼巨人。\n魔力溢出眼瞳，放射強力的詭異光線。\n\n・對全體敵人造成大幅度的魔法傷害，\n使其陷入黑暗狀態。\n・根據角色的最高 HP，對剩餘 HP 最高的敵人\n造成中幅度的物理傷害，並降低其 TP。\n");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303901, /*unit_name*/"獨眼巨人", /*prefab_id*/303901, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】以怪力為傲的兇暴的獨眼巨人。\n魔力溢出眼瞳，放射強力的詭異光線。\n\n・對全體敵人造成大幅度的魔法傷害，\n使其陷入黑暗狀態。\n・根據角色的最高 HP，對剩餘 HP 最高的敵人\n造成中幅度的物理傷害，並降低其 TP。\n");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/304000, /*unit_name*/"夢魘", /*prefab_id*/304000, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】在虛幻中誕生，徘徊於黑暗中的魔獸。\n將獵物綑綁於惡夢之牢中，並津津有味地吃著其生息。\n\n・對物理最高的1名敵方角色造成特大幅度的\n　魔法攻擊，並使其TP特大幅度下降。\n・以物理攻擊力最高的1名敵方角色為中心，對範圍\n　內的全體敵人造成中幅度的魔法傷害，並賦予\n　詛咒狀態。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/304200, /*unit_name*/"加爾格", /*prefab_id*/304200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】啃食屍體的殘暴的大型魔獸。\n強韌的刺佈滿全身，追捕獵物的執念甚深。\n・根據角色的最高 HP，對全體敵人\n造成中幅度的物理傷害。\n・對後方範圍的敵人造成中幅度的物理傷害，\n並使其暈眩。");
